void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ((60.0 + iTime) / 5.0) * ((fragCoord.xy - (iResolution.xy / 2.0)) / iResolution.y);

    float value = pow(uv.x,2.0) + pow(uv.y,2.0);
    float color = sin(value) * 3.0;
    float low = abs(color);
    float med = abs(color) - 1.0;
    float high = abs(color) - 2.0;
    if(color > 0.0) {
	  fragColor = vec4(low, med, high,1.0);
    } else {
      fragColor = vec4(med, med, low,1.0);
    }
}

#define PI 3.141592
#define PI2 6.283184
#define SEGMENTS 4.0 / 2.0
#define COLOR1 vec3(0.0,0.0,0.0)
#define COLOR2 vec3(0.1,0.1,0.1)

void mainImage2( out vec4 fragColor, in vec2 fragCoord ) {
    float aspect = iResolution.y / iResolution.x;
	vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
    //uv.x += 0.2;
    uv.y *= aspect;
    float theta = (atan(uv.y, uv.x) + PI) / PI2 - iTime * 0.09;
    if(theta > 1.0) theta = mod(theta, 1.0);
    if(theta < 0.0) theta = 1.0 - mod(abs(theta), 1.0);
    vec3 value = length(uv) < 0.05 ? COLOR1 : floor(mod(theta * SEGMENTS, 1.0) * 2.0) == 0.0 ? COLOR1 : COLOR2;
	fragColor = vec4(value, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float aspect = iResolution.x / iResolution.y;
    vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
	uv.x *= aspect;
    vec3 value = fract((uv.x * sin(iTime) + uv.y * cos(iTime)) * (SEGMENTS + (sin(iTime * 8.0) * 0.5 + 0.5) * 1.5)) < 0.5 ? COLOR1 : COLOR2;
	fragColor = vec4(value, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv-=0.5;
    uv.x*= iResolution.x/iResolution.y;

    float dist,dist1,dist2,mask,l,final = 1.0;
	//This parameter controls how many sheets are in the picture
    float s = 0.03;

    float amp,freq;
	uv.x-=iResolution.x/iResolution.y;

    //This parameter controls when the algorithm stop drawing sheets (-1 is no sheet, 1 all sheets)
    float factorSheets = iResolution.x/iResolution.y;
    //Optional: very funny :)
    //factorSheets*=sin(0.4*iTime);

    for(float f = -iResolution.x/iResolution.y;f<factorSheets;f+=s){
        uv.x+=s;
        //This parameter controls the frequency of the waves, modulated by an exp along the x-axis 
        freq = 5.0*exp(-20.0*(f*f));
        //This parameter controls the amplitude of the waves, modulated by an exp along the x-axis 
        amp = 0.12*exp(-7.0*(f*f));
        dist = amp*pow(sin(freq*uv.y+2.0*iTime+100.0*sin(122434.0*f)),2.0)*exp(-5.0*uv.y*uv.y)-uv.x;
        mask = 1.0-smoothstep(0.0,0.005,dist);

        //Draw each line of the sheet
        dist1 = abs(dist);
        dist1 = smoothstep(0.0,0.01,dist1);

        //Draw the shadow of each line
        dist2 =abs(dist);
        dist2 = smoothstep(0.0,0.03,dist2);
        dist2 = mix(0.5,1.0,dist2);
        dist2 *= mask;

        //Combine shadow and line
        l = mix(dist1,dist2,mask);
        //Combine the current sheet with the last drawn
        final=mix(l,l*final,mask);
	}

 //Add some color   
 //	vec3 white = vec3(1.0,1.0,1.0);
 //	vec3 blue = vec3(0.0/255.0,204.0/255.0,255.0/255.0);
 //	vec3 color = mix(white,blue,final);
 //	fragColor = vec4(color,1.0);

	fragColor = vec4(final,final,final,1.0);
}

