mat2 rotate2d(float angle)
{
    return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}

float createShape(vec2 centerPosition, float side)
{  
    float a = atan(centerPosition.y,centerPosition.x);
    float r = TWOPI/side;
    return cos(floor(.5+a/r)*r-a)*length(centerPosition.xy);
}

mat2 scale2d(vec2 size)
{
    return mat2(size.x, 0.0, 0.0, size.y);
}

// from https://www.shadertoy.com/view/4sfGzS
float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);

    vec2 uv = p.xy + f.xy;
	vec2 rg = vec2(texture( iChannel1, (uv+vec2(37.0,17.0)*p.z+0.5)/256.0, -100.0 ).x,
                   texture( iChannel1, (uv+vec2(37.0,17.0)*(p.z+1.0)+0.5)/256.0, -100.0 ).x );
	return mix( rg.x, rg.y, f.z );
}

float dfVoxel(vec3 p)
{
    vec3 cube = vec3(0.5, 0.5, 0.5);
    return length(max(abs(p)-(cube - 0.1),0.0)) - 0.1;
}

vec3 nrmVoxel(vec3 p) 
{
    p += 0.001 * normalize(p);
    vec2 dd = vec2(0.001,0.0);
    float base = dfVoxel(p);
    return normalize(vec3(
        dfVoxel(p+dd.xyy) - base,
        dfVoxel(p+dd.yxy) - base,
        dfVoxel(p+dd.yyx) - base
    ));
}

// Stolen from shane's Voxel Corridor: https://www.shadertoy.com/view/MdVSDh# :)
// Tri-Planar blending function. Based on an old Nvidia tutorial.
vec3 tex3D( sampler2D tex, in vec3 p, in vec3 n ){

    n = max(abs(n), 0.001);//n = max((abs(n) - 0.2)*7., 0.001); //  etc.
    n /= (n.x + n.y + n.z ); 
	p = (texture(tex, p.yz)*n.x + texture(tex, p.zx)*n.y + texture(tex, p.xy)*n.z).xyz;
    return p*p;
}

// Stolen from shane's Voxel Corridor: https://www.shadertoy.com/view/MdVSDh# :)
// Texture bump mapping. Four tri-planar lookups, or 12 texture lookups in total. I tried to 
// make it as concise as possible. Whether that translates to speed, or not, I couldn't say.
vec3 doBumpMap( sampler2D tx, in vec3 p, in vec3 n, float bf){

    const vec2 e = vec2(0.001, 0);

    // Three gradient vectors rolled into a matrix, constructed with offset greyscale texture values.    
    mat3 m = mat3( tex3D(tx, p - e.xyy, n), tex3D(tx, p - e.yxy, n), tex3D(tx, p - e.yyx, n));

    vec3 g = vec3(0.299, 0.587, 0.114)*m; // Converting to greyscale.
    g = (g - dot(tex3D(tx,  p , n), vec3(0.299, 0.587, 0.114)) )/e.x; g -= n*dot(n, g);

    return normalize( n + g*bf ); // Bumped normal. "bf" - bump factor.

}

vec3 lightVoxel(vec3 p, vec3 rd)
{
    vec3 n = nrmVoxel(p);
    vec3 color = tex3D(iChannel1, p, n) * vec3(1.0, 0.7, 0.7);
    vec3 bumpedN = doBumpMap(iChannel1, p, n, 0.02);
    vec3 ldir = normalize(vec3(1.0));
    float diffuse = max(0.0, dot(bumpedN, ldir));
    float specular = pow(max(0.0, dot(-rd, reflect(-ldir, bumpedN))), 16.0);
    return color * (diffuse + 0.3) + specular*0.3;
}


