    ro = ro + ro*pow(iMouse.y/iResolution.y, 2.0)*10.0;
    vec3 rd = normalize(-ro);

	vec3 ax1 = normalize(cross(rd, vec3(0.0, 1.0, 0.0)));
    vec3 ax2 = normalize(cross(rd, ax1));

    rd = normalize(rd*3.0 + ax1*uv.x + ax2*uv.y);

    // iq's cube intersection :)
    vec3 cubeIntersect = (-ro -0.8*sign(rd))/rd;
    float dist = max(cubeIntersect.x, max(cubeIntersect.y, cubeIntersect.z));
    float incr = 0.0;

    for (int i=0; i<32; i++) {
        incr = dfVoxel(ro + rd*dist)/2.0;
        dist += incr;
    }

    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    if (incr < 0.05) {
        fragColor.rgb = lightVoxel(dist*rd + ro, rd);
    } else {
     	fragColor.rgb = sin(texture(iChannel0, vec2((uv.x+1.0)*0.5, 0.5)).r*vec3(9.3, 7.5, 5.9));   
    }
