float sdCapsule( vec2 p, vec2 a, vec2 b, float r )
{
    vec2 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float radius = 0.1 * abs(cos(iTime));

	//vec2 uv_position = (fragCoord.xy / iResolution.y); // non stretched uv space
    //vec2 uv_position = (iResolution.xy - fragCoord.xy) / iResolution.y; // top-right instead of bottom left
    vec2 uv_position = (iResolution.xy - 2.0*fragCoord.xy) / iResolution.y; // push it to center

    vec2 position_1 = sin(iTime + vec2(0.25, 0.25));
    vec2 position_2 = ((iResolution.xy - 2.0*iMouse.xy) / iResolution.y); // why negative why
    vec2 position_3 = cos(iTime + vec2(0.25, 0.25));

    float potential = 0.0;
    potential += 1.0 - sdCapsule(uv_position, position_1, position_2, radius);

    potential += 1.0 - smoothstep(0.0, 0.4, length(uv_position + position_3)); // remove 1.0 for COOLNESS
   //potential += 1.0 - smoothstep(0.0, radius, length(screen_origin + position_2));
	//potential += 1.0 - smoothstep(0.0, radius, length(position_2));
    //potential += 1.0 - smoothstep(0.0, radius, length(screen_origin + (position_1 + position_2)));
    //potential += 1.0 - smoothstep(0.0, radius, length(screen_origin + (position_1 - position_2)));


	vec3 color = vec3(potential);

    fragColor = vec4(smoothstep(0.99, 1.0, color), 1.0);
}

