    float encloseR = RING_RADIUS;
    float encloserRadius = OBSTACLE_RADIUS;
    float spacingX = (w / (float(N) + 1.0));
    float spacingY = h / (float(M) + 1.0);
    float x = 0.0;
    float y = 0.0;
    vec2 lightPos = vec2(aspectRatio/2.0 + sin(iTime)*0.1, 0.5+ cos(iTime)*0.1);

