void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float time = iTime;
  vec2 pos, size;
  vec2 npos = fragCoord.xy / iResolution.xy;   // 0.0 .. 1.0

  // get uv position with origin at window center
  float aspect = iResolution.x / iResolution.y;   // aspect ratio x/y
  vec2 ratio = vec2(aspect, 1.0);                 // aspect ratio (x/y,1)     
  uv = (2.0 * npos - 1.0) * ratio;             // -1.0 .. 1.0

  vec2 mpos = iMouse.xy / iResolution.xy;      //  0.0 .. 1.0
  vec2 mp = (2.0 * mpos - 1.0) * ratio;        // -1.0 .. 1.0  
  float speed = (mpos.y - 0.4) * time;    

  vec3 col;
  int selection = int((iMouse.x / iResolution.x) * float(BACKGROUNDS));

  if      (selection == 6)   
    col = BackgroundColor(fragCoord, iChannel0, iResolution.xy);

  else if (selection == 5)
    col = BackgroundColor(fragCoord+vec2(-180.0*speed,0.0), iChannel0, iResolution.xy);

  else if (selection == 4)
    col = BumpyBackground (uv, iChannel2, 4.0 * speed);

  else if (selection == 3)
    col = BaseGround(npos, iChannel1, speed);

  else if (selection == 2)
    col = PipeTunnel(uv, iChannel1, speed);

  else if (selection == 1)
    col = DonjonTunnel (uv, speed).rgb;

  else
    col = SquareTunnel(uv, speed);

  float intensity = 0.0;

  //--- line --- 
  pos = vec2(-0.5*sin(time), 0.0);
  const vec3 lineColor1 = vec3(0.1, 0.4, 0.7);
  intensity = line (uv, pos, mp, 0.016);
  if ((intensity > 0.0) && (intensity <= 1.0))
    col = mix(col, lineColor1, intensity);

  //--- line segment --- 
  const vec3 lineColor2 = vec3(0.9, 0.9, 0.2);
  intensity = segment (uv, pos, mp, 0.02);     
  if ((intensity > 0.0) && (intensity <= 1.0))
    col = mix(col, lineColor2, intensity);

  //--- green rectangle ---
  const vec3 rectangleColor = vec3(0.2, 0.8, 0.5);
  intensity = 0.6 * rectangle (vec2(sin(time),-0.9),vec2(0.5, 0.1));
  col = mix(col, rectangleColor.rgb, intensity);

  //--- rounded rectangle ---
  const vec3 rectColor = vec3(0.1, 0.8, 0.5);
  pos = vec2(-sin(time), 0.6);
  size = vec2(0.16, 0.02);
  intensity = 0.6 * roundedRectangle (pos, size, 0.1, 0.2);
  col = mix(col, rectColor, intensity);

  //--- rounded frame ---
  const vec3 frameColor = vec3(0.5, 0.3, 0.6);
  size = vec2(0.28, 0.10);
  intensity = roundedFrame (pos, size, 0.08, 0.2);
  col = mix(col, frameColor, intensity);

  //--- red dot ---
  const vec3 circleColor2 = vec3(0.6, 0.2, 0.2);
  vec2 redPos = vec2(-0.5*sin(time), 0.0);
  intensity = circle(redPos, 0.05, 100.0);
  col = mix(col, circleColor2, intensity);

  //--- mouse dot ---
  if (iMouse.z > 0.0)
  {
    const vec3 circleColorM = vec3(1.6, 1.2, 0.6);
    intensity = circle(mp, 0.05, 100.0);
    col = mix(col, circleColorM, intensity);
  }

  //--- yellow circle ---
  const vec3 circleColor = vec3(1, 0.8, 0.3);
  intensity = circle(vec2(sin(time), 0.6), 0.2);
  //intensity = circle(vec2(sin(time), 0.6), 0.2, 6.);
  col = mix(col, circleColor, intensity);

  //--- violet circleBox ---
  const vec3 cbColor = vec3(0.5, 0.3, 0.8);
  pos = vec2(-sin(time), -0.6);
  float between = 0.5+0.5*sin(time);  // mix value
  size = vec2(0.15, 0.10);
  intensity = 0.8 * circleBox(pos, size, 0.02, between);
  col = mix(col, cbColor, intensity);

  //--- brown squircle ---
  const vec3 sqColor = vec3(0.8, 0.5, 0.3);
  pos = vec2(1.3, 0.6*sin(time+HALF_PI));
  float power = 0.8 + 2.0*(1.0+sin(time));
  intensity = 0.9 * squircle (pos, 0.1, power);
  if (intensity > 0.0) 
    col = mix(col, sqColor, intensity);

  //--- pink ring ---
  vec3 ringColor = vec3(0.9, 0.4, 0.6);
  pos = vec2(0.5*sin(time), 0.0);
  intensity = ring2(pos, 0.2 +0.05*sin(time), 0.04);
  if (intensity > 0.0) 
    col = mix(col, ringColor, intensity);

  //--- haloRing ---
  ringColor = vec3(1.0, 1.0, 1.5);
  intensity = haloRing (vec2(-sin(time), 0.0), 0.3, 6.0);
  col = mix(col, ringColor, intensity);

  //--- polygon ---
  const vec3 polygonColor = vec3(0.5, 0.9, 0.3);
  pos = vec2(sin(time), -0.5);
  pos = rotateCCW(uv - pos,time);
  float n = floor (6.0 + 3.0 * sin(time));
  intensity = 0.8 * polygon(pos,n, 0.4);
  col = mix(col, polygonColor.rgb, intensity);

  fragColor = vec4 (col, 1.0);
}

const int maxIter = 100;
float df(vec3 p, float power) {
    vec3 z = p;
    float r = 0.0;
    float dr = 1.0;
    for(int i = 0; i < maxIter; i++) {
        r = length(z);
        if(r > 100.0) break;

        float theta = acos(z.z/r);
        float phi = atan(z.y, z.x);

        dr = power*pow(r, power-1.0)*dr + 1.0;

        r = pow(r, power);
        theta *= power;
        phi *= power;

        z = r*vec3(sin(theta)*cos(phi), sin(theta)*sin(phi), cos(theta));
        z += p;
    }
    return 0.5*log(r)*r/dr;
}


struct Ray {
    bool hit;
    vec3 hitPos;
    float t;
    int steps;
};
const int maxSteps = 100;
Ray trace(vec3 camPos, vec3 rayDir) {
    vec3 p = vec3(0);
    float t = 0.0; 
    int steps = 0;
    bool hit = false;
    for(int i = 0; i < maxSteps; i++) {
        p = camPos + t*rayDir;
        float d = df(p, 8.0*abs(sin(0.5*iTime)) + 1.0);
        if(d < 0.001) {
            hit = true;
            steps = i;
            break;
        }
        t += d*0.9;
    }
    return Ray(hit, p, t, steps);
}


vec3 shading(Ray tr) {
    if(tr.hit) {
    	return vec3(pow(1.0 - float(tr.steps)/float(maxSteps), 0.7));
    }
    else {
        return vec3(0);
    }
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.0*fragCoord.xy - iResolution.xy) / iResolution.y;

    //vec3 camPos = vec3(-2, 0, 0);
    vec3 camPos = 2.0*vec3(cos(iTime), 0, sin(iTime));
    vec3 camFront = normalize(-camPos);
    vec3 camUp = vec3(0, 1, 0);
    vec3 camRight = cross(camFront, camUp);
    float focus = 1.0;

    vec3 rayDir = normalize(uv.x*camRight + uv.y*camUp + focus*camFront);
    Ray tr = trace(camPos, rayDir);

	fragColor = vec4(shading(tr), 1.0);
}

// a spiral of successive twisted squares

// a twisted square can be defined as a transformation (rotation and scaling) of the unit square
// so the inverse matrix will be calculated to turn a point (x,y) back onto this unit square
// the point is then coloured if it is within a certain distance of the edge of the unit square
// to do this I clamp a function f(x) that peaks at (0,1) and goes <=0 quickly between them
// e.g. f(x) = -(x^100-1)^2+1
// each pixel is then an accumulation of all the squares in the spiral multiplied by a colour
// which can be parameterised to give ripples outwards 

#define pi 3.14159265
#define twoPi (2.0 * pi)

// matrix to scale and rotate a unit square
mat2 square(float scale, float angle)
{
    mat2 mrot = mat2(cos(angle), sin(angle),
                     -sin(angle), cos(angle));
    mat2 mscale = mat2(scale, 0.0,
                       0.0, scale);
    return mrot * mscale;
}

// colour function returns 1 when within step of the unit square
float colour(float x, float y)
{
    float step = 0.02;
    if((x >= -1.0 && x <= -1.0 + step) || (x <= 1.0 && x >= 1.0 - step)) {
         if(y >= -1.0 && y <= 1.0) {
             return 1.0;
         }
    } else if((y >= -1.0 && y <= -1.0 + step) || (y <= 1.0 && y >= 1.0 - step)) {
        if(x >= -1.0 && x <= 1.0) {
             return 1.0;
         }
    }
    return 0.0;
}

// smooth version of above
float colour2(float x, float y)
{
    float xx = x * x;
    float yy = y * y;
    float xxxx1 = (pow(xx, 8.0) - 1.0);
    float yyyy1 = (pow(yy, 8.0) - 1.0);
    float xy = (1.0 - xxxx1 * xxxx1) * step(-yy, 0.0);
    float yx = (1.0 - yyyy1 * yyyy1) * step(-xx, 0.0);
    return clamp(xy + yx, 0.0, 1.0);
}

const float speed = 0.15;
const float scalem = 0.08;
const float anglem = 0.25;
const float angles = 0.03;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    for (int i = 0; i < 55; i++) {
        float ii = pow(1.1, float(i) + fract(iTime * speed)) * 0.1;
        float scale = scalem * ii;
        float angle = twoPi * (anglem * ii + iTime * angles);
    	mat2 s = square(scale, angle);
    	mat2 is = inverse(s);
    	vec2 p = is * uv;
        float intensity = 0.4 * colour2(p.x, p.y);
        fragColor += clamp(vec4(intensity * sin(-iTime + ii), intensity * sin(-1.5 * iTime + ii), intensity * sin(-2.0 * iTime + ii), 0.0), 0.0, 1.0);
    }
}

float hash(float n) {
  return fract(sin(n)*43578.5453);
}

vec2 hash(vec2 n) {
  float m = hash(dot(n, vec2(12.23, 30.343)));
  return vec2(hash(m + 2.34), hash(m + 93.34));
}

float noise(vec2 g) {
  vec2 p = floor(g);
  vec2 f = fract(g);

  f = f*f*(3.0 - 2.0*f);
  float n = p.x + 57.0*p.y;

  float x = mix(hash(n), hash(n + 1.0), f.x);
  float y = mix(hash(n + 57.0), hash(n + 58.0), f.x);
  return mix(x, y, f.y);
}

float noise(float g) {
  float p = floor(g);
  float f = fract(g);

  f = f*f*(3.0 - 2.0*f);
  return mix(hash(p), hash(p + 1.0), f);
}

float voronoi(vec2 x) {
  vec2 p = floor(x);
  vec2 f = fract(x);

  vec2 res = vec2(8.0);

  for(int i = -1; i <= 1; i++)
  for(int j = -1; j <= 1; j++) {
    vec2 g = vec2(float(i), float(j));
    vec2 o = hash(p + g);
    if(i == 0)
      o = 0.5 + 0.5*sin(6.28138*o + 0.2*iTime);

    vec2 r = g + o - f;

    float d = max(abs(r.x), abs(r.y));

    if(d < res.x) {
      res.y = res.x;
      res.x = d;
    } else if(d < res.y) {
      res.y = d;
    }
  }

  return res.y - res.x;
}

vec3 formula(vec2 p) {
  p *= mat2(cos(iTime*0.1), sin(iTime*0.1), -sin(iTime*0.1), cos(iTime*0.1));
  p += vec2(iTime*0.5, sin(iTime*0.7));

  float v = 0.0;
  float a = 0.7, f = 1.0;

  for(int i = 0; i < 4; i++) {
    float v1 = voronoi(p*f + 5.0);
    float v2 = 0.0;

    v1 = 1.0 - smoothstep(0.0, 0.4, v1);
    v2 =  a*noise(v1*5.5 + 0.3);

    v += v2;

    f *= 1.6;
    a *= 0.55;
  }

  return vec3(pow(v, 6.0), pow(v, 4.0), pow(v, 2.0));
}

vec3 grey = vec3(0.21, 0.72, 0.07);

vec3 cube(vec3 p, vec3 n) {
  vec3 m = pow(abs(n), vec3(10.0));

  vec3 x = formula(p.yz);
  vec3 y = formula(p.xz);
  vec3 z = formula(p.xy);

  return (m.x*x + m.y*y + m.z*z)/(m.x + m.y + m.z);
}

vec3 bump(vec2 p, float e) {
  vec2 h = vec2(e, 0.0);

  vec3 g = grey*mat3(
    formula(p + h) - formula(p - h),
    formula(p + h.yx) - formula(p - h.yx),
    -0.1/grey);

  return normalize(g);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ) {
  vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

  vec3 rd = normalize(vec3(p, 1.97));
  vec3 sn = normalize(bump(p, 0.008));
  vec3 re = reflect(rd, sn);

  vec3 col = vec3(0);

  vec3 mat = formula(p);

  col += pow(clamp(dot(-rd, re), 0.0, 1.0), 8.0);
  col *= vec3(0.2, 0.5, 1.4)*mat.b;

  col += vec3(0.2, 1.0, 0.5)*smoothstep(0.0, 2.0, sin(mat.r*mat.b));

  col = pow(col, vec3(1.0/2.2));
  fragColor = vec4(col, 1);
}

#define time iTime

//rotation matrix
//mat3 ry = mat3(cos(time), 0, -sin(time), 0, 1, 0, sin(time), 0, cos(time));


//lots of these functions are imported from here
//http://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdSphere(vec3 p, float r) {
	return length(p) - r;
}
float sdBox(vec3 p, vec3 b) {
	vec3 d = abs(p) - b;
	return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

float opU(float d1, float d2) {
	return min(d1, d2);
}
float opS(float d1, float d2) {
	return max(-d1, d2);
}
float opI(float d1, float d2) {
	return max(d1, d2);
}

float displace(vec3 p) {
	return sin(p.x + 1.1*time)*sin(p.y + 1.3*time)*sin(p.z + 1.2*time);
}

float opDisplace(vec3 p) {
	float d1 = opS(sdBox(mod(p, vec3(2.0)) - 0.5*vec3(2.0), vec3(1.0)), sdSphere(p, 3.0));
	float d2 = displace(p);
	return d1+d2;
}


//distance field
float dist(vec3 p) {
	return opU(opDisplace(p), sdBox(p + vec3(0, 1.1, 0), vec3(6, 0.1, 6)));
}


//normal
vec3 calcNormal(vec3 p) {
	float d = 0.001;
	return normalize(vec3(
		dist(p + vec3(d, 0, 0)) - dist(p + vec3(-d, 0, 0)),
		dist(p + vec3(0, d, 0)) - dist(p + vec3(0, -d, 0)),
		dist(p + vec3(0, 0, d)) - dist(p + vec3(0, 0, -d))
		));
}


struct ray {
	vec3 rayDir;
	vec3 position;
	vec3 normal;
	int steps;
	float t;
};

//ray marching
const int maxStep = 100;
ray trace(vec3 from, vec3 rayDir) {
	float t = 0.0;
	vec3 p;
	int steps;
	for(int i = 0; i < maxStep; i++) {
		p = from + t*rayDir;
		float d = dist(p)/2.0;
		t += d;
		if(d < 0.01) {
			p -= 0.001*rayDir;
			steps = i;
			break;
		}
	}
	return ray(rayDir, p, calcNormal(p), steps, t);
}

//shading function
vec3 lightPos = vec3(0, 10, 10);
const float mix1 = 0.3;
const float mix2 = 0.5;
const float shininess = 12.0;
vec3 shade(vec3 position, vec3 rayDir, vec3 normal, int steps) {
	ray tr = trace(lightPos, normalize(position - lightPos));
	bool visible;
	if(distance(tr.position, position) < 0.1) {
		visible = true;
	}
	else {
		visible = false;
	}

	float diffuse = max(dot(normal, -tr.rayDir), 0.0);
	float specular = pow(max(dot(rayDir, reflect(normal, -tr.rayDir)), 0.0), shininess);
	vec3 directColor = vec3(0);
	if(visible) directColor = vec3(mix(diffuse, specular, mix1));

	float ao = 1.0 - float(steps)/float(maxStep);
	vec3 indirectColor = ao * vec3(0.6, 0.7, 1);

	return 2.0*mix(directColor, indirectColor, mix2);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*gl_FragCoord.xy - iResolution.xy)/iResolution.x;

    //camera settings
	vec3 camPos = vec3(10.0*cos(time/2.0), 1, -10.0*sin(time/2.0));
	vec3 camFront = normalize(-camPos);
	vec3 camUp = vec3(0, 1.0, 0);
	vec3 camRight = cross(camFront, camUp);

    //ray marching
	vec3 rayDir = normalize(uv.x*camRight + uv.y*camUp + 1.0*camFront);
	ray tr = trace(camPos, rayDir);
    //shading
	vec3 color = shade(tr.position, tr.rayDir, tr.normal, tr.steps);

	fragColor = vec4(color, 1.0);
}

