float smooth_step( float edge0, float edge1, float x )
{
    float p = clamp((x - edge0) / (edge1 - edge0), 0.0, 1.0);
    float v = p * p * (3.0 - 2.0 * p); // smoothstep formula

    //v = smoothstep( edge0, edge1, x ); // Compare with built-in

    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(fragCoord.y < iResolution.y / 100.0)
    {
        float pct = (fragCoord.x / iResolution.x) + (iTime / 400.0);
        fragColor = vec4(sin(mix(0.0, 125.664, pct)),sin(mix(0.0, 125.664, pct + 0.33)),sin(mix(0.0, 125.664, pct + 0.66)),1.0);
    }
    else
    {
        float localTime = smooth_step(0.0, 1.0, mod(iTime, 30.0) / 30.0);
        float time = sqrt(sin(localTime * 3.14159));
        //float time = mod(iTime, 32.0) * 0.2;
        //float scale = min(200.0, 2.0 / pow(time, time));
        float scale = mix(1.5, 0.00001, time);
        float sizeX = 3.5 * scale;
        float sizeY = 2.0 * scale;
        //float centerX = -0.75;
        //float centerY = 0.0;
        float centerX = -1.78;
        float centerY = 0.0;
        float minX = centerX - sizeX / 2.0;
        float maxX = minX + sizeX;
        float minY = centerY - sizeY / 2.0;
        float maxY = minY + sizeY;
        float xPercent = fragCoord.x / iResolution.x;
        float yPercent = fragCoord.y / iResolution.y;
        float x0 = mix(minX, maxX, xPercent); //scaled x coordinate of pixel (scaled to lie in the Mandelbrot X scale (-2.5, 1))
        float y0 = mix(minY, maxY, yPercent); //scaled y coordinate of pixel (scaled to lie in the Mandelbrot Y scale (-1, 1))
        float x = 0.0;
        float y = 0.0;
        int iteration = 0;
        int max_iteration = 1000;
        while (x*x + y*y < 2.0*2.0  &&  iteration < max_iteration) {
            float xtemp = x*x - y*y + x0;
            y = 2.0*x*y + y0;
            x = xtemp;
            iteration = iteration + 1;
        }
        //float color = sin(float(iteration) / float(max_iteration)); //palette[iteration]
        //fragColor = vec4(color);
        float pct = (float(iteration) / float(max_iteration)) + (iTime / 400.0);
        fragColor = vec4(sin(mix(0.0, 125.664, pct)),sin(mix(0.0, 125.664, pct + 0.33)),sin(mix(0.0, 125.664, pct + 0.66)),1.0);
    }
}

const float epsilon = 0.0001;
vec3 light;

float scene(vec3, inout int);
bool march (out vec3, in vec3, in vec3, inout int);
vec3 calc_gradient(in vec3);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    light = vec3(2.0 * sin(iTime), 2.0 * cos(iTime), 0.0)/1.5;

    vec3 cameraPosition = vec3(0.0, 0.0, -3.0);
    vec3 cameraFocalPoint = cameraPosition - vec3(0.0, 0.0, 1.0);
    cameraPosition.xy += (fragCoord.xy - iResolution.xy/2.0) / iResolution.xx;

    vec3 hitPosition;
    int id = 1;

    bool hit = march(hitPosition, cameraPosition, cameraPosition - cameraFocalPoint, id);

    if(!hit) {
        fragColor*=0.0;
        return;
    }

    if(id == 0){
        fragColor = vec4(1.0,1.0,1.0,1.0);
    	return;
    }
    if(id == 1) fragColor = vec4 (1.0, 0.0, 0.0, 1.0);
    if(id == 2) fragColor = vec4 (1.0, 1.0, 1.0, 1.0);
    if(id == 3) fragColor = vec4 (0.0, 1.0, 1.0, 1.0);
    if(id == 4) fragColor = vec4 (0.0, 0.0, 1.0, 1.0);


    vec3 normal = normalize(calc_gradient(hitPosition));
    vec3 lm = normalize(light - hitPosition);
    vec3 rm = normalize(2.0 * dot(lm, normal) * normal - lm);
    vec3 v = normalize(cameraPosition - hitPosition);

    vec3 lightEnd;
    int occId;

    march(lightEnd, hitPosition + 2.0*lm*epsilon + 2.0*normal*epsilon, lm, occId);

    vec3 lightcoefficient = vec3(0.12, 0.12, 0.12);

    if(length(lightEnd-light) < 0.2 || false) {
		lightcoefficient += 
            0.35 * dot(lm, normal) + 
            0.4 * pow(clamp(dot(rm, v),0.0,1.0), 4.0);
    }

    fragColor.xyz *= lightcoefficient;
}

float max3(float a, float b, float c) {
    return max(a, max(b, c));
}

vec3 calc_gradient(vec3 p) {
    vec3 delta = vec3(epsilon, 0.0, 0.0);
    int id;
 	return normalize(vec3 (
        (scene(p + delta.xyz,id) - scene(p,id))/ epsilon, 
        (scene(p + delta.yxz,id) - scene(p,id))/ epsilon, 
        (scene(p + delta.zyx,id) - scene(p,id))/ epsilon
   	));

}

bool march(out vec3 endPosition, in vec3 startPosition, in vec3 direction, inout int id) {

    vec3 mdir = normalize(direction);
    endPosition = startPosition;

    for(int i = 0; i < 400; i++) {
        float distance = scene(endPosition,id);

        if(distance < epsilon)
            return true;

        if(distance > 1000.0)
            return false;

        endPosition += distance * mdir;
    }

    return false;

}

float scene (vec3 p, inout int id) {
	float s1 = length(p) - 0.5;
    float s2 = length(p - vec3(1.0 * sin(-iTime*0.3), 2.0*cos(iTime*0.3), 0.0)) - 0.2;
    float l = length(p-light)-0.05;
    float b = max(abs(p.x - 2.0), max(abs(p.y),abs(p.z))) - 0.5;
    float c = max3(
    	abs(p.x + 2.0) - 0.4,
    	abs(p.y - 0.0) - 2.0,
    	abs(p.z - 0.0) - 4.0
    );

    if (l < s1 && l < s2 && l < b && l < c) id = 0;
    else if(s1 < s2 && s1 < b && s1 < c) id = 1;
    else if(s2 < b && s2 < c) id = 2;
    else if(b < c) id = 3;
    else id = 4;

    return min(s1,min(s2,min(l,min(b,c))));
}

float epsilon = 0.001;
float sphere_r = 0.20;
vec3 sphere_c = vec3(0.9,0.5,0.5);
vec3 point_behind_screen = vec3 (0.9,0.5,-2.0); 
float z = 0.0;
vec3 camera_pos = vec3(0.0,0.0,-3.0);


float SDF(vec3 p, inout int id){
    vec3 sphere_c2 = vec3(0.1 + cos(iTime),0.35, 3.5 + 3.8*sin(iTime));
    float c1 = length(p - sphere_c) - sphere_r;
    float c2 = length(p - sphere_c - sphere_c2) - sphere_r;

    if(c1 < c2){ 
        id = 0;
    }
    else{ 
        id = 1;
    }

    return min(c1,c2);
}

vec3 calc_gradient (in vec3 p){
    int id;
    return normalize (vec3((SDF(vec3(p.x+epsilon,p.y,p.z),id) - SDF(vec3(p.x,p.y,p.z),id))/epsilon, 
             (SDF(vec3(p.x,p.y + epsilon,p.z),id) - SDF(vec3(p.x,p.y,p.z),id))/epsilon, 
             (SDF(vec3(p.x,p.y,p.z + epsilon),id) - SDF(vec3(p.x,p.y,p.z),id))/epsilon));
}


bool ray_march(in vec3 start, in vec3 dir, out vec3 end, inout int id){
    end = start;

    for(int i = 0; i < 200; i++){

        float dist_to_object = SDF(end,id);

        if(dist_to_object < epsilon){
            return true;
        }

        if(dist_to_object > 1000.0){
            return false;
        }

        end += dist_to_object * dir;

    }
    return false;
} 


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 light_point = vec3(2.0*sin(iTime),0.5,2.0*cos(iTime));
    vec2 screen = fragCoord/iResolution.y;
    vec3 point_on_screen = vec3(screen,z);
    vec3 end;
	vec3 point_dir = normalize (point_on_screen - point_behind_screen);
    int id = 1;

    if(!ray_march(point_on_screen, point_dir,end,id)){
        fragColor = vec4(0.0,0.0,0.0,0.1);
        return;
    }

    if(id == 0)
    	fragColor = vec4 (0.0, 0.0, 0.1, 1.0);
    if(id == 1)
        fragColor = vec4 (0.1, 0.0, 0.1, 1.0);

    vec3 lm = normalize (light_point - end) ;
	vec3 normal = normalize (calc_gradient(end));
	vec3 rm = normalize (2.0 * dot(lm , normal) * normal-lm);
    vec3 v = normalize (camera_pos - point_on_screen); 
    fragColor.xyz += vec3(0.1, 0.1, 0.1) +
        0.3 * dot(lm, normal) +
        0.4 * pow(clamp(dot(rm, v),0.0,1.0), 10.0);

}

