const vec3 ld = vec3(0.,1., .5);

float t = 0.;

float fullAtan(vec2 p)
{
    return step(0.0,-p.x)*3.14159 + sign(p.x) * atan(p.x, sign(p.x) * p.y);
}

float gear(vec2 p)
{
	float r = min(max(.5 * (cos(atan(p.x, p.y) * 16.)) + 0.9, 0.4), .6);
	return length(p) - .9 - r;
}

vec2 df(vec3 p)
{
	float a = fullAtan(p.xz) + iTime;

    vec2 rev = vec2(
        max(max(abs(p.x*.866 - p.z*.5), abs(-p.x*.866 - p.z*.5)), abs(p.z)), // 2d hex
            p.y) - 2.5;
    rev *= mat2(cos(a),-sin(a),sin(a),cos(a));

	vec2 res = vec2(abs(gear(rev))-0.032, 0);
    if (iMouse.z > 0.)
		if (p.z-1. > res.x) 
            res = vec2(p.z-1., 1);

	float plane = p.y+1.;
	if (plane < res.x)
		return vec2(plane,2);

	return res; 

}

vec3 nor( vec3 p, float prec )
{
    vec2 e = vec2( prec, 0. );
    vec3 n = vec3(
		df(p+e.xyy).x - df(p-e.xyy).x,
		df(p+e.yxy).x - df(p-e.yxy).x,
		df(p+e.yyx).x - df(p-e.yyx).x );
    return normalize(n);
}

// from iq code
float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<1; i++ )
    {
		float h = df( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += h*.25;
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0., 1. );
}

// from iq code
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<10; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = df( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

