// Grab all sky information for a given ray from camera
// from Dave Hoskins // https://www.shadertoy.com/view/Xsf3zX
vec3 GetSky(in vec3 rd, in vec3 sunDir, in vec3 sunCol)
{
	float sunAmount = max( dot( rd, sunDir), 0.0 );
	float v = pow(1.0-max(rd.y,0.0),6.);
	vec3  sky = mix(vec3(.1, .2, .3), vec3(.32, .32, .32), v);
	sky = sky + sunCol * sunAmount * sunAmount * .25;
	sky = sky + sunCol * min(pow(sunAmount, 800.0)*1.5, .3);
	return clamp(sky, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragColor = vec4(1);

	vec2 g = fragCoord.xy;
	vec2 si = iResolution.xy;
	vec2 uv = (2.*g-si)/min(si.x, si.y);

	vec2 camp = vec2(.73,.37) * 5.;
	vec3 rayOrg = vec3(cos(1.57),sin(camp.y),sin(1.57)) * camp.x * 2.;
	vec3 camUp = vec3(0,1,0);
	vec3 camOrg = vec3(0,1.2,0);

	float fov = .5;
	vec3 axisZ = normalize(camOrg - rayOrg);
	vec3 axisX = normalize(cross(camUp, axisZ));
	vec3 axisY = normalize(cross(axisZ, axisX));
	vec3 rayDir = normalize(axisZ + fov * uv.x * axisX + fov * uv.y * axisY);

	float s = 0.;
	float d = 0.;
	vec3 p = rayOrg + rayDir * d;
	float dMax = 20.;

	for (float i=0.; i<250.; i++)
	{
		if (log(d*d/s/1e5)>0. || d>dMax) break;
		s = df(p).x;
		d += s * 0.2;
		p = rayOrg + rayDir * d;	
	}

    vec3 sky = GetSky(rayDir, ld, vec3(1.5));

	if (d<dMax)
	{
		vec3 n = nor(p, 0.001);

		// iq lighting
		float occ = calcAO( p, n );
        float amb = clamp( 0.5+0.5*n.y, 0.0, 1.0 );
        float dif = clamp( dot( n, ld ), 0.0, 1.0 ) * (df(p+n*1.16).x);
        float spe = pow(clamp( dot( rayDir, ld ), 0.0, 1.0 ),16.0);

        dif *= softshadow( p, ld, 0.1, 10. );

        vec3 brdf = vec3(0.0);
        brdf += 1.20*dif*vec3(1.00,0.90,0.60);
        brdf += 1.20*spe*vec3(1.00,0.90,0.60)*dif;
        brdf += 0.30*amb*vec3(0.50,0.70,1.00)*occ;
        brdf += 0.02;
        fragColor.rgb *= brdf;

        fragColor.rgb = mix( fragColor.rgb, sky, 1.0-exp( -0.01*d*d ) ); 
	}
	else
	{
		fragColor.rgb = sky;
	}
}

float udRoundBox( vec3 p, vec3 b, float r )
{
  return length(max(abs(p)-b,0.0))-r;
}

float map(vec3 p)
{
   float len=1.45;
   float thick=.1;
   float rad=.3;
    p.z = p.z + .4 * p.y;
  return min(
	min(udRoundBox(p, vec3(thick,thick,len), rad),
		udRoundBox(p+vec3(0.,len,0.), vec3(thick,thick,len), rad)),
	min(udRoundBox(p+vec3(0.,0.+(len-8.*thick),0.-(len-8.*thick)), vec3(thick,len,thick), rad),
		udRoundBox(p+vec3(0.,0.+(len-8.*thick),len-(len-8.*thick)), vec3(thick,len,thick), rad)));
}

vec3 calcNormal( in vec3 pos, in float t )
{
    vec2 e = vec2(1.0,-1.0)*0.001*t;
    return normalize( e.xyy*map( pos + e.xyy ) +
					  e.yyx*map( pos + e.yyx ) +
					  e.yxy*map( pos + e.yxy ) +
					  e.xxx*map( pos + e.xxx ) );
}

vec3 cameraRay(in vec3 position, in vec3 target, in vec2 fragCoord)
{
    vec3 eye = normalize(target - position);
    vec3 up = vec3(0., 1., 0.);
    vec3 right = cross(eye, up);
    up = cross(eye,right);

    mat3 cameraMatrix = mat3(right.x, up.x, eye.x,
                			 right.y, up.y, eye.y,
                			 right.z, up.z, eye.z);

    vec2 uv = fragCoord.xy / iResolution.xy - vec2(.5);
    uv.x = uv.x * iResolution.x/iResolution.y;
    float focalDistance = .8;
    return (normalize(vec3(uv.x,uv.y,focalDistance)) * cameraMatrix) * .5;
}


vec4 shade(in vec3 pos, in vec3 normal, in vec3 cameraVector, in vec3 lightPos, in vec4 lightColor, in vec4 surface)
{
    vec3 light = normalize(lightPos - pos);
    float diffuse = -dot(normal, -cameraVector);
    return mix(surface, vec4(1.), diffuse * 20. / length(pos));
}

vec4 march(in vec3 ro, in vec3 rd, vec3 shapeLoc, vec3 light)
{

    vec4 color = vec4(118.0,195.0,101.0,255.0) / vec4(255.); // Sky color

    float t = 0.0;
    const int maxSteps = 110;
    for(int i = 0; i < maxSteps; ++i)
    {
        vec3 p = ro + rd * t;

        vec3 po = p + vec3(0.0,8.0*iTime, 5.*(p.z - mod(p.z, 8.)));

        float ang = iTime+20.*(length(po)-mod(length(po),8.));
        float scale = 1.;
        mat3 rotation = mat3(scale*cos(ang),-sin(ang), 0.,
                  sin(ang),scale*cos(ang), 0.,
                   0.,0.,scale);

        vec3 pm = mod(po,8.0) - shapeLoc;

        pm = rotation * pm;

        float d = map(pm);

        if(d < 0.001)
        {
            vec3 normal = calcNormal(pm, t);
            ang = -ang;
            mat3 rotationI = mat3(cos(ang),-sin(ang), 0.,
                  sin(ang),cos(ang), 0.,
                   0.,0.,1.);
            normal = rotationI * normal;
			color = shade(p, normal, -rd, light,
                          vec4(1.), 
                          color);
            break;
        }

        t += d;
    }

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 shapeLoc = vec3(3.0,3.0,3.0);
    vec3 cameraLoc = vec3(4.0 * sin(0.6*iTime), 6.0 + 4.0 * sin(0.4*iTime) , 4.0) + shapeLoc;
    vec3 cameraTarget = shapeLoc;// + vec3(0.0,1.+1.*sin(iTime*.01),0.0);
    vec3 light = vec3(-1.);

    vec3 ro = cameraLoc;
    vec3 rd = cameraRay(cameraLoc, cameraTarget, fragCoord);

    fragColor = march(ro, rd, shapeLoc, light);
}

#define SMOOTH_SHAPE_CONTOURS 1

//Functions
vec2 rotate2dVec(vec2 uv, float rotation)
{
    float cs = cos(rotation);
	float sn = sin(rotation);

    float px = uv.x * cs - uv.y * sn; 
	float py = uv.x * sn + uv.y * cs;

    return vec2(px,py);
}

float drawSmoothCircle(vec2 uv, float distance, float rotation, float size, float smoothStepMin)
{
    vec2 circleCenter = rotate2dVec(uv,rotation) - vec2(0.0, distance);
    float circleShape = 1.0 - smoothstep(smoothStepMin,1.0,length(circleCenter)*size);

    return circleShape;
}

float drawCircle(vec2 uv, float distance, float rotation, float size)
{
    vec2 circleCenter = rotate2dVec(uv,rotation) - vec2(0.0, distance);
    float circleShape = 1.0 - min(floor(length(circleCenter)*size),1.0);

    return circleShape;
}


float smoothCheckIfOdd(float value)
{
    float result = abs(mod(value*0.5-0.5,1.0)-0.5)*2.0;
    return result;
}

//This palette solution is from http://iquilezles.org/www/articles/palettes/palettes.htm
vec3 colorPalette(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
	return a+b*cos(6.28318530718*(c*t+d));
}


//Constants
float myTime;
float pi = 3.14159265358979323846264338327950288419716;
float numCircles;
float sinTime;
float circleSize;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    myTime = iTime * 2.0;
    numCircles = (floor((myTime+pi) * 0.5 * (1.0/(2.0*pi)))*2.0)+3.0;
    sinTime = (sin(myTime*0.5)+1.0)*0.35;
    circleSize = 1.0/sinTime;

	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 centeredUv = uv - 0.5;
    centeredUv.x *= iResolution.x/iResolution.y;
    centeredUv = rotate2dVec(centeredUv,(sin(myTime)+myTime*1.5)*0.25);

    float circlesResult;
    float smoothStepMin = 1.0-0.002-circleSize*0.002;

    if(SMOOTH_SHAPE_CONTOURS==1)
    {    
        for(int i = 0; i < 48; i++)
        {
            if(i > int(numCircles))
            {
                break;
            }
            circlesResult += drawSmoothCircle(centeredUv, 0.45, ((pi*2.0)/min(numCircles+1.0,48.0))*float(i),circleSize, smoothStepMin);
        }
    }
    else 
    {
        for(int i = 0; i < 48; i++)
        {
            if(i > int(numCircles))
            {
                break;
            }
            circlesResult += drawCircle(centeredUv, 0.45, ((pi*2.0)/min(numCircles+1.0,48.0))*float(i),circleSize);
        }
    }

    circlesResult = smoothCheckIfOdd(circlesResult);

    vec3 outColor = colorPalette(myTime*0.05 + length(centeredUv), vec3(0.5,0.5,0.5), vec3(0.5,0.5,0.5), vec3(1.0,1.0,1.0), vec3(0.0,0.33,0.67)) * circlesResult;

    fragColor = vec4(outColor,1.0);

}

//MATLAB Jet Colormap
//kbinani/glsl-colormap
//https://github.com/kbinani/glsl-colormap/blob/master/LICENSE
float colormap_red(float x) {
    if (x < 0.7) {
        return 4.0 * x - 1.5;
    } else {
        return -4.0 * x + 4.5;
    }
}

float colormap_green(float x) {
    if (x < 0.5) {
        return 4.0 * x - 0.5;
    } else {
        return -4.0 * x + 3.5;
    }
}

float colormap_blue(float x) {
    if (x < 0.3) {
       return 4.0 * x + 0.5;
    } else {
       return -4.0 * x + 2.5;
    }
}

vec4 colormap(float x) {
    float r = clamp(colormap_red(x), 0.0, 1.0);
    float g = clamp(colormap_green(x), 0.0, 1.0);
    float b = clamp(colormap_blue(x), 0.0, 1.0);
    return vec4(r, g, b, 1.0);
}


//math
vec2 cmult(vec2 z1, vec2 z2) {
    return vec2(z1.x*z2.x - z1.y*z2.y, z1.x*z2.y + z1.y*z2.x);
}


const int maxIter = 300;
float mandelbrot(vec2 c) {
    vec2 z = vec2(0.0, 0.0);
    vec2 zd = vec2(0.0, 0.0);
    for(int i = 0; i < maxIter; i++) {
        if(length(z) > 10.0) break;
        zd = 2.0*cmult(z, zd) + vec2(1.0, 0.0);
        vec2 za = vec2(abs(z.x), abs(z.y));
        z = cmult(za, za) + c;
    }

    float d = 0.5*length(z)*log(length(z))/length(zd);
    return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 offset = vec2(-1.75, -0.03);
    float scale = 1.0/pow((0.01*float(iFrame)), 2.0);
	vec2 uv = -scale*(2.0*fragCoord.xy - iResolution.xy) / iResolution.y + offset;

    float d = mandelbrot(uv);
    float color = clamp(8.0*d/scale, 0.0, 1.0);
    color = pow(color, 0.2);

	fragColor = colormap(color);
}

