vec2 cmult(vec2 a, vec2 b)
{
    vec2 c;
    c.x = a.x * b.x - a.y * b.y;
    c.y = a.x * b.y + a.y * b.x;
    return c;
}

vec2 weirdmap(vec2 a, vec2 b, float exp)
{
    vec2 c;
    c.x = a.x * b.x - pow(abs(a.y), abs(0.25 + exp));
    c.y = a.x * b.y + a.y * b.x;
    return c;
}

vec2 spacify(vec2 p)
{
    vec2 c = (p.xy / (iResolution.xy * 0.8)) - vec2(0.9, 0.6);
	return c;
}

vec2 unspacify(vec2 p)
{
    vec2 screenPos;
    screenPos.xy = p.xy + vec2(0.9, 0.6);
    screenPos.xy *= iResolution.xy * 0.8;
    return screenPos;
}


vec2 startwalk()
{
    vec2 z;
    vec2 mouse = iMouse.xy;
    clamp(mouse.x, 0.0, iResolution.x);
    clamp(mouse.y, 0.0, iResolution.y);

    z = 2.0 * spacify(mouse.xy);
    z.x += 0.2 * sin(iTime*3.0) * cos(iTime * 0.22);
    z.y += 0.2 * sin(iTime*2.0) * cos(iTime * 1.3);
    return z;
}

float pi2 = 0.5 * 3.14159265359;

float piecewise(float x)
{
	if (x <= 2.0 ) return sin(x);
    if (x <= 10.0) return sin(log(x));
    return sin(log(log(x)));
}


vec3 hsv2rgb(vec3 c) {
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec4 colour(vec2 start, vec2 end, int n)
{
    float g = piecewise(length(start - end));
    //g -= float(floor(g));
    float b = piecewise(length(spacify(iMouse.xy) - end)*2.0);
    //b -= float(floor(g));
    //float invsq = 1.0 / (length(start) * length(start));
    /*float v = g;
    float h = float(mod(float(n), 12.0))/12.0;
    float s = b;
    vec3 rgb = hsv2rgb(vec3(h,s,v));
    return vec4(rgb[0], rgb[1], rgb[2], 1.0);*/

    return vec4(float(n)/64.0, g, b, 1.0);
}


void pdeMainImage(out vec4 fragColor, in vec2 fragCoord, out vec2 zplas )
{
	vec2 c = spacify(fragCoord);

    vec2 c1 = c * 2.5;
    vec2 c2 = c * (2.3 + 0.2 * sin(iTime));
    float vert = 5.0*(iMouse.y / iResolution.y) + 0.4 * sin(iTime / 2.1);

    fragColor = vec4(1.0,0.0,0.0,1.0);
    vec2 z = startwalk();
    int i = 0;
    vec2 z2;
    zplas = z;
    for (int n = 0; n < 64; n++) {
        if (i == 0 && length(z) > 16.0) i = n;
        if (n == 3) zplas = z;
        /*if (n == 4 && length(z) > 2.0) zplas = z; 
        if (n == 5 && length(z) > 3.0) zplas = z;*/
        if (n == 10) z2 = z;
        z = weirdmap(z, z, vert) + c2;                
    }
    fragColor = colour(c1, z2, i);

	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
}

// PLASMA EFFECT
//
// We said that the a pixel's color only depends on its coordinates
// and other inputs (such as time)
// 
// There is an effect called Plasma, which is based on a mixture of
// complex function in the form of f(x,y).
//
// Let's write a plasma!
//
// http://en.wikipedia.org/wiki/Plasma_effect

float PI = 3.14;

vec3 RotateVec3Z(vec3 v, float rad)
{
	float cos = cos(rad);
	float sin = sin(rad);
	//if (RIGHT_HANDED_COORD)
	return vec3(cos * v.x + sin * v.y, -sin * v.x + cos * v.y, v.z);
}

vec2 RotateVec2Z(vec2 v, float rad)
{
	float cos = cos(rad);
	float sin = sin(rad);
	//if (RIGHT_HANDED_COORD)
	return vec2(cos * v.x + sin * v.y, -sin * v.x + cos * v.y);
}

void daisyMainImage( out vec4 fragColor, in vec2 fragCoord, in vec2 zplas )
{
    fragCoord = unspacify(zplas);
	vec2 p = vec2(fragCoord.xy / iResolution.xy);
	vec2 r = 2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
    r = RotateVec2Z(r, 0.5 * PI + iTime / 3.0);

    float t = iTime;
    r = r * 8.0;

    float v1 = sin(r.x +t);
    float v2 = sin(r.y +t);
    float v3 = sin(r.x+r.y +t);
    float v4 = sin(sqrt(r.x*r.x+r.y*r.y) + t);
	float v = v1+v2+v3+v4;

	vec3 ret;

	// mix colors
	v *= 1.0;
	ret = vec3(sin(v), sin(v+0.5*PI), sin(v+1.0*PI));

	ret = 0.5 + 0.5*ret;

    vec3 pixel = ret;
