// Based on IQ's tutorial http://www.iquilezles.org/blog/?p=2411
vec2 sphIntersect( vec3 ro, vec3 rd, vec4 sph )
{
    vec3 oc = ro - sph.xyz;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - sph.w*sph.w;
    float h = b*b - c;
    if( h<0.0 ) return vec2(1.0 / 0.0);
    h = sqrt( h );
    return vec2(-b - h, -b +h);
}


void marchRay( vec3 ro, vec3 rd, inout vec3 finalCol)
{ 

    vec2 intersect = sphIntersect(ro, rd, vec4(0,0,0.0,1.5));
    if(intersect.x == 1.0/0.0 || intersect.y < 0.0)
        return;

    intersect.x = max(intersect.x, 0.0);
    ro += rd * intersect.x;    
    float intersectionLength = (intersect.y - intersect.x);

    float stepLength = intersectionLength/float(STEPS-1);

    float density = 0.0;
    float t = 0.0;
	for( int i=0; i<STEPS; i++ )
	{        
        vec3 p = ro + t*rd;
        density += smoothstep(0.5, 0.65, fractalNoise(p*0.65));
        t += stepLength;
	}
    density /= float(STEPS);
    float edgeDot = abs(dot(rd, normalize(ro)));
    float edgeFade = 1.0-pow(1.0-edgeDot, 4.0);
    density *= edgeFade;
    //density += 0.5*abs(dot(rd, normalize(ro)));
    density = smoothstep(0.2, 0.65, density);
    density *= intersectionLength/3.0;

    // Cloud colour
    finalCol.rgb = mix(finalCol, vec3(1), density);

    // Orb shading
    float orbDot = 0.5+0.5*(dot(SUN_VECTOR, normalize(ro)));
    finalCol = mix(finalCol, vec3(1.0), 0.5*orbDot*edgeFade*(intersectionLength/3.0) );

    // Edge halo
    float edgeRing = 0.75*pow(1.0-abs(2.0*pow(1.0-edgeDot, 2.0)-1.0), 4.0);
    finalCol = mix(finalCol, vec3(0.5) + 0.5*(finalCol + vec3(0.5)), edgeRing);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
	vec2 R = iResolution.xy,
    uv = ( fragCoord - .5*R) / R.y;

    vec2 mouse = ( iMouse.xy - .5*R) / R.y;
    mouse *= step(0.0,iMouse.z);

    float camDist = 1.0/tan(radians(FOV*0.5));

    vec3 camForward = vec3(0, 0, 1);
    camForward = mat3(rotationMatrix(UP, mouse.x*-radians(180.0) + iTime*radians(10.0))) * camForward;
	vec3 camRight = cross(UP, camForward);
    camForward = mat3(rotationMatrix(camRight, mouse.y*radians(90.0) )) * camForward;
	vec3 camUp = cross(camForward, camRight);
    vec3 vectorToPixel = vec3(uv.xy,camDist)*NEARPLANE;
   	vectorToPixel = (uv.x*camRight + uv.y*camUp + camDist*camForward)*NEARPLANE;
    vec3 camPosition = camForward * (-2.4 + 0.5*sin(0.1*(iTime-32.0)));

    float skyDot = dot(normalize(vectorToPixel), SUN_VECTOR);
    // BG gradient:
    vec3 bgCol = mix(vec3(0.2, 0.1, 0.4), vec3(0.4, 0.6, 1.0), 0.5+0.5*skyDot);
    // Add sun flare:
    bgCol = mix( bgCol, vec3(1.15, 1.0, 1.5), pow(max(1.0-degrees(acos(clamp(skyDot, 0.0, 1.0)))*0.015, 0.0), 8.0));
    vec3 result = vec3(bgCol);
    marchRay(camPosition, normalize(vectorToPixel), result);
    //fragColor = vec4(mix(bgCol, result.rgb, result.a), 1.0);
    fragColor = vec4(result, 1);
}

//math
vec2 cmult(vec2 z1, vec2 z2) {
    return vec2(z1.x*z2.x - z1.y*z2.y, z1.x*z2.y + z1.y*z2.x);
}
vec2 cpow(vec2 z, float a) {
    float r = length(z);
    float arg = atan(z.y, z.x);

    r = pow(r, a);
    arg *= a;

    return vec2(r*cos(arg), r*sin(arg));
}


const int maxIter = 300;
float mandelbrot(vec2 c, float n) {
    vec2 z = vec2(0.0, 0.0);
    vec2 zd = vec2(0.0, 0.0);
    for(int i = 0; i < maxIter; i++) {
        if(length(z) > 10.0) break;
        zd = n*cmult(cpow(z, n-1.0), zd) + vec2(1.0, 0.0);
        z = cpow(z, n) + c;
    }

    float d = 0.5*length(z)*log(length(z))/length(zd);
    return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 offset = vec2(-0.749, 0.101);
    float scale = 1.0/pow((0.01*float(iFrame)), 1.0);
