    float d = mandelbrot(uv, float(iTime)/2.0 + 1.0);
    float color = clamp(8.0*d/scale, 0.0, 1.0);
    color = pow(color, 0.2);

	fragColor = vec4(vec3(color), 1.0);
}

float maxcomp(in vec3 p ) { return max(p.x,max(p.y,p.z));}
float sdBox( vec3 p, vec3 b )
{
  vec3  di = abs(p) - b;
  float mc = maxcomp(di);
  return min(mc,length(max(di,0.0)));
}

const mat3 ma = mat3( 0.60, 0.00,  0.80,
                      0.00, 1.00,  0.00,
                     -0.80, 0.00,  0.60 );

vec4 map( in vec3 p )
{
    float d = sdBox(p,vec3(1.0));
    vec4 res = vec4( d, 1.0, 0.0, 0.0 );

    float ani = smoothstep( -0.2, 0.2, -cos(0.5*iTime) );
	float off = 1.5*sin( 0.01*iTime );

    float s = 1.0;
    for( int m=0; m<4; m++ )
    {
        p = mix( p, ma*(p+off), ani );

        vec3 a = mod( p*s, 2.0 )-1.0;
        s *= 3.0;
        vec3 r = abs(1.0 - 3.0*abs(a));
        float da = max(r.x,r.y);
        float db = max(r.y,r.z);
        float dc = max(r.z,r.x);
        float c = (min(da,min(db,dc))-1.0)/s;

        if( c>d )
        {
          d = c;
          res = vec4( d, min(res.y,0.2*da*db*dc), (1.0+float(m))/4.0, 0.0 );
        }
    }

    return res;
}

vec4 intersect( in vec3 ro, in vec3 rd )
{
    float t = 0.0;
    vec4 res = vec4(-1.0);
	vec4 h = vec4(1.0);
    for( int i=0; i<64; i++ )
    {
		if( h.x<0.002 || t>10.0 ) break;
        h = map(ro + rd*t);
        res = vec4(t,h.yzw);
        t += h.x;
    }
	if( t>10.0 ) res=vec4(-1.0);
    return res;
}

float softshadow( in vec3 ro, in vec3 rd, float mint, float k )
{
    float res = 1.0;
    float t = mint;
	float h = 1.0;
    for( int i=0; i<32; i++ )
    {
        h = map(ro + rd*t).x;
        res = min( res, k*h/t );
		t += clamp( h, 0.005, 0.1 );
    }
    return clamp(res,0.0,1.0);
}

vec3 calcNormal(in vec3 pos)
{
    vec3  eps = vec3(.001,0.0,0.0);
    vec3 nor;
    nor.x = map(pos+eps.xyy).x - map(pos-eps.xyy).x;
    nor.y = map(pos+eps.yxy).x - map(pos-eps.yxy).x;
    nor.z = map(pos+eps.yyx).x - map(pos-eps.yyx).x;
    return normalize(nor);
}

// light
vec3 light = normalize(vec3(1.0,0.9,0.3));

vec3 render( in vec3 ro, in vec3 rd )
{
    // background color
    vec3 col = mix( vec3(0.3,0.2,0.1)*0.5, vec3(0.7, 0.9, 1.0), 0.5 + 0.5*rd.y );

    vec4 tmat = intersect(ro,rd);
    if( tmat.x>0.0 )
    {
        vec3  pos = ro + tmat.x*rd;
        vec3  nor = calcNormal(pos);

        float occ = tmat.y;
		float sha = softshadow( pos, light, 0.01, 64.0 );

		float dif = max(0.1 + 0.9*dot(nor,light),0.0);
		float sky = 0.5 + 0.5*nor.y;
        float bac = max(0.4 + 0.6*dot(nor,vec3(-light.x,light.y,-light.z)),0.0);

        vec3 lin  = vec3(0.0);
        lin += 1.00*dif*vec3(1.10,0.85,0.60)*sha;
        lin += 0.50*sky*vec3(0.10,0.20,0.40)*occ;
        lin += 0.10*bac*vec3(1.00,1.00,1.00)*(0.5+0.5*occ);
        lin += 0.25*occ*vec3(0.15,0.17,0.20);	 

        vec3 matcol = vec3(
            0.5+0.5*cos(0.0+2.0*tmat.z),
            0.5+0.5*cos(1.0+2.0*tmat.z),
            0.5+0.5*cos(2.0+2.0*tmat.z) );
        col = matcol * lin;
    }

    return pow( col, vec3(0.4545) );
}

void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 fragRayOri, in vec3 fragRayDir )
{
    vec3 col = render( fragRayOri + vec3(0.0,1.0,2.5), fragRayDir );
    fragColor = vec4( col, 1.0 );
}

void mainPlain(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x/iResolution.y;

    float ctime = iTime;
    // camera
    vec3 ro = 1.1*vec3(2.5*sin(0.25*ctime),1.0+1.0*cos(ctime*.13),2.5*cos(0.25*ctime));
    vec3 ww = normalize(vec3(0.0) - ro);
    vec3 uu = normalize(cross( vec3(0.0,1.0,0.0), ww ));
    vec3 vv = normalize(cross(ww,uu));
    vec3 rd = normalize( p.x*uu + p.y*vv + 2.5*ww );

    vec4 col;
    mainVR(col, fragCoord, ro - vec3(0.0,1.0,2.5), rd);

    fragColor = col;
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainAnaglyph(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x/iResolution.y;

    float ctime = iTime;
    // camera
    vec3 ro = 1.1*vec3(2.5*sin(0.25*ctime),1.0+1.0*cos(ctime*.13),2.5*cos(0.25*ctime));
    vec3 ww = normalize(vec3(0.0) - ro);
    vec3 uu = normalize(cross( vec3(0.0,1.0,0.0), ww ));
    vec3 vv = normalize(cross(ww,uu));
    //vec3 rd = normalize( p.x*uu + p.y*vv + 2.5*ww );
    vec3 ta = vec3(0.0);

	vec3 eyes_line = normalize(cross(vec3(0.0, 1.0, 0.0), ro));
	float d_eyes_2 = 0.08;
	vec3 ro_left = ro + d_eyes_2 * eyes_line;
	vec3 ro_right = ro - d_eyes_2 * eyes_line;
    vec3 ta_left = ta + d_eyes_2 * eyes_line;
    vec3 ta_right = ta - d_eyes_2 * eyes_line;


	// camera-to-world transformation
    mat3 ca_left  = setCamera(ro_left, ta_left, 0.0);
    mat3 ca_right = setCamera(ro_right, ta_right, 0.0);

    // rays' direction
	vec3 rd_left = ca_left * normalize( vec3(p.xy,1.0) );
	vec3 rd_right = ca_right * normalize( vec3(p.xy,1.0) );

    // render both eye
    vec4 col_left;
    mainVR(col_left, fragCoord, ro_left - vec3(0.0,1.0,2.5), rd_left);
    vec4 col_right;
    mainVR(col_right, fragCoord, ro_left - vec3(0.0,1.0,2.5), rd_right);

    //vec3 col = vec3( col_right.r, col_left.g, col_left.b);
    vec3 col = vec3( col_left.r, col_right.g, col_right.b);

    fragColor = vec4(col, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    // mainPlain(fragColor, fragCoord);
    mainAnaglyph(fragColor, fragCoord);
}

// Based on won3d's smoothstep filter example (https://www.shadertoy.com/view/MlGXD3)
// Which was in turn based on hornet's bicubic filter example (https://www.shadertoy.com/view/MtVGWz)

vec4 sampleLevel0( vec2 uv )
{
    return texture( iChannel0, uv, -10.0 );
}


vec4 SampleSmoothlinear( vec2 uv, vec2 texSize )
{
    // Round  down the sample location to get the exact center of our "starting" texel
    // The starting texel will be at location [1, 1] in the grid, where [0, 0] is the
    // top left corner.
    vec2 samplePos = uv * texSize;
    vec2 texPos1 = floor(samplePos - 0.5) + 0.5;

    // Compute the fractional offset from our starting texel to our original sample.
    vec2 f = samplePos - texPos1;

    // !!! smoothstep fractional offsets. Cheaper things would likey work as well but 
    // this is simple as a PoC.
    f = smoothstep(vec2(0.0), vec2(1.0), f);

    // That's it!
    return sampleLevel0((texPos1 + f)/texSize);
}


vec4 SampleHaversinelinear( vec2 uv, vec2 texSize )
{
    // Round  down the sample location to get the exact center of our "starting" texel
    // The starting texel will be at location [1, 1] in the grid, where [0, 0] is the
    // top left corner.
    vec2 samplePos = uv * texSize;
    vec2 texPos1 = floor(samplePos - 0.5) + 0.5;

    // Compute the fractional offset from our starting texel to our original sample.
    vec2 f = samplePos - texPos1;

    // !!! change smoothstep to haversine
    f = ( vec2( 1.0 ) - cos( f * 3.141592) ) * 0.5;

    // That's it!
    return sampleLevel0((texPos1 + f)/texSize);
}

//note: uniform pdf rand [0;1[
vec3 hash32n(vec2 p)
{
	p  = fract(p * vec2(5.3987, 5.4421));
    p += dot(p.yx, p.xy +  vec2(21.5351, 14.3137));
	return fract(vec3(p.x * p.y * 95.4307, p.x * p.y * 97.5901, p.x * p.y * 93.8369));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 sample_uv = uv;
    sample_uv.x = mod( sample_uv.x, 0.33333 );
   	sample_uv += 0.01 * vec2( cos(iTime), sin(iTime) );
    sample_uv *= (iMouse.z>0.0)? iMouse.x * 0.001 : 0.06125;

    vec4 samplev;
    if ( uv.x < 0.3333 )
    	samplev = SampleSmoothlinear( sample_uv, iChannelResolution[0].xy );
    else if ( uv.x < 0.66667 )
        samplev = SampleHaversinelinear( sample_uv, iChannelResolution[ 0 ].xy );
    else
        samplev = sampleLevel0( sample_uv );

    fragColor = samplev;
    fragColor -= step(abs(uv.x-0.3333), 0.001);
    fragColor -= step(abs(uv.x-0.6667), 0.001);
    fragColor.rgb += (hash32n(uv+fract(iTime))+hash32n(uv+0.1337*fract(iTime))-1.0)/255.0; //dither output
}

#define T iTime
#define PI 3.1415926
void r(inout vec2 p, in float a) {p = cos(a)*p + sin(a)*vec2(p.y, -p.x);}
float sb(in vec3 p, in vec3 b) {vec3 d = abs(p) - b; return min(max(d.x,max(d.y,d.z)), 0.) + length(max(d, 0.));}
float sp(in vec3 p, in vec4 n) {return dot(p,n.xyz) + n.w;}

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float map(in vec3 p)
{
    p.y += sin(p.z*.5 + T);
    p.x += 2.;
    vec3 c = p*.5;
    p = mod(p-2., 4.)-2.;
    p = abs(p);
    float s = .3;
    float d = sb(p, vec3(s, p.y, s));
    d = smin(d, sp(c+vec3(0., .5, 0.), vec4(0., 1., 0., 1.)), .2);
    d = smin(d, length(cos(p)) - .1, .4);
    for (int i = 0; i < 3; i++)
    {
        p.y -= 1.;
        r(p.xy, PI / 2. + T * float(i));
        r(p.yz, PI / 2. - T * float(i));
        r(p.zx, PI / 4. * c.y + T);
        d = smin(d, sb(p, vec3(s, p.y, s)), .5);
        s *= .75;
    }
    return d;
}

// lj's raymarch from Revision
float df(in vec3 ro, in vec3 rd)
{
    float d = 0.;
    float ii;
    for (int i = 0; i < 100; i++)
    {
        ii = float(i);
        float m = map(ro + rd * d);
        d += m*.9;
        if (abs(m) < 0.02) break;
        if (d > 40.) break;
    }
    return 1.-ii/30.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy)*2.-1.;
    uv.x *= iResolution.x/iResolution.y;
    vec3 ro = vec3(uv, -T), rd = normalize(vec3(uv, -1.)), p, n, col;
    float m = df(ro, rd);
    p = ro + rd * m;
    col = vec3(m);
    col = abs(sin(col + vec3(.92424, .122, .5245) + T))+clamp(tan(1.+ T*.5)*.5, .1, .2);
	fragColor = vec4(col,1.0);
    fragColor = 1.-fragColor;
}

#define PI 3.1415926535897932384626433832795

const vec4 deepblue = vec4(0.05, 0.3, 0.74, 1.0);
const vec4 midblue = vec4(0.12, 0.36, 0.85, 1.0);
const vec4 whiteblue = vec4(0.5, 0.76, 0.94, 1.0);

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	vec2 uv = (fragCoord.xy / iResolution.xy - vec2(0.5, 0.5)) * 4.0;
    uv.x += cos(iTime * 0.25) * 0.2;
    uv = uv + uv * sin(iTime * 0.2) * 0.1;
    float waves1 = abs(cos(uv.x * cos(uv.y / PI) * PI) + sin(uv.y * PI + iTime)) / 2.0;
    float waves2 = (cos(sin(uv.x - uv.y) + iTime) + sin(uv.x * uv.x * uv.y)) / 2.0;
    vec4 color = midblue;
    if(waves2 < 0.125) color = deepblue;
    if(waves1 < 0.125) color = whiteblue;
	fragColor = color;
}

