    float r = 1. + 0.2*pow(0.5 + 0.5*sin(iTime * TAU + p.y/2.), 4.);
    p.x = abs(p.x);
    p.y = p.y - p.x*sqrt((1.7-p.x));
    p.y/=0.7;
    o.gb*= smoothstep( r, r+aa, length(p) );
}

