mat2 scale(float size)
{
    return mat2(size,0,0,size);
}

vec2 multScreen (vec2 uv , float value)
{
    uv*=value;
    return fract(uv);
}

mat2 rotation(float angle)
{
    float rot = radians(angle);
	return mat2(cos(rot), -sin(rot), sin(rot), cos(rot));
}

vec3 createShape(vec2 uv, float sides, vec2 pos, vec3 color, float angle, float size, float thickness)
{
    uv = uv *2.-1.2;
    uv /= size;
    uv*=rotation(angle);
    uv.x+=pos.x;
    uv.y+=pos.y;

    float a = atan(uv.x,uv.y)+PI;
  	float r = TWO_PI/float(sides);
    float dist = cos(floor(.5+a/r)*r-a)*length(uv);

    return vec3(step(thickness,dist)-step(1.,dist)) * color;
}

vec3 rectangle(vec2 uv , vec2 position , vec2 size , vec3 color)
{
    float xBorder = position.x + size.x;
    float yBorder = position.y + size.y;

    float hor = step(0.0,uv.x-position.x)-step(0.0,uv.x-xBorder);
    float ver = step(0.0,uv.y-position.y)-step(0.0,uv.y-yBorder);

    return (ver * hor) * color;
}

vec3 verticalStripe(vec2 uv, float start, float end , vec3 color)
{
    return (step(start,uv.x)-step(end,uv.x))*color;
}

vec3 horizontalStripe(vec2 uv, float start, float end , vec3 color)
{
    return (step(start,uv.y)-step(end,uv.y))*color;
}


vec3 createCircle(vec2 uv, vec2 position,float radius,vec3 color)
{
    uv += position;
  	float pos =  (distance(uv,vec2(0.4)),distance(uv,vec2(0.6)));
  	return step(pos,radius) * color;
}

