//distance field
float df(vec3 p, float r) {
    return length(p) - r;
}


struct Ray {
    vec3 position;
    int steps;
    float t;
};

//it finds the intersection point and returns various data
const int maxSteps = 100;
Ray trace(vec3 from, vec3 rayDir) {
    vec3 p = vec3(0);
    int steps = 0;
    float t = 0.0;
    for(int i = 0; i < maxSteps; i++) {
        p = from + t*rayDir;
        float d = df(p, 1.0);
        if(d < 0.001) {
            steps = i;
            break;
        }
        t += d;
    }
    return Ray(p, steps, t);
}


//is point p is visible from the light?
bool isVisible(vec3 p, vec3 lightPos) {
    Ray tr = trace(lightPos, normalize(p - lightPos));
    if(distance(tr.position, p) < 0.1) {
        return true;
    }
    else {
        return false;
    }
}


