// Textures
vec3 checkerboard(vec2 p,float size){
  p*=size;
  vec2 f=fract(p.xy)-0.5;
  return vec3(f.x*f.y>0.0?1.0:0.0);
}

// Object Color
vec3 objcolor(vec3 p){
  return checkerboard(p.xz,0.4);
}

