  vec3 p=campoint;
  float d=maxdist;
  float s=0.0;
  for(int i=0;i<64;i++){
      d=objdistance(p);
      s+=d;
      if (d<preci) break;
      if (s>maxdist) break;
      p=campoint+ray*s;
  }
  */

  float fadeout=max(maxdist-s,0.0)/maxdist;
  fragColor = vec4(objcolor(p)*fadeout, 1.0);
}

