mat3 RotZ(float a){return mat3(cos(a),-sin(a),0.,sin(a),cos(a),0.,0.,0.,1.);}

vec3 path(vec3 p)
{
	p *= RotZ(p.z * 0.1);
    p += sin(p.zxy * 0.5) * 0.5;
	p *= RotZ(p.z * 0.2);
   	return sin(p.zxy * 0.2) * 2.;
}

float wireShape(vec3 p, float df, float size, float thick, vec3 space) 
{
	return length(
		min(
			min(
				vec2(df - size,abs(p.y)-space.y),
				vec2(df - size,abs(p.z)-space.z)
			),
			vec2(df - size,abs(p.x)-space.x)
		)
	) - thick;
}

float thick = 0.05;
float space = 0.5;
float radius = 2.;
float dist = 0.;

float df(vec3 p)
{
	p += path(p);
	p *= RotZ(p.z * 0.01);
    p.z = mod(p.z,space)-space*.5;
    //dist = max(abs(p.x),abs(p.y));
    //dist = length(p);
    //dist = max(abs(p.y)+p.x,-p.x);
    dist = abs(p.y);
    return wireShape(p, dist, radius, thick, vec3(0.15));
}

vec3 nor( vec3 pos, float prec )
{
	vec3 eps = vec3( prec, 0., 0. );
	vec3 nor = vec3(
	    df(pos+eps.xyy) - df(pos-eps.xyy),
	    df(pos+eps.yxy) - df(pos-eps.yxy),
	    df(pos+eps.yyx) - df(pos-eps.yyx) );
	return normalize(nor);
}

// return color from temperature 
//http://www.physics.sfasu.edu/astro/color/blackbody.html
//http://www.vendian.org/mncharity/dir3/blackbody/
//http://www.vendian.org/mncharity/dir3/blackbody/UnstableURLs/bbr_color.html
vec3 blackbody(float Temp)
{
	vec3 col = vec3(255.);
    col.x = 56100000. * pow(Temp,(-3. / 2.)) + 148.;
   	col.y = 100.04 * log(Temp) - 623.6;
   	if (Temp > 6500.) col.y = 35200000. * pow(Temp,(-3. / 2.)) + 184.;
   	col.z = 194.18 * log(Temp) - 1448.6;
   	col = clamp(col, 0., 255.)/255.;
    if (Temp < 1000.) col *= Temp/1000.;
   	return col;
}

// get density of the df at surfPoint
// ratio between constant step and df value
float SubDensity(vec3 surfPoint, float prec, float ms) 
{
	vec3 n;
	float s = 0.;
    const int iter = 3;
	for (int i=0;i<iter;i++)
	{
		n = nor(surfPoint,prec); 
		surfPoint = surfPoint - n * ms; 
		s += df(surfPoint);
	}

	return 1.-s/(ms*float(iter)); // s < 0. => inside df
}

float SubDensity(vec3 p, float s) 
{
	vec3 n = nor(p,s); 							// precise normale at surf point
	return df(p - n * s)/s;						// ratio between df step and constant step
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 g = fragCoord;
	vec2 si = iResolution.xy;

	vec2 uv = (g+g-si)/si.y;

	float time = iTime;

	vec3 ro = vec3(0,0, time*5.);
	ro -= path(ro);

	vec3 cv = ro + vec3(0,0,4); // cam view
	cv -= path(cv);

	vec3 lp = ro;	// light pos

	vec3 cu = normalize(vec3(0,1,0));
  	vec3 z = normalize(cv-ro);
    vec3 x = normalize(cross(cu,z));
  	vec3 y = cross(z,x);
  	vec3 rd = normalize(z + uv.x*x + uv.y*y);

	float s = 1., d = 0.;
	for (int i=0; i<250; i++) 
	{
		if (log(d*d/s/1e6)>0.|| d > 50.) break; 
		d += (s = df(ro+rd*d))*0.5;
	}

	vec3 p = ro + rd * d;											// surface point
	vec3 ld = normalize(lp-p); 										// light dir
	vec3 n = nor(p, 0.1);											// normal at surface point
	vec3 refl = reflect(rd,n);										// reflected ray dir at surf point 
	float diff = clamp( dot( n, ld ), 0.0, 1.0 ); 					// diffuse
	float fre = pow( clamp( 1. + dot(n,rd),0.0,1.0), 4. ); 			// fresnel
	float spe = pow(clamp( dot( refl, ld ), 0.0, 1.0 ),16.);		// specular
	vec3 col = vec3(0.8,0.5,0.2);
	float sss = df(p - n*0.001)/0.01;								// quick sss 0.001 of subsurface

	float sb = SubDensity(p, 0.01, 0.01);							// deep subdensity 
	vec3 bb = clamp(blackbody(100. * sb),0.,1.);					// blackbody color
	float sss2 = 1.0 - SubDensity(p, 1.5); 							// one step sub density of df of 1.5 of subsurface

	vec3 a = (diff + fre + bb * sss * .8 + col * sss * .2) * 0.35 + spe;
    vec3 b = col * sss2;

	fragColor.rgb = clamp(mix(a, b, .8-exp(-0.005*d*d)),0.,1.);
}

mat3 rotateYmat(float ang)
{
    return mat3(cos(ang), 0.0, sin(ang),
                0.0, 1.0, 0.0,
                -sin(ang), 0.0, cos(ang));
}
mat3 rotateXmat(float ang)
{
    return mat3(1.0, -0.0, 0.0,
                0.0, cos(ang), -sin(ang),
                0.0, sin(ang), cos(ang));
}

mat3 rotateZmat(float ang)
{
    return mat3(cos(ang), -sin(ang), 0.0,
                sin(ang), cos(ang), 0.0,
                0.0, 0.0, 1.0);
}

float map( vec3 p, vec3 origin, float s )
{    
    vec3 offset = vec3(sin(p.x*2. + iTime*2.),cos(p.z*10. + iTime*2.),1.0)*0.1;
	float d = length(p + offset - origin)- s;
	offset = vec3(sin(p.x*3. + iTime*2.),cos(p.z*2. + iTime*2.),1.0)*0.2;
    for(int i = 0; i < 3; i++)
    {

        float prism2 = length(p + offset*float(i) - origin)- s;
        d = max(d, -prism2);
    }
  	return d;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv = uv*2.0-1.0;
    uv.x *= iResolution.x/iResolution.y;

    mat3 rotation = //mat3(1.0);
      rotateXmat(iTime*0.4)*rotateYmat(iTime*0.5);
    vec3 direction = normalize(vec3(uv.x,uv.y, 1.0)*rotation);




    float t = 0.0;
	vec3 p;
    vec3 finalColor; 

    vec3 origin = vec3(0.,0.,-4.)*rotation;
    vec3 offset;
    vec3 sphereOrigin = vec3(0., 0., 0.0);				

    vec4 sound = texture (iChannel0,vec2(fragCoord.x/iResolution.x, 0.75));      
    float soundColor = texture (iChannel0,vec2(0.5, 0.75)).x;   

    vec3 color = vec3(.5 + sin(uv.x+iTime +soundColor*50.)*.4,.5 +cos(uv.y+iTime + soundColor*5.)*.5,.5);
    for (int k = 0; k <15; k++)
    {
        p = origin + t*direction;
        float d = map(p,sphereOrigin, 2.0);

        {
            vec3 directionalOffset = -normalize(p)*sound.x*normalize(vec3(uv, 1.0));
            vec3 position = p + directionalOffset;
            float radius = 0.1+float(k)*.5;        
            float lineThickness = 0.02 + float(k)*0.01;
            //position.y += position.y*abs(uv.x);
            float distanceFromCenter = length(position);
            float condition = step( distanceFromCenter, radius)
                - step(distanceFromCenter, radius - lineThickness);
            finalColor += color*condition;
        }

        t += d;
    }
    float fog = 1.0/(1.0+t*t*0.1);
    fragColor = vec4(finalColor+color*vec3(fog), fog);    

}

// Andi Smithers 
// wave effect using curves

#define C(x)   a*cos(x-t) 
#define S(x)   a*sin(x-t)
#define X0(x,xx)  x-C(xx)

float trochoid(float x, float t, float a) 
{
    return S(X0(x,X0(x,X0(x,X0(x,X0(x,x))))));
}

#define plot(Y) o += smoothstep(40.0/R.y, 0.0, abs(Y-uv.y))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // uniforms
    float borderSize = 0.05;
    float antiAliasSize = 0.03;
    vec4 borderColor = vec4(1.0,1.0,0.95, 1);
    vec4 centreColor = vec4(0.1,0.5,0.9,1);
    float t = iTime;

    vec2 shadowVec = vec2(-0.01, 0.05);
    float scaleBox = 1.2;

    // convert UV into normalized coord
    vec2 iuv = fragCoord.xy / iResolution.xy;
	vec2 uv = fragCoord.xy / iResolution.y;
	uv.y+=0.3;

    float wave = trochoid(uv.x*8.0, 0.0, sin(t))*0.05;
  //  float alpha2 = clamp(1.0-abs(mod(sin(t)+uv.x*1.5, 3.142)-1.57)*1.3, 0.0, 1.0);
    float alpha3 = smoothstep(0.0, 0.7, 1.0-abs(iuv.x*2.0-1.0));
    float alpha = smoothstep(10.0/iResolution.y, 0.0, abs(wave-uv.y+0.5));
    float alpha2 = uv.y>0.6 ? 1.0 : max(uv.y*5.0 -2.0, 0.0);
 //   float wave = abs(sin(iuv.x + rounding ) - iuv.y*32.0+16.0);
 //   wave = clamp(1.0-wave, 0.0, 1.0);

    // blend against a background texture - because shadeoy
    vec4 background = vec4(0.2,0.5,1.0,1.0);
    vec4 color = vec4(1.0, 1.0, 1.0, clamp(alpha*40.0, 0.0, 1.0)*alpha2*alpha3);

    // and set the final color 
    fragColor = (color*color.a) + background*(1.0-color.a);
}

// Simplfied port of Oil by Patricio Gonzalez Vivo
// http://www.kamend.com/2012/06/perlin-noise-and-glsl/

vec4 mod289 (vec4 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec4 permute (vec4 x) { return mod289(((x*34.0)+1.0)*x); }
vec4 taylorInvSqrt (vec4 r) { return 1.79284291400159 - 0.85373472095314 * r; }
vec2 fade (vec2 t) { return t*t*t*(t*(t*6.0-15.0)+10.0); }

// Classic 2D Perlin noise
float cnoise (vec2 P) {
    vec4 Pi = floor(P.xyxy) + vec4(0.0, 0.0, 1.0, 1.0);
    vec4 Pf = fract(P.xyxy) - vec4(0.0, 0.0, 1.0, 1.0);
    Pi = mod289(Pi); // To avoid truncation effects in permutation
    vec4 ix = Pi.xzxz;
    vec4 iy = Pi.yyww;
    vec4 fx = Pf.xzxz;
    vec4 fy = Pf.yyww;

    vec4 i = permute(permute(ix) + iy);

    vec4 gx = fract(i * (1.0 / 41.0)) * 2.0 - 1.0 ;
    vec4 gy = abs(gx) - 0.5 ;
    vec4 tx = floor(gx + 0.5);
    gx = gx - tx;

    vec2 g00 = vec2(gx.x,gy.x);
    vec2 g10 = vec2(gx.y,gy.y);
    vec2 g01 = vec2(gx.z,gy.z);
    vec2 g11 = vec2(gx.w,gy.w);

    vec4 norm = taylorInvSqrt(vec4(dot(g00, g00), dot(g01, g01), dot(g10, g10), dot(g11, g11)));
    g00 *= norm.x;  
    g01 *= norm.y;  
    g10 *= norm.z;  
    g11 *= norm.w;  

    float n00 = dot(g00, vec2(fx.x, fy.x));
    float n10 = dot(g10, vec2(fx.y, fy.y));
    float n01 = dot(g01, vec2(fx.z, fy.z));
    float n11 = dot(g11, vec2(fx.w, fy.w));

    vec2 fade_xy = fade(Pf.xy);
    vec2 n_x = mix(vec2(n00, n01), vec2(n10, n11), fade_xy.x);
    float n_xy = mix(n_x.x, n_x.y, fade_xy.y);
    return 2.3 * n_xy;
}


float noise(vec2 p){         
	return  0.4 * cnoise(p);    
}

float pattern( in vec2 uv, in float time){   

    //instead of just getting a noise field value on uv position and time,
    //we first warp the uv space by noise a couple of times
    //this gives more intersting 'curly' looking noise
    //this is known as domain warping: http://www.iquilezles.org/www/articles/warp/warp.htm

    //get a noise value q
    vec2 q = vec2(9.0, 0.);
    q.y = noise(uv + vec2(5.2*time,1.3*time)) ;

    //now warp uv space by q and get noise on that
    vec2 r = vec2(0.);
    r.x = noise(uv + 4.0 * q + vec2(1.7,9.2));

    //now warp uv space by r and get noise on that
    return noise(uv + 4.0*r);

}

const float SCALE = 2.0;
const float BRIGHTNESS = 3.5;
const float SPEED = 0.3;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 st = fragCoord.xy / iResolution.xy;
    vec2 uv = -1.0 + 2. * st;
    uv *= SCALE;
    float time = iTime*SPEED;
    vec3 color = vec3(pattern(uv,time)*BRIGHTNESS);
    fragColor = vec4(color,1.0);
}

#define RM_DIST 0.001
#define AA_DIST 0.025

#define SHOW_AA_FACTOR 0

float dsphere(vec3 p, vec3 c, float r)
{
    return length(p-c) - r;
}

float dplane(vec3 p, vec3 o, vec3 n, float h)
{
    vec3 op = p-(o+n*h);
    return dot(op,n);
}

float opU(float d1,float d2,out float idx)
{
    idx = d1 < d2 ? 0.0 : 1.0;
    return min(d1,d2);
}

float opB(in float d1, in float d2, in float k)
{
    float h = clamp( 0.5+0.5*(d2-d1)/k,0.0,1.0);
    return mix(d2,d1,h) - k*h*(1.0-h);
}

float map(vec3 p , out float idx)
{
    float d1 = dsphere(p,vec3(0.0),1.0);
    d1+=0.05*sin(p.x*10.0+iTime*2.0)*sin(p.y*10.0)*sin(p.z*10.0);

    float d2 = dsphere(p,vec3(2.15,0.0,0.0),0.5);
    d2 += 0.02*sin(p.x*10.0 + iTime*3.0);

    float d3 = dplane(p,vec3(0.0),normalize(vec3(0.0,1.0,0.0)),-1.5);

    float d4 = opB(d1,d2,1.3);

    return opU(d3,d4,idx);
}

vec3 norm( vec3 p )
{
    vec2 e = vec2(0.01,0.0);
    float idx =0.0;
	return normalize( vec3( map( p + e.xyy ,idx) - map( p - e.xyy,idx ),
                            map( p + e.yxy,idx ) - map( p - e.yxy,idx ),
                            map( p + e.yyx,idx ) - map( p - e.yyx,idx )
                            ));
}

void accumColor(out vec3 acc_c, in vec3 new_c, in float aa_factor)
{
    if(acc_c == vec3(1.0,2.0,3.0))
    {
        acc_c = new_c;
    }
    else
    {
#if SHOW_AA_FACTOR
        acc_c = vec3(aa_factor);
#else
        acc_c = mix(acc_c,new_c,aa_factor);
#endif
    }
}

float shadow(vec3 pi, vec3 l,float k)
{
    float t = 1.0;
    float tmax = 30.0;
    float res = 1.0;
    for(int i=0; i<256; ++i)
    {
        vec3 p = pi + t * l;
        float idx;
        float d = map(p,idx);

        res = min(res, k * d/t);
        if(d < 0.0001 || t > tmax) break;

        t+=d;
    }
    return clamp(res,0.0,1.0);
}

vec3 shadeP( in vec3 p,in vec3 rd, float idx)
{
    vec3 mat = idx < 0.5 ? vec3(0.6,0.2,0.1) : vec3(0.4,0.7,0.9);
    vec3 l = normalize(vec3(1.0,1.0,1.0));
    vec3 n = norm(p);
    float s = shadow(p,l,8.0);
    float rs = shadow(p,reflect(rd,n),12.0);

    vec3 col = vec3(0.1)*mat;
    col += clamp(dot(l,n),0.0,1.0)*mat*s*0.8;
    col += pow(clamp(dot(reflect(rd,n),l),0.0,1.0),150.0)*0.1;
    col += pow(1.0-clamp(dot(-rd,n),0.0,1.0),3.0)*vec3(0.6,0.7,1.0)*0.2;
    col += 0.1*rs*vec3(0.5,0.8,1.0);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    vec2 iMouseN = iMouse.xy / iResolution.xy;
    iMouseN = iMouseN*2.0 -1.0;
    iMouseN.x*=iResolution.x/iResolution.y;

    float xLimit = iMouse.z > 0.5 ? iMouseN.x : 0.0;
    float time = iTime * 2.0;;
    vec3 ro = vec3(9.0*sin(0.2*time),3.0+sin(0.3*time),9.0*cos(0.2*time));
    vec3 camt = vec3(0.0,0.0,0.0);

    float alpha = 0.2;
    vec3 f = normalize(vec3(camt-ro));
    vec3 rf = normalize(vec3(sin(alpha),cos(alpha),0.0));
    vec3 r = normalize(cross(f,rf));
    vec3 u = normalize(cross(r,f));

    vec3 rd = normalize(uv.x*r+uv.y*u+f*3.0);

    vec3 col = vec3(1.0,2.0,3.0);
    float aa_d = uv.x < xLimit ? AA_DIST : 0.0;

    float t = 0.0;
    float tmax = 15.0;
    float lastd = 0.0;
    float aa_factor = 1.0;
    float idx;
    bool pickColor = false;
    for(int i = 0; i<256; ++i)
    {
        vec3 p = ro+rd*t;

        float newd = map(p,idx);

        if(newd < lastd)
        {   
            pickColor = true;
        }
        else if(newd <= aa_d)
        {   
            if(pickColor)
            {
                pickColor = false;
                accumColor(col,shadeP(p,rd,idx),aa_factor);
                aa_factor = clamp(aa_factor - smoothstep(aa_d,0.0,lastd),0.0,1.0);
            }
        }
        lastd = newd;

        if( newd < RM_DIST || t >= tmax) break;

        t+=newd;
    }


    vec3 bckcol = vec3(0.0,0.0,0.0);

    if(t<tmax)
    {
        vec3 p = ro + rd * t;
        float parm = uv.x < xLimit ? smoothstep(0.985,1.0,t/tmax) : 0.0;
        vec3 scol = mix(shadeP(p,rd,idx),bckcol, parm );
        accumColor(col,scol,aa_factor);
    }
    else
    {
        accumColor(col,bckcol,aa_factor);
    }


    float w = 0.004;
    float l = step(xLimit+w,uv.x) + 1.0-step(xLimit-w,uv.x);
    col*=l;

    col = pow(col,vec3(1.0/2.2));
	fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //pattern center point
    vec2 center = vec2(iResolution/2.0);

    //postion of inside and outside rings to set size and thickness 
    float outRadius = 91.0;
    float inRadius = 90.0;

    //multiplier for rotation offset
    float spinMulti = 80.0;

    //number of rings in chain
	const int ringCount = 20;

    //multiplier for pattern speed
    float timeMulti = 0.50;

    //offsets for distorting x and y postions of pattern
    int xOffset = 5;
    int yOffset = 10;

    vec2 rings[ringCount];

    rings[0] = center;

    for(int x = 0;x<ringCount;x++){

        rings[x] = rings[x-1] + vec2( 
            sin(iTime * float(x + xOffset) * timeMulti) * spinMulti,
            cos(iTime * float(x + yOffset) * timeMulti) * spinMulti
        );

        if(distance(fragCoord,rings[x-1]) < outRadius && distance(fragCoord,rings[x-1]) > inRadius){

            if(distance(rings[x],fragCoord) > 50.0 ){
                fragColor = vec4(0,0,1,1);
            }
            else{
            	fragColor = vec4(1,0.5,0,1);
            }

    	}
    }
}

// My frist Shadertoy Shader


float MakeCircle(vec2 uv, vec2 pos, float rad, float edgeblur)
 {
     float dist = length(uv-pos); // lenght from center

     // using smoothstep to make circles and nice edges
     float c = smoothstep(rad,rad-edgeblur, dist); 

     return c;
 }

float mickey (vec2 uv, vec2 pos)
 {
     float mask = MakeCircle(uv, vec2(pos.x, pos.y), .15, 0.01);   // Face
     mask += MakeCircle(uv, vec2(.12 + pos.x, .15 + pos.y), .075, .01);  // Ears
     mask += MakeCircle(uv, vec2(-.12 + pos.x,.15 + pos.y), .075, .01);  // Ears

     float mouth = .0;
     mouth += MakeCircle(uv, vec2(pos.x, pos.y), .1, .01);
     mouth -= MakeCircle(uv, vec2(pos.x, 0.03 + pos.y), .1, .01);
     mouth -= MakeCircle(uv, vec2(-.095 + pos.x, -.005 + pos.y), .04, .01);
     mouth -= MakeCircle(uv, vec2(.095 + pos.x, -.005 + pos.y), .04, .01);
     mask -= mouth;

     float eyes = .0;
     eyes += MakeCircle(uv, vec2(-.08 + pos.x, .05 + pos.y), .04, .01);
     eyes += MakeCircle(uv, vec2(.08 + pos.x, .05 + pos.y), .04, .01);
     mask -= eyes;

     return mask;
 }

float mickeyGrid(vec2 uv, vec2 pos)
 {
     // I think this could be done with a XOR loop...
     float mask =0.0;
     mask += mickey(uv, vec2(pos.x, pos.y));
     mask += mickey(uv, vec2(pos.x, 0.5 + pos.y));
     mask += mickey(uv, vec2(pos.x, 1.0 + pos.y));
     mask += mickey(uv, vec2(pos.x, -0.5 + pos.y));
     mask += mickey(uv, vec2(pos.x, -1.0 + pos.y));
     mask += mickey(uv, vec2(pos.x + 0.5, pos.y));
     mask += mickey(uv, vec2(pos.x + 1.0, pos.y));
     mask += mickey(uv, vec2(pos.x + -0.5, pos.y));
     mask += mickey(uv, vec2(pos.x + -1.0, pos.y));
     mask += mickey(uv, vec2(pos.x + 0.5, pos.y + 0.5));
     mask += mickey(uv, vec2(pos.x + 1.0, pos.y + 0.5));
     mask += mickey(uv, vec2(pos.x + 0.5, pos.y + 1.0));
     mask += mickey(uv, vec2(pos.x + 1.0, pos.y + 1.0));
     mask += mickey(uv, vec2(pos.x + 0.5, pos.y + -0.5));
     mask += mickey(uv, vec2(pos.x + 0.5, pos.y + -1.0));
     mask += mickey(uv, vec2(pos.x + -0.5, pos.y + 0.5));
     mask += mickey(uv, vec2(pos.x + -1.0, pos.y + 0.5));
     mask += mickey(uv, vec2(pos.x + -1.0, pos.y + -0.5));
     mask += mickey(uv, vec2(pos.x + -0.5, pos.y + -1.0));
     mask += mickey(uv, vec2(pos.x + -0.5, pos.y + -0.5));
     mask += mickey(uv, vec2(pos.x + -1.0, pos.y + -1.0));

     return mask;
 }

float funkyColorCircles(vec2 uv, float rings, float ringThickness, float speed)
 {
     vec2 r = uv * rings; // ++ == more rings

     float v1 = sin(r.x + iTime) * 2.0;   // Color fade Grid x
     float v2 = cos(r.y + iTime);    // Color fade Grid y
     float v3 = sin(r.x + r.y + iTime);  // Color fade Grid xy mod

     // makes reducing circles (over time)
     float v4 = tan(sqrt(r.x * r.x + r.y * r.y) + iTime * speed) * ringThickness; 

     return v1 + v2 + v3 + v4; 
 }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
 {
  vec2 uv = fragCoord.xy / iResolution.xy; // Screen Coords

     uv -= 0.5; // Pos Middle of screen
     uv.x *= iResolution.x/iResolution.y; // fix X Stretch

    float rot = radians(iTime * 35.0); // Rotation Speed (using time)
     float move = sin(iTime) * 0.4; // Simple move back and forth on sin wave (using time)

     uv = mat2(cos(rot), -sin(rot), sin(rot), cos(rot)) * uv; // uv rotation

    // Background Color fade mix on sin wave (using time)
     vec3 color = mix(vec3(1., 1., 1.), vec3(0., 1., 1.), uv.y ) * sin(iTime);

    color += funkyColorCircles(uv, 12.0,  0.25, 4.0) * move; // Added move just because...

     color +=  vec3(1., 1., 1.) * mickeyGrid(uv, vec2(move,move)); // Add Mickey Color Mask

     fragColor = vec4(color, 1.0); // output to screen
 }
