// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

/*
   Epitrochoid facts:
  --------------------
	- (a) The circle of radius b rolls on the outside of the circle of radius a. The point P is at a
		  distance c from the center of the circle of radius b.

*/

#define res_            iResolution
#define time_           iTime
#define trace_len_      322
#define eps_            .2

#define crot(a)  mat2(cos(a),-sin(a),sin(a),cos(a))

vec2  domain(vec2 uv, float s);
vec2  parametric(float t);
float trace(vec2 p, float t);
float _union_chamfer(float a, float b, float r);

void mainImage(out vec4 o, vec2 f) {
	vec2 p = domain(f, 6.);
    vec2 np = f / res_.xy;

  	p *= crot(time_*.2);

    // t -> [0., 5.]
    float t = time_ * sign(sin(time_));

    // Epitrochoid traces
    float dtrace = smoothstep(0.19, 0.01, trace(p, t));
    // Axis
    float ax = min(
        smoothstep(0., 0.02, abs(p.x)),
        smoothstep(0., 0.02, abs(p.y))
    );
    // Origin
    float org = smoothstep(0.09, 0., length(p)-.01);

    // Palette 
    vec3 gray   = vec3(.2);
    vec3 white  = vec3(5.);

    // Shading
    vec3 c  = vec3(0.);
    c = mix(c, gray, fract(1.-ax));
    c = mix(c, white, org);

    c = mix(c, vec3(1.), dtrace);
	c = mix(c, vec3(1., 0., 0.), dtrace/length(p*2.2)*3.);
    c.b += dtrace*p.x;

    o = vec4(c, 1.);
}
float _union_chamfer(float a, float b, float r) {
	return min(min(a, b), (a - r + b)*sqrt(0.5));
}


vec2 parametric(float t) {
    t /= atan(t, 2.*t);
	float a = 2.;
    float b = 1.2;
    float c = 1.9;
    return vec2( (a+b)*cos(t) - c*cos((a/b + 1.) * t), (a+b)*sin(t) - c*sin((a/b + 1.) * t) );
}

float trace(vec2 p, float t) {
    float d = 1e10;
    float e = 0.;
    for(int i = 0; i < trace_len_; i++) {
    	d  = _union_chamfer(d, distance(p, parametric(t-e)), parametric(t).y * .15);
        e += eps_; 
    }
    return d;
}

vec2 domain(vec2 uv, float s) {
    return (2. * uv.xy - res_.xy) / res_.y * s;
}

float PI=3.14159265;

// Floor
vec2 objPlane(in vec3 p, in float offset, in int materialID)
{
  return vec2(p.y+offset,materialID);
}

vec2 opAdd(in vec2 obj0, in vec2 obj1)
{
    float r = min(obj0.x,obj1.x);
	return vec2(r, r == obj0.x ? obj0.y : obj1.y);
}

vec2 obSub(in vec2 obj0, in vec2 obj1)
{
    float r = max(obj0.x,-obj1.x);
	return vec2(r, r > obj0.x ? obj0.y : obj1.y);
}

vec2 objSphere(in vec3 p, in float r, in int materialID)
{
  float d = length(p) - r;
  return vec2(d,materialID);
}

vec2 objCube( vec3 p, vec3 b, in int materialID)
{
  float d = length(max(abs(p)-b,0.0));
  return vec2(d,materialID);
}

vec2 vScene(in vec3 p)
{
  return opAdd(
      		objPlane(p,1.0,0),
      		obSub(
                objCube(p,vec3(1.0,1.0,1.0),1),
                objSphere(p,1.25,2)
            )
      	 );
}

vec3 material0(in vec3 p) 
{
  if (fract(p.x*0.2)>0.2)
  {
    if (fract(p.z*0.2)>0.2)
      return vec3(0,0.1,0.2);
    else
      return vec3(1,1,1);
  }
  else
  {
    if (fract(p.z*.2)>.2)
      return vec3(1,1,1);
    else
      return vec3(0.3,0.2,0);
   }
}
vec3 material1(in vec3 p) { return vec3(1.7,1.2,2.0); }
vec3 material2(in vec3 p) { return vec3(1.0,0.0,0.0); }

vec3 SetupCamera(vec3 camPos, vec3 camLookAt, vec3 worldUp, vec2 uv)
{
  // Camera setup.
  vec3 camDir=normalize(camLookAt-camPos);
  vec3 camRight=normalize(cross(worldUp,camDir));
  vec3 camUp=cross(camDir,camRight);
  vec3 vcv=(camPos+camDir);
  vec3 scrCoord=vcv+uv.x*camRight*iResolution.x/iResolution.y+uv.y*camUp;
  return normalize(scrCoord-camPos);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  // Texture Coordinate
  vec2 uv = fragCoord.xy/iResolution.xy - 0.5;

  // Mouse Coordinate
  vec2 m = iMouse.xy/iResolution.xy*PI*vec2(2.0,0.5);

  // Camera
  const float cameraDistance = 4.0;
  const vec3 cameraUp = vec3(0,1,0);
  const vec3 cameraLookAt = vec3(0,0,0);
  const float cameraFar=100.0;
  const float cameraNear=0.001;
  vec3 cameraPos=vec3(cos(m.y)*cos(m.x),sin(m.y),cos(m.y)*sin(m.x))*cameraDistance; 
  vec3 scp=SetupCamera(cameraPos, cameraLookAt, cameraUp, uv);

  // Raymarching.
  const int rayCount = 200;
  vec2 rayDistance=vec2(0.001,0.0 /* Material ID */);
  vec3 c,p,N;

  float f=0.0;
  for(int i=0;i<rayCount;i++)
  {
    if ((abs(rayDistance.x) < cameraNear) || (f > cameraFar)) 
      break;

    f+=rayDistance.x;  
    p=cameraPos+scp*f; // rayPoint
    rayDistance = vScene(p);
  }

  if (f < cameraFar)
  {
    // y is used to manage materials.
    if (rayDistance.y<=0.0) 
      c = material0(p);
    else if (rayDistance.y<=1.0) 
      c = material1(p);
    else if (rayDistance.y<=2.0) 
      c = material2(p);

    // Lighting
	const vec3 e=vec3(0.00001,0,0);
    vec3 n = vec3(rayDistance.x-vScene(p-e.xyy).x,
                  rayDistance.x-vScene(p-e.yxy).x,
                  rayDistance.x-vScene(p-e.yyx).x);
    N = normalize(n);
	vec3 L = vec3(sin(iTime)*20.0,20.0,cos(iTime)*20.0);
    float b=dot(N,normalize(cameraPos-p+L));

    //simple phong lighting, LightPosition = CameraPosition
    fragColor=vec4((b*c+pow(b,16.0))*(1.0-f*.001),1.0);
  }
  else 
    fragColor=vec4(0,0,0,1); //background color
}

// Based on https://www.shadertoy.com/view/ltfSWn
// created by inigo quilez - iq
// (see http://iquilezles.org/www/articles/mandelbulb/mandelbulb.htm for details)
//
// and the blog entry "Distance Estimated 3D Fractals (V): The Mandelbulb & Different
// DE Approximations" by Mikael Hvidtfeldt Christensen
// (see http://blog.hvidtfeldts.net/index.php/2011/09/distance-estimated-3d-fractals-v-the-mandelbulb-different-de-approximations/)
//
//
// Code for 4x rotated-grid SSAA for antialiasing taken from
// https://www.shadertoy.com/view/XsXXWS by Morgan Mc Guire (Morgan3D)
//
// (take also a look at the excellent documented source code if you need a very good and very detailed
// explanation of how to render mandelbulbs!)
//
//
// For an explanation of how to archieve realistic looking outdoor lighting take a look at:
//
// http://iquilezles.org/www/articles/outdoorslighting/outdoorslighting.htm
//
// and for raymarching in general: "rendering worlds with two triangles"
//
// http://iquilezles.org/www/material/nvscene2008/nvscene2008.htm
//
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
// Thanks to iq for the great tutorials, for shadertoy and everything else


// set to true for antialiasing (enhances rendering quality but decreases speed by a factor of 4!)
#define ANTIALIASING false


// globals (are bad, i know, but make life sometimes easier :-) )
float dr;

//Fractal
const int   NUM_ITERATIONS  = 5;
const float SCALE           = 3.0; 
      vec3  OFFSET          = vec3(1.0, 1.0, 1.0);
	  mat3  m               = mat3(1,0,0,0,1,0,0,0,1);//Initialized in main function


// background color function, computes skycolor based on ray direction (used, if the ray hits nothing)
vec3 skyColor(vec3 rd)
{
    vec3 sunDir = vec3(0.0, -1.0, 0.0);
    vec3 sunColor = vec3(1.6, 1.8, 2.2);

    float sunAmount = max(dot(rd, sunDir), 0.0);
    float v = pow(1.0 - max(rd.y, 0.0), 6.0);
    vec3 sky = mix(vec3(0.1, 0.2, 0.3), vec3(0.32, 0.32, 0.32), v);

    sky += sunColor * sunAmount * sunAmount * 0.25 + sunColor * min(pow(sunAmount, 800.0) * 1.5, 0.3);


    return clamp(sky, vec3(0.0, 0.0, 0.0), vec3(1.0, 1.0, 1.0));
}


// signed distance field function (mandelbulb power 8)
float map(vec3 p)
{
	const int itMax = 14;

    vec3 w = p;

    float r = 0.0;
    float dist = 0.0;

    dr = 1.0;


    for(int it = 0; it < itMax; it++)
    {
        r = length(w);

        if (r > 2.0)
        {
            dist = 0.5 * log(r) * r / dr;

            break;
        }

        // extract polar coordinates
        float theta = acos(w.z / r);
        float phi =  atan(w.y, w.x);

        // scale and rotate point
        float rp2 = w.x * w.x + w.y * w.y + w.z * w.z;
        float rp4 = rp2 * rp2;

        float radPower = rp4 * rp4;
        float phiPower = phi * 8.0;
        float thetaPower = theta * 8.0;


        // convert back to cartesian coordinates
        float sinTheta = sin(thetaPower);
        float sinPhi   = sin(phiPower);
        float cosTheta = cos(thetaPower);
        float cosPhi   = cos(phiPower);

        w.x = radPower * sinTheta * cosPhi;
        w.y = radPower * sinTheta * sinPhi;
        w.z = radPower * cosTheta;

        // add c
        w += p;

        // compute dr
        float r2 = r * r;
        float r4 = r2 * r2;
        dr *= r4 * r2 * r * 8.0 + 1.0;
    }

    return dist;
}


// raymarching with distance field
float intersect( in vec3 ro, in vec3 rd)
{
    const int maxStepCount = 256;	// maximum no. of steps to march
    const float tMax = 100.0;		// maximum distance to march
    const float epsilon = 0.0003;	// determines precision (smaller values for more details
    								// and more noise due to holes from precision problems)

	float t = 0.0;

    // march!
    for(int i = 0; i < maxStepCount; i++)
    {
        // march forward along the ray, compute point p on ray for this step
        vec3 p = ro + rd * t;

        // get distance to nearest surface from distance field
        float distance = map(p);

        // if we're already marched too far (t > tMax),
        // or we're close enough (less than epsilon) to the surface (we have "hit" it)
        if ((t > tMax) || (distance < epsilon))
            break;

        // march further along the ray with the maximal distance possible,
        // which is distance to the closest surface from the actual position
        // taken from the signed distance field describing the mandelbulb
        // this ensures, that we haven't gone too far, so that we are not already inside the mandelbulb
        t += distance;
    }

    // if we're not exceeded the maximum marching distance, return ray parameter t for
    // computating the hit surface position
    if( t < tMax )
   		return t;
    else
        return -1.0;	// we missed it, signal this with -1.0
}


// cheap distance field soft shadow computation
// (see article "free penumbra shadows for raymarching distance fields":
// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm)
float softShadow(vec3 surfacePoint, vec3 lightDir)
{
    vec3 origin = surfacePoint + 0.001 * lightDir;

    vec3 ro = origin;
    vec3 rd = lightDir;


    const float k = 2.0; // 8.0
    float res = 1.0;
	float t = 0.0;

    for (int i = 0; i < 100; i++)
    {
        float h = map(ro + rd * t);

        if (h < 0.0001)
            return 0.0;

        if (t > 100.0)
            break;

        res = min(res, k * h / t);
        t += h;
    }

    return res;
}


// numerically approximate normal via discrete gradient computation (first order)
vec3 computeNormal(vec3 surfacePoint)
{
    const float epsilon =  0.001;

    vec3 epsX = vec3(epsilon, 0.0, 0.0);
    vec3 epsY = vec3(0.0, epsilon, 0.0);
    vec3 epsZ = vec3(0.0, 0.0, epsilon);

    float dx = map(surfacePoint + epsX) - map(surfacePoint - epsX);
    float dy = map(surfacePoint + epsY) - map(surfacePoint - epsY);
    float dz = map(surfacePoint + epsZ) - map(surfacePoint - epsZ);

    vec3 n = normalize(vec3(dx, dy, dz));

    return n;
}


// cheap distance field ambient occlusion computation
float computeAO(vec3 surfacePoint, vec3 normal)
{
    const float k = 5.0;
    const float delta = 0.005;

    float sum = 0.0;

    for(float i = 0.0; i < 10.0; i+=1.0)
    {
        float factor = 1.0 / pow(2.0, i);
        vec3 samplePoint = surfacePoint + normal * i * delta;

        sum += factor * (i * delta - map(samplePoint));
    }

    return 1.0 - k * sum;
}


// compute fragment color by raymarching the whole scene, given 2D coords
vec3 raymarch(vec2 point)
{
    // scene parameters
    const vec3 cameraLookAt = vec3(0.0, 0.1, 0.0);

    const vec3 light1 = vec3(  0.577, 0.577, -0.577 );
	const vec3 light2 = vec3( -0.707, 0.000,  0.707 );

	const float fov = 1.2;	// change for bigger field of view


    // compute aspect ratio 'corrected' pixel position
    float aspectRatio = iResolution.x / iResolution.y;
    vec2 xy = -1.0 + 2.0 * point.xy / iResolution.xy;

    vec2 s = xy * vec2(aspectRatio, 1.0);


    // slow down time ... (and start 30 s later)
    float time = iTime * 0.5 + 16.0;

	// some magic for camera movement ...  (time dependent camera radius and rotations)
	float r = 2.3 + 0.1  * cos(0.29 * time);
    vec3  ro = vec3( r * cos(0.33 * time), 0.8 * r * sin(0.37 * time), r * sin(0.31 * time) ); // ray origin
	float cr = 0.5 * cos(0.1 * time);
	vec3 cp = vec3(sin(cr), cos(cr), 0.0);

    // compute orthonormal camera basis
    vec3 cameraDir = normalize(cameraLookAt - ro);
	vec3 cameraRight = normalize(cross(cameraDir, cp));
	vec3 cameraUp = normalize(cross(cameraRight, cameraDir));

    // compute ray direction for perspective camera
	vec3 rd = normalize( s.x  * cameraUp + s.y * cameraRight + fov * cameraDir );


	vec3 col;
	vec3 tra;

    // intersect ray (ray origin (ro), ray direction (rd) with scene,
    // get ray parameter t for determining hit surface point
    float t = intersect(ro, rd);


    // nothing hit -> background color based on ray direction
    if( t < 0.0 )
        col = skyColor(rd);
	else
	{
        // hit at t, compute position, normal, reflection, usw. (see links above)
		vec3 pos = ro + t * rd;
        vec3 nor = computeNormal(pos);
        vec3 hal = normalize( light1 - rd);
        vec3 ref = reflect( rd, nor );

        // for using resoluts of orbit traps for color
		float trc = 0.1 * log(dr);

        // position based color for 'colorful' coloration :-)
        tra = vec3(trc, trc, 0) * abs(pos);


        col = vec3(0.7, 0.2, 0.2);
		col = mix( col, vec3(1.0, 0.5, 0.2), sqrt(tra.y) );
		col = mix( col, vec3(1.0, 1.0, 1.0), tra.x );

        // compute diffuse components from both lights
		float dif1 = clamp( dot( light1, nor ), 0.0, 1.0 );
		float dif2 = clamp( 0.5 + 0.5*dot( light2, nor ), 0.0, 1.0 );

        // add other lighting components (ambient occlusion, softshadows, specular)
        // see iq article (http://iquilezles.org/www/articles/outdoorslighting/outdoorslighting.htm)
        float occ =  0.05 * computeAO(pos, nor);
        float sha = softShadow(pos, light1);
        float fre = 0.04 + 0.96 * pow( clamp(1.0 - dot(-rd, nor), 0.0, 1.0), 5.0 );
        float spe = pow( clamp(dot(nor, hal),0.0, 1.0), 12.0 ) * dif1 * fre * 8.0;

        // some color magic
        // 'good artists copy, great artists steal' - this is stolen from:
        // https://www.shadertoy.com/view/ltfSWn
		vec3 lin  = 1.5 * vec3(0.15, 0.20, 0.23) * (0.7 + 0.3 * nor.y) * (0.2 + 0.8 * occ);
		     lin += 3.5 * vec3(1.00, 0.90, 0.60) * dif1 * sha;
		     lin += 4.1 * vec3(0.14, 0.14, 0.14) * dif2 * occ;
             lin += 2.0 * vec3(1.00, 1.00, 1.00) * spe * sha * occ;
             lin += 2.0 * vec3(0.20, 0.30, 0.40) * (0.02 + 0.98 * occ);
             lin += 2.0 * vec3(0.8, 0.9, 1.0) * smoothstep( 0.0, 1.0, ref.y ) * occ;

		col *= lin;
        col += spe * 1.0 * occ * sha;
	}


    // cheap gamma correction (for gamma = 1.0 / 2.0, close to 'official' lcd screen gamma = 1.0 / 2.2)
	col = sqrt( col );


	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color;


	if(!ANTIALIASING)
		// single sample for speed
		color = raymarch(gl_FragCoord.xy);
	else
    	// 4x rotated-grid SSAA for antialiasing
    	// (taken from https://www.shadertoy.com/view/XsXXWS by Morgan3D)
		color =   (	raymarch(gl_FragCoord.xy + vec2(-0.125, -0.375)) +
			 		raymarch(gl_FragCoord.xy + vec2(+0.375, -0.125)) +
					raymarch(gl_FragCoord.xy + vec2(+0.125, +0.375)) +
			 		raymarch(gl_FragCoord.xy + vec2(-0.375, +0.125))	) / 4.0;


	fragColor = vec4( color, 1.0 );
}

//
// CHANGE THIS MODE DEFINE TO TRY DIFFERENT OPTIONS
//  
// Sorry, only looks right at 1920x1080 non-full-screen.
//

// Major under-sampling in the center.
// Major over-sampling towards the ends.
// Warp not intended to look like VR, but have similar properties.
// All of these are linear filtering operations.

// 0 Red ......... NoAA Resolve Then Nearest Warp
// 1 Orange ...... NoAA Resolve Then Bilinear Warp
// 2 Gold ........ 4xSGSSAA Box Resolve Then Bilinear Warp
// 3 Yellow ...... 4xSGSSAA Gaussian Resolve With Warp + Noise

#define MODE 3

//================================================================================

#define F1 float
#define F2 vec2
#define F3 vec3
#define F4 vec4

// Kernel scale.
// This is a horrible hack, but maybe good enough for now.
F1 Kern(F2 p){
 // Convert to {-1 to 1} space.
 p=p/iResolution.xy;p=p*2.0-1.0;
 p=abs(p);
 p=p*p*0.5+p*0.5;
 return 0.5*p.x+0.5*p.y;}

// Warp (given integer coordinates).
// This is a proxy for a VR warp.
F2 Warp(F2 p){
 // Convert to {-1 to 1} space.
 p=p/iResolution.xy;p=p*2.0-1.0;
 // Transform.
 p=p*abs(p)*0.5+p*0.5;
 // Convert back to integer.
 p=p*0.5+0.5;p*=iResolution.xy;return p;}

// Unwarp.
F2 Unwarp(F2 p){
 // Convert to {-1 to 1} space.
 p=p/iResolution.xy;p=p*2.0-1.0;
 // Invert transform.
 F2 pp=p;
 p=abs(p);
 p=0.5*(sqrt(8.0*p+1.0)-1.0);
 if(pp.x<0.0)p.x=-p.x;
 if(pp.y<0.0)p.y=-p.y;
 // Convert back to integer.
 p=p*0.5+0.5;p*=iResolution.xy;return p;}

// Convert from linear to sRGB.
F1 Srgb(F1 c){return(c<0.0031308?c*12.92:1.055*pow(c,0.41666)-0.055);}

// Convert from sRGB to linear.
F1 Linear(F1 c){return(c<=0.04045)?c/12.92:pow((c+0.055)/1.055,2.4);}    

//
// Semi-Poor Quality Temporal Noise
//

// Base.
// Ripped ad modified from: https://www.shadertoy.com/view/4djSRW
F1 Noise(F2 p,F1 x){p+=x;
 F3 p3=fract(F3(p.xyx)*10.1031);
 p3+=dot(p3,p3.yzx+19.19);
 return fract((p3.x+p3.y)*p3.z)*2.0-1.0;}

// Step 1 in generation of the dither source texture.
F1 Noise1(F2 uv,F1 n){
 F1 a=1.0,b=2.0,c=-12.0,t=1.0;   
 return (1.0/max(a*4.0+b*4.0,-c))*(
  Noise(uv+F2(-1.0,-1.0)*t,n)*a+
  Noise(uv+F2( 0.0,-1.0)*t,n)*b+
  Noise(uv+F2( 1.0,-1.0)*t,n)*a+
  Noise(uv+F2(-1.0, 0.0)*t,n)*b+
  Noise(uv+F2( 0.0, 0.0)*t,n)*c+
  Noise(uv+F2( 1.0, 0.0)*t,n)*b+
  Noise(uv+F2(-1.0, 1.0)*t,n)*a+
  Noise(uv+F2( 0.0, 1.0)*t,n)*b+
  Noise(uv+F2( 1.0, 1.0)*t,n)*a+
 0.0);}

// Step 2 in generation of the dither source texture.
F1 Noise2(F2 uv,F1 n){
 F1 a=1.0,b=2.0,c=-2.0,t=1.0;   
 return (1.0/(a*4.0+b*4.0))*(
  Noise1(uv+F2(-1.0,-1.0)*t,n)*a+
  Noise1(uv+F2( 0.0,-1.0)*t,n)*b+
  Noise1(uv+F2( 1.0,-1.0)*t,n)*a+
  Noise1(uv+F2(-1.0, 0.0)*t,n)*b+
  Noise1(uv+F2( 0.0, 0.0)*t,n)*c+
  Noise1(uv+F2( 1.0, 0.0)*t,n)*b+
  Noise1(uv+F2(-1.0, 1.0)*t,n)*a+
  Noise1(uv+F2( 0.0, 1.0)*t,n)*b+
  Noise1(uv+F2( 1.0, 1.0)*t,n)*a+
 0.0);}

// Compute temporal dither from integer pixel position uv.
F1 Noise3(F2 uv){return Noise2(uv,fract(iTime));}    

// Energy preserving dither, for {int pixel pos,color,amount}.
F1 Noise4(F2 uv,F1 c,F1 a){
 // Grain value {-1 to 1}.
 F1 g=Noise3(uv)*2.0;
 // Step size for black in non-linear space.
 F1 rcpStep=1.0/(256.0-1.0);
 // Estimate amount negative which still quantizes to zero.
 F1 black=0.5*Linear(rcpStep);
 // Estimate amount above 1.0 which still quantizes to 1.0.
 F1 white=2.0-Linear(1.0-rcpStep);
 // Add grain.
 return clamp(c+g*min(c+black,min(white-c,a)),0.0,1.0);}

//
// Pattern
//

// Dummy shader given pixel position.
F1 Shade(F2 p){
 p.xy+=sin(iTime)*2.0;
 F2 pp=F2(p.x+p.y/16.0,p.y+p.x/16.0);
 pp*=pp;
 F1 x=sin(pp.x/800.0)>0.0?0.5:0.0;
 F1 y=sin(pp.y/1000.0)>0.0?0.5:0.0;
 return x+y;}

// 4xMSAA pattern for quad given integer coordinates.
//
//  . x . . | < pixel
//  . . . x |
//  x . . .
//  . . x .
//
//  01
//  23
//
F2 Quad4(F2 pp){
 int q=(int(pp.x)&1)+((int(pp.y)&1)<<1);
 if(q==0)return pp+F2( 0.25,-0.25);
 if(q==1)return pp+F2( 0.25, 0.25);
 if(q==2)return pp+F2(-0.25,-0.25);
         return pp+F2(-0.25, 0.25);}

// Rotate {0.0,r} by a {-1.0 to 1.0}. 
F2 Rot(F1 r,F1 a){return F2(r*cos(a*3.14159),r*sin(a*3.14159));}

// Rotate {pp} by a {0.0 to 1.0}. 
F2 Rot2(F2 pp,F1 a){
 a*=3.14159*2.0;
 F1 cosA=cos(a);
 F1 sinA=sin(a);
 return F2(dot(pp,F2(cosA,sinA)),dot(pp,F2(-sinA,cosA)));}

//
// RESOLVES
//

// Single center sample, nearest warp.
F1 ResolveNoAA(F2 pp){return Shade(floor(Warp(pp)));}

// Single center sample, bilinear warp.
F1 ResolveNoAABi(F2 pp){
 pp=Warp(pp);
 F2 ii=floor(pp);
 F1 a=Shade(ii);
 F1 b=Shade(ii+F2(1.0,0.0));
 F1 c=Shade(ii+F2(0.0,1.0));
 F1 d=Shade(ii+F2(1.0,1.0));
 F2 ff=pp-ii;
 a=mix(a,b,ff.x);
 c=mix(c,d,ff.x);
 a=mix(a,c,ff.y);
 return a;}

F1 Shade4(F2 pp){return
 Shade(pp+F2( 0.125,-0.375))*0.25+
 Shade(pp+F2(-0.375,-0.125))*0.25+
 Shade(pp+F2( 0.375, 0.125))*0.25+
 Shade(pp+F2(-0.125, 0.375))*0.25;}

// 4xSGSSAA box resolve, bilinear warp.
F1 Resolve4xBi(F2 pp){
 pp=Warp(pp);
 F2 ii=floor(pp);
 F1 a=Shade4(ii);
 F1 b=Shade4(ii+F2(1.0,0.0));
 F1 c=Shade4(ii+F2(0.0,1.0));
 F1 d=Shade4(ii+F2(1.0,1.0));
 F2 ff=pp-ii;
 a=mix(a,b,ff.x);
 c=mix(c,d,ff.x);
 a=mix(a,c,ff.y);
 return a;}

// Resolve With Warp

void M44(inout F1 sumC,inout F1 sumW,F2 pp,F2 mm){
 F1 kern=Kern(mm);
 kern*=3.0;
 F2 jj=Quad4(pp);
 F1 c=Shade(jj);
 jj=Unwarp(jj);
 F2 vv=mm-jj;
 F1 w=exp2(-(0.5+kern)*dot(vv,vv));    
 sumC+=c*w;sumW+=w;}

void M4(inout F1 sumC,inout F1 sumW,F2 pp,F2 mm){
 M44(sumC,sumW,pp+F2( 0.125,-0.375),mm);
 M44(sumC,sumW,pp+F2(-0.375,-0.125),mm);
 M44(sumC,sumW,pp+F2( 0.375, 0.125),mm);
 M44(sumC,sumW,pp+F2(-0.125, 0.375),mm);}

// 4xSGSSAA Gaussian Resolve With Warp
// Over-kill on the number of filter taps taken for the resolve,
// this is not required in practice,
// just don't have time for an optimal implementation.
F1 ResolveM4(F2 pp){
 F2 ww=floor(Warp(pp));
 F1 sumC=0.0;
 F1 sumW=0.0;
 // 
 M4(sumC,sumW,ww+F2(-1.0,-2.0),pp);
 M4(sumC,sumW,ww+F2( 0.0,-2.0),pp);
 M4(sumC,sumW,ww+F2( 1.0,-2.0),pp);
 //
 M4(sumC,sumW,ww+F2(-2.0,-1.0),pp);
 M4(sumC,sumW,ww+F2(-1.0,-1.0),pp);
 M4(sumC,sumW,ww+F2( 0.0,-1.0),pp);
 M4(sumC,sumW,ww+F2( 1.0,-1.0),pp);
 M4(sumC,sumW,ww+F2( 2.0,-1.0),pp);
 //
 M4(sumC,sumW,ww+F2(-2.0, 0.0),pp);
 M4(sumC,sumW,ww+F2(-1.0, 0.0),pp);
 M4(sumC,sumW,ww+F2( 0.0, 0.0),pp);
 M4(sumC,sumW,ww+F2( 1.0, 0.0),pp);
 M4(sumC,sumW,ww+F2( 2.0, 0.0),pp);
 //   
 M4(sumC,sumW,ww+F2(-2.0, 1.0),pp);
 M4(sumC,sumW,ww+F2(-1.0, 1.0),pp);
 M4(sumC,sumW,ww+F2( 0.0, 1.0),pp);
 M4(sumC,sumW,ww+F2( 1.0, 1.0),pp);
 M4(sumC,sumW,ww+F2( 2.0, 1.0),pp);
 //
 M4(sumC,sumW,ww+F2(-1.0, 2.0),pp);
 M4(sumC,sumW,ww+F2( 0.0, 2.0),pp);
 M4(sumC,sumW,ww+F2( 1.0, 2.0),pp);
 // 
 return sumC/sumW;}





