void mainImage(out F4 fragColor,in F2 fragCoord){
 F2 pp=fragCoord;
 F1 cc=0.0;
 int ss=MODE;
 // Red
 if(ss==0){
  if(pp.y<8.0){fragColor.xyz=F3(1.0,0.0,0.0);return;}
  cc=ResolveNoAA(pp);}
 // Orange
 else if(ss==1){
  if(pp.y<8.0){fragColor.xyz=F3(1.0,0.5,0.0);return;}
  cc=ResolveNoAABi(pp);}
 // Gold
 else if(ss==2){
  if(pp.y<8.0){fragColor.xyz=F3(1.0,0.75,0.0);return;}
  cc=Resolve4xBi(pp);}
 // Yellow
 else if(ss==3){
  if(pp.y<8.0){fragColor.xyz=F3(1.0,1.0,0.0);return;}
  cc=ResolveM4(pp);
  cc=Noise4(pp,cc,1.0/16.0);}
 // Output   
 fragColor.x=Srgb(cc);
 fragColor.yxz=fragColor.xxx;}

vec3 hash (vec3 p)
{
    p = vec3(dot(p, vec3(189.0, 75.0, 121.0)), dot(p, vec3(122.0, 220.0, 62.0)), dot(p, vec3(34.0, 96.0, 162.0)));
    return fract(sin(p) * 4328395.432885) * 2.0 - 1.0;
}

float perlin3 (vec3 p)
{
    vec3 lbb = floor(p);
    vec3 rtf = ceil(p);

    vec3 ltb = vec3(lbb.x, rtf.y, lbb.z);
    vec3 rbb = vec3(rtf.x, lbb.y, lbb.z);
    vec3 rtb = vec3(rtf.x, rtf.y, lbb.z);
    vec3 rbf = vec3(rtf.x, lbb.y, rtf.z);
    vec3 lbf = vec3(lbb.x, lbb.y, rtf.z);
    vec3 ltf = vec3(lbb.x, rtf.y, rtf.z);

    vec3 glbb = hash(lbb);
    vec3 gltb = hash(ltb);
    vec3 grbb = hash(rbb);
    vec3 grtb = hash(rtb);
    vec3 grbf = hash(rbf);
    vec3 grtf = hash(rtf);
    vec3 glbf = hash(lbf);
    vec3 gltf = hash(ltf);

    vec3 dlbb = p - lbb;
    vec3 dltb = p - ltb;
    vec3 drbb = p - rbb;
    vec3 drtb = p - rtb;
    vec3 drbf = p - rbf;
    vec3 drtf = p - rtf;
    vec3 dlbf = p - lbf;
    vec3 dltf = p - ltf;

    float d1 = dot(dlbb, glbb);
    float d2 = dot(dltb, gltb);
    float d3 = dot(drbb, grbb);
    float d4 = dot(drtb, grtb);
    float d5 = dot(drbf, grbf);
    float d6 = dot(drtf, grtf);
    float d7 = dot(dlbf, glbf);
    float d8 = dot(dltf, gltf);

    p = mod(p, 1.0);
    p = p * p * (3.0 - 2.0 * p);

    float x1 = mix(d1, d3, p.x);
    float x2 = mix(d2, d4, p.x);
    float x3 = mix(d7, d5, p.x);
    float x4 = mix(d8, d6, p.x);

    float y1 = mix(x1, x2, p.y);
    float y2 = mix(x3, x4, p.y);

    return mix(y1, y2, p.z);
}

vec2 rotate (vec2 p, float r)
{
    r *= 3.1415926 / 180.0;
    float x = p.x * cos(r) - p.y * sin(r);
    float y = p.x * sin(r) + p.y * cos(r);
    return vec2(x, y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;

    vec2 ws = uv;
    ws.x *= iResolution.x / iResolution.y;
    ws *= 8.0;

    ws = rotate(ws, 45.0 * cos(iTime) * abs(uv.x) * 1.0 + iTime * 25.0);

    float r = perlin3(vec3(ws, iTime));

    ws = rotate(ws, r * 180.0);
    ws = mod(ws, 2.0);
    float c = dot(ws, vec2(1.0, 1.0)) * 0.5 - 0.5 * length(abs(uv * 2.0 - 1.0));
	fragColor = vec4(c, c, c, 1.0);
}

// author: Juan Francisco
// title: Clouds

#define NUM_OCTAVES 10

float random (in vec2 _st) { 
    return fract(sin(dot(_st.xy,
                         vec2(12.9898,78.233))) * 
        43758.5453123);
}

float noise (in vec2 _st) {
    vec2 i = floor(_st);
    vec2 f = fract(_st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) + 
            (c - a)* u.y * (1.0 - u.x) + 
            (d - b) * u.x * u.y;
}

float fbm ( in vec2 _st) {
    float v = 0.0;
    float a = 0.5;
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2( cos(0.5), sin(0.5), -sin(0.5), cos(0.50));
    //mat2 rot = mat2( 0, 3. * sin(0.5), 0, cos(0.50));

    for (int i = 0; i < NUM_OCTAVES; ++i) {
        v += a * noise(_st);
        _st = rot * _st * 2.0 + shift;
        a *= 0.5;
    }

    return v;
}

vec3 grayscale(in vec3 _rgb) {
    vec3 base = vec3(0.2989, 0.587, 0.114);
    float lum = base.r * _rgb.r + base.g * _rgb.g + base.b * _rgb.b;
    return vec3(lum);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {

    vec2 st = fragCoord.xy/ iResolution.xy * 5. + iTime / 25.;
    vec3 color = vec3(0);

    vec2 q = vec2(0.);
    q.x = fbm( st + 0.00*iTime);
    q.y = fbm( st + vec2(1.0));

    vec2 r = vec2(0.);
    r.x = fbm( st + 1.0*q + vec2(1.7,9.2)+ 0.15*iTime );
    r.y = fbm( st + 1.0*q + vec2(8.3,2.8)+ 0.126*iTime);

    float f = fbm( st + fbm(st + fbm(st)));

    color = mix(vec3(0.198,0.629,0.667),
                vec3(0.666667,0.666667,0.498039),
                clamp((f*f)*4.0,0.0,1.0));

    color = mix(color,
                vec3(0,0,0.164706),
                clamp(length(q),0.0,1.0));

    color = mix(color,
                vec3(0.666667,1,1),
                clamp(length(r.x),0.0,1.0));

    float x = .5 + fbm(st);
    float nt = noise(vec2(iTime * .013, iTime * .013));
    float v1 = pow(length(r), 2.5 + 1.5 * (1. - nt));
    float v2 = 1.;
    float alpha = mix(v2, v1, nt);

    vec3 colorv = 1. * color + f*f*f*color*color;
    vec3 tint = vec3(0.682,0.933,1.000);

    // tint image
    vec3 c1 = vec3(0.051,0.739,1.000);
    vec3 c2 = vec3(1.000,0.080,0.089);
    vec3 c3 = vec3(1.000,0.985,0.067);

    vec3 total = vec3(.0);
    total = mix(c1, c2, noise(st));
    total = mix(total, c3, noise(st * .75));

    fragColor = vec4(total * grayscale(colorv),alpha);
}

