mat3 RotZ(float a){return mat3(cos(a),-sin(a),0.,sin(a),cos(a),0.,0.,0.,1.);}	// get rotation matrix near z

vec2 df(vec3 p)
{
    p.xy -= sin(p.z*0.02)*5.;
   	float a = 0.72 * atan(p.x,p.y) * cos(p.z*0.025) * 2.;
    p.xy *= mat2(cos(a),-sin(a),sin(a),cos(a));
	p = mod(p,6.)-3.;
    float cyl = min(max(abs(p.x),abs(p.y)),length(p.yz));
	return vec2(cyl-0.4, 0);
}

vec3 nor( vec3 p, float prec )
{
    vec2 e = vec2( prec, 0. );
    vec3 n = vec3(
		df(p+e.xyy).x - df(p-e.xyy).x,
		df(p+e.yxy).x - df(p-e.yxy).x,
		df(p+e.yyx).x - df(p-e.yyx).x );
    return normalize(n);
}

// from Dave Hoskins // https://www.shadertoy.com/view/Xsf3zX
vec3 GetSky(in vec3 rd, in vec3 sunDir, in vec3 sunCol)
{
	float sunAmount = max( dot( rd, sunDir), 0.0 );
	float v = pow(1.0-max(rd.y,0.0),6.);
	vec3  sky = mix(vec3(.1, .2, .3), vec3(.32, .32, .32), v);
	sky = sky + sunCol * sunAmount * sunAmount * .25;
	sky = sky + sunCol * min(pow(sunAmount, 800.0)*1.5, .3);
	return clamp(sky, 0.0, 1.0);
}

float SubDensity(vec3 p, float ms) 
{
	return df(p - nor(p,0.0001) * ms).x/ms;
}

vec2 shade(vec3 ro, vec3 rd, float d, vec3 lp, vec3 ldo, float li)
{
	vec3 p = ro + rd * d;
	vec3 n = nor(p, 0.1);
	vec3 ldp = normalize(lp-n*1.5-p);
	vec3 refl = reflect(rd,n);
	float amb = 0.6;
	float diff = clamp( dot( n, ldp ), 0.0, 1.0);
	float fre = pow( clamp( 1. + dot(n,rd),0.0,1.0), 4.);
	float spe = pow(clamp( dot( refl, ldo ), 0.0, 1.0 ), 16.);
	float sss = 1. - SubDensity(p, 0.1);
	return vec2(
        (diff + fre + spe) * amb * li,
        (diff + fre + sss) * amb * li + spe
    );
}

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;
	float t = iTime * 35.;

    vec3 ld = vec3(0.,1., .5);

	vec3 ro = vec3(0,0,t);
    ro.xy += sin(t*0.02)*5.;
	vec3 cu = vec3(0,1,0);
	vec3 tg = ro + vec3(0,0,.1);

	float fov = .5;
	vec3 z = normalize(tg - ro);
	vec3 x = normalize(cross(cu, z));
	vec3 y = normalize(cross(z, x));
	vec3 rd = normalize(z + fov * (uv.x * x + uv.y * y));

	float s = 1., d = 1.;
	float dm = 100.;

	for (float i=0.; i<250.; i++)
	{
		if (log(d/s/1e3)>0. || d>dm) break;
		d += (s = df(ro + rd * d).x) * .4;
	}

    fragColor.rgb = GetSky(rd, ld, vec3(1.5));

	if (d<dm)
	{
		vec2 sh = shade(ro, rd, d, ro, ld, 1.);
		fragColor.rgb = mix( 
            vec3(.5,1,.8) * sh.x * 0.1 + vec3(0.8,0.5,0.2) * sh.y * 1.2, 
            fragColor.rgb, 
            1.0 - exp( -0.001*d*d ) ); 
	}
}

#define CUT .25
#define STEP 50.
#define SPEED 8.
#define PI2 6.28318

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ROT = iResolution.y / iMouse.y;
    float FIRST = iMouse.x - iResolution.y;

    float n = abs(FIRST);

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv + uv - 1.;
    uv *= iResolution.xy / iResolution.yy;
    vec3 color = vec3(CUT);
    float a = 0.;
    for (float q = 0.; q < n; q++) {
        float angle = q/n*PI2 + iTime/SPEED;
        vec2 center = vec2(sin(angle)/1., cos(angle)/ROT);
        vec2 d = center-uv;
        if (d.x*d.x+d.y*d.y < 0.5) {
        	a = STEP;
            color += 1./a;
        }
    }
    if(color.x > 1.) color = 1.-(color-1.);
    if(color.x < 0.) color = -1.*color;
    if(FIRST < 0.) color += (.5*float(1&int(color*a))-.5);
	fragColor = vec4(color,1.0);
}

float sdfPlane(in vec3 pos, in vec3 n) {
    return dot(pos, n);
}

float sdfSphere(in vec3 pos, in float radius) {
	return length(pos) - radius;   
}

float sdfUnion(in float a, in float b) {
	return min(a, b);   
}

float samp(in vec3 pos) {
 	vec3 spos = vec3(mod(pos.x, .7f) - 0.35f, pos.y, mod(pos.z, 0.7f) - 0.35f);
    float sphere = sdfSphere(spos, 0.5f);
    return sphere;
}



vec3 march(in vec3 origin, in vec3 dir, in float maxlen) {
	float dist = 0.0f;
    vec3 pos = origin;
    vec3 d = dir;

    while (dist < maxlen) {
    	float t = samp(pos);
        if (t < 0.001f) {
        	float fx = samp(vec3(pos.x + 0.0001f, pos.y, pos.z)) - samp(vec3(pos.x - 0.0001f, pos.y, pos.z));
			float fy = samp(vec3(pos.x, pos.y + 0.0001f, pos.z)) - samp(vec3(pos.x, pos.y - 0.0001f, pos.z));
			float fz = samp(vec3(pos.x, pos.y, pos.z + 0.0001f)) - samp(vec3(pos.x, pos.y, pos.z - 0.0001f));
			vec3 normal = normalize(vec3(fx, fy, fz));
            if (dot(-d, normal) < 0.0f) normal = -normal;
            return vec3(max(normal.y, 0.4f));
        }

        d = normalize(d + vec3(0, -0.0025f, 0));

        dist += 0.01f;
        pos += 0.01f * d;
    }

    return vec3(0.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float time = iTime;

    vec3 campos = vec3(-1.0f + iTime, 1.5f, -4.0f + iTime);
    vec3 dir = normalize(vec3(1.5f, 1.0f, 1.0f));
    vec3 side = normalize(cross(dir, vec3(0, 1, 0)));
    vec3 up = normalize(cross(side, dir));
    float fov = 128.0f / 180.0f * 3.141592;
   	float ifov = 1.0f / tan(fov / 2.0f);
    vec2 ndc = vec2(uv.x * 2.0f - 1.0f, (uv.y * 2.0f - 1.0));
    ndc.y *= iResolution.y / iResolution.x;

    vec3 rdir = normalize(side * ndc.x + up * ndc.y + dir * ifov);

    vec3 c = march(campos, rdir, 50.0f);

	fragColor = vec4(c,1.0);
}

// codapalette
vec3 palette(float i) {
	if(i<4.0) {
		if(i<2.0) {
			if(i<1.0) return vec3(0.0,0.0,0.0);
			else return vec3(1.0,3.0,31.0);
		}
		else {
			if(i<3.0) return vec3(1.0,3.0,53.0);
			else return vec3(28.0,2.0,78.0);
		}
	}
	else if(i<8.0) {
		if(i<6.0) {
			if(i<5.0) return vec3(80.0,2.0,110.0);
			else return vec3(143.0,3.0,133.0);
		}
		else {
			if(i<7.0) return vec3(181.0,3.0,103.0);
			else return vec3(229.0,3.0,46.0);
		}
	}
	else {
		if(i<10.0) {
			if(i<9.0) return vec3(252.0,73.0,31.0);
			else return vec3(253.0,173.0,81.0);
		}
		else if(i<12.0) {
			if(i<11.0) return vec3(254.0,244.0,139.0);
			else return vec3(239.0,254.0,203.0);
		}
		else {
			return vec3(242.0,255.0,236.0);
		}
	}
}

vec4 colour(float c) {
	c*=12.0;
	vec3 col1=palette(c)/256.0;
	vec3 col2=palette(c+1.0)/256.0;
	return vec4(mix(col1,col2,c-floor(c)),1.0);
}

// twitter simulator 2017
float distfunc(vec3 pos) {
    pos.y += sin(pos.z * 0.1) * 4.0;
    float sky = pow(abs(pos.x) * -pos.y, 0.5);
    if(pos.y > 0.0) {
        return(0.0);
    }
	return((cos(pos.x) + sin(pos.z) + pos.y - 3.0) + sky);
}

// iteration shaded ray marcher with some bonus stuff
void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    float time=iTime;
	vec2 coords=(2.0*fragCoord.xy-iResolution.xy)/max(iResolution.x,iResolution.y);

	vec3 ray_dir=normalize(vec3(coords,1.0+0.0*sqrt(coords.x*coords.x+coords.y*coords.y)));
	vec3 ray_pos=vec3(0, sin(time) * 4.0 - 4.0, -time * 10.0);

	float a=cos(time * 0.1)*10.0;
	ray_dir=ray_dir*mat3(
		cos(a),0.0,sin(a),
		0.0,1.0,0.0,
		-sin(a),0.0,cos(a)
	);

	float i=192.0;
	for(int j=0;j<192;j++)
	{
		float dist=distfunc(ray_pos);
		ray_pos+=dist*ray_dir*0.5;

		if(abs(dist)<0.001) { i=float(j); break; }
	}

    float c=i/192.0;
    c = c < 0.1 ? 0.1 : c;
    float badving = pow(clamp(1.0 - length(coords) + 0.7, 0.0, 1.0), 0.4);
    float badlines =  mod(fragCoord.y, 2.0) < 1.0 ? 0.8 : 1.0;
    vec4 col = colour(1.0 - c) * badving * badlines;
    float interpval = 1.0 - clamp(length(col) - 0.2, 0.0, 1.0);
    fragColor = mix(col, colour(texture(iChannel0, ray_dir.xy).x), interpval);
}

#define t iTime
#define ic0 iChannel0

const float EPS = 0.001;
const float MAX_ITER = 40.0;
const float MAX_T = 100.0;

vec3 simple_camera(vec2 uv) {
    vec3 forward = vec3(0.0, 0.0, -1.0);
    vec3 up = vec3(0.0, 1.0, 0.0);
    vec3 side = vec3(1.0, 0.0, 0.0);
    return normalize(forward + uv.x * side + uv.y * up);
}

float opU( float d1, float d2 ) {
  return min(d1, d2);
}

// Smooth minimum : http://iquilezles.org/www/articles/smin/smin.htm
float smin( float a, float b, float k ) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}


float sdSphere(vec3 p, float s) {
  return length(p)-s;
}

float sdPlane(vec3 p, vec4 n) {
    return dot(p, n.xyz) + n.w;
}

/*
	Return the closest distance to any surface from point p.
 */
float map(vec3 p) {
    //float s1 = sdSphere(p - vec3(0.0, 0.0 + sin(iTime * 2.0), -3.0), 1.0);
    float s1 = sdSphere(p - vec3(0.0, -0.5 + texture(ic0, vec2(.1, 0.)).x * 2. + sin(iTime * 2.0)/ 5., -3.0), 0.7 + texture(ic0, vec2(.4, 0.)).x);
    float s2 = sdPlane(p - vec3(0.0, -1.0, 0.0), vec4(0.0, 1.0, 0.0, 1.0));
    return smin(s1, s2, 4.0);
}


float intersect(vec3 origin, vec3 rayDir) 
{
    float t = 0.0;
    float dt = 0.1;

    for (float i = 0.0; i < MAX_ITER; i += 1.0) {
        t += dt;

        dt = map(t * rayDir + origin);
        if (abs(t) < EPS) {
            break;
        }

        if (t > MAX_T) {
            t = 0.0;
            break;
        }

    }
	return t;
}



vec3 plasma(vec2 uv) {
    float s1 = .5 + .5 * min(texture(ic0, vec2(.1, 0.) ).x * 1.5, 1.);
    float s2 = .5 + .5 * min(texture(ic0, vec2(.4, 0.) ).x * 1.5, 1.);
    float s3 = .5 + .5 * min(texture(ic0, vec2(.6, 0.) ).x * 1.5, 1.);
    float s4 = .5 + .5 * min(texture(ic0, vec2(.9, 0.) ).x * 1.5, 1.);

    vec2 p1 = vec2(0., 0.);
    vec2 p2 = vec2(0., 1.);
    vec2 p3 = vec2(1., 1.0);
    vec2 p4 = vec2(1., 0.);

    float c1 = .5 + .5 * sin(distance(uv, p1) * 10. * s1 + t + s1 / 4.);
    float c2 = .5 + .5 * sin(distance(uv, p2) * 10. * s2 + t + s2 / 4.);
    float c3 = .5 + .5 * sin(distance(uv, p3) * 10. * s3 + t + s3 / 4.);
    float c4 = .5 + .5 * sin(distance(uv, p4) * 25. * s4 + t * 3. + s4 / 4.);

	return vec3(c1 + c4 * .33, c2 + c4 * .33, c3 + c4 * .33);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 cameraOrigin = vec3(0.0);
    vec3 rayDir = simple_camera(uv);

    vec3 color = vec3((uv.y - 0.5) * (1.5 + sin(t * .2) * .5), 0.0, uv.y);

    float t = intersect(cameraOrigin, rayDir);

    if (t > 0.0) {
        // calculate intersection point
        vec3 hit = cameraOrigin + t * rayDir;        

        // get the color of the pixel
        //color = texture(iChannel0, hit.xy).xyz;
        color = plasma(hit.xy / 7.);

        // fade with distance
        color *= vec3(6.0 / (t*t));
    }


    fragColor = vec4(color, 1.0);
}

