// blackbody by aiekick : https://www.shadertoy.com/view/lttXDn

// -------------blackbody----------------- //

// return color from temperature 
//http://www.physics.sfasu.edu/astro/color/blackbody.html
//http://www.vendian.org/mncharity/dir3/blackbody/
//http://www.vendian.org/mncharity/dir3/blackbody/UnstableURLs/bbr_color.html

vec3 blackbody(float Temp)
{
	vec3 col = vec3(255.);
    col.x = 56100000. * pow(Temp,(-3. / 2.)) + 148.;
   	col.y = 100.04 * log(Temp) - 623.6;
   	if (Temp > 6500.) col.y = 35200000. * pow(Temp,(-3. / 2.)) + 184.;
   	col.z = 194.18 * log(Temp) - 1448.6;
   	col = clamp(col, 0., 255.)/255.;
    if (Temp < 1000.) col *= Temp/1000.;
   	return col;
}

// -------------blackbody----------------- //

void mainImage(out vec4 o, in vec2 f )
{
    tilt = 0.;
    t = iTime /4.;
    st = sin(t);
    ct = cos(t);
    vor = 0.;

    vec2 R = iResolution.xy;
          uv  = vec2(f-R/2.) / R.y;
	vec3	dir = camera(uv);
    vec4	col = vec4(0.0);
    vec3	pos = vec3(.0, .0, 11.0);

    vec4	inter = (march(pos, dir));

    /*
	* lighting originally taken from gltracy : https://www.shadertoy.com/view/XsB3Rm
	*/    
    if (inter.w <= 20.) // else ray didn't hit anything
	{
		vec3	v = pos+inter.w*dir;
        #ifdef	BYE_BYE_FPS
        vec3	n = vec3(0.);
        if (tilt > 0.) // only calculate normal when we need it
            n = calcNormal(v, E, dir);
        #else
        vec3	n = calcNormal(v, E, dir);
        #endif
        vec3	ev = normalize(v - pos);
		vec3	ref_ev = reflect(ev, n);
        vec3	light_pos   = vec3(10.0, 10.0, 50.0);
		vec3	light_color = vec3(.1, .4, .7)*(1.-blackbody(vor*1500.) );
        vec3	vl = normalize(light_pos - v);
		float	diffuse  = max(0.0, dot(vl, n));
		float	specular = pow(max(0.0, dot(vl, ref_ev)), 4.);
        col.xyz = light_color * (specular)+ diffuse * vec3(.6, .2, .2)*(1.-blackbody(vor*1500.) );
    }
    #ifdef AO
    // basic AO
    col.xyz += (vec3(inter.x /40.))*mix(vec3(.1, .4, .7), vec3(.6, .2, .2), st );
    #endif
    #ifdef ED
    col = col/2. + col*(50.*(dFdx(col)*dFdy(col)) )/2.;
    #endif
   	o = col;
}

vec3 hash( vec3 p )
{
	p = vec3(	dot(p,vec3(127.1,311.7, 520.6 )),
		dot(p,vec3(269.5,183.3, 150.9 )),
		dot(p, vec3(285.1, 422.4, 211.8 ) ) );
	return fract( (p)*18.5453); // sin(p)
}

// I took a 2d voronoi from iq, then tweaked it a bit, original : https://www.shadertoy.com/view/MslGD8

float	voronoi3d( in vec3 x)
{
    #ifdef	SIMPLEX
    x += .5*vec3(simplex3d(x/1.5+t) );
    #endif
	vec3 n = floor( x * 2.);
	vec3 f = fract( x * 2.);

	vec3 m = vec3( 8., 8., 8. );
	for (float k=-1.; k<=1.; k++)
    {
		for( float j=-1.; j<=1.; j++ )
        {
			for( float i=-1.; i<=1.; i++ )
			{
				vec3  	g = vec3( (i), (j), (k) );
				vec3  	o = hash( n + g );
				vec3 	r = g - f + (.5+.5*sin( t*4. +6.2831*o));
				float	d = dot( r, r );
				if( d < m.x )
					m = vec3( d,o.xy);
			}
        }
    }
	return float( ( (m.x) ) );
}

float	scene(vec3 p)
{
    float	mind = 1e5;
	float	ming = 1e5;
 	float	minh = 1e5;

    mind = (sdTorus(p, vec2(7.5, .5)+ct*2.*exp(-t*4.+.75) ) - 1.25 )-(vor )/7.;
	ming = (sdTorus(p+vec3(0.,0., 0.+ct), vec2(4.5, .5)-ct*1.*exp(-t+2.) ) - 1. )+(vor )/7.;
    minh = (sdTorus(p, vec2(3., 0.)-ct*2.*exp(-t+.5) ) - 2. )-(vor )/20.;

    mind = min(mind, minh);
    mind = max(mind, -ming);

    #ifdef BYE_BYE_FPS
    if (vor > -.0)
    {
	    mind = minh;
        tilt++;
    }
    #endif
    return(mind);
}

vec4	march(vec3 pos, vec3 dir)
{
    vec2	dist = vec2(0.0);
    vec3	p = vec3(0.0);
    vec4	s = vec4(0.0);

    for (int i = -1; i < I_MAX; ++i)
    {
    	p = pos + dir * dist.y;

        #ifdef ROT
		p.xz *= mat2(ct,st,-st, ct);
		#endif

        dist.x = scene(p);
        dist.y += dist.x;
        if (dist.x < E || dist.y > 20. || tilt > 0.)
        {
            if (dist.y > 20.)
                break;
            dist.y -= dist.x;
            for (int i = -1; i < 15; ++i)
            {
                p = pos + dir * dist.y;
                #ifdef ROT
			    p.xz *= mat2(ct,st,-st, ct);
			    #endif

                #ifdef NOISE_OFFSET
                noise_texture = vec3(texture(iChannel0, (uv.xy) ).xy*.057, 0. );
                #endif

                vor = voronoi3d(p + noise_texture * .75 );

                dist.x = scene(p);
	            dist.y += dist.x;
                if (dist.x < E*2. || dist.y > 20.)
                {
                	break;
                }
            }
            break;
        }
        s.x++;
    }
    s.w = dist.y;
    return (s);
}

// http://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdTorus( vec3 p, vec2 t )
{
	vec2 q = vec2(length(p.xy)-t.x,p.z);

    return length(q)-t.y;
}

vec2	rot(vec2 p, vec2 ang)
{
	float	c = cos(ang.x);
    float	s = sin(ang.y);
    mat2	m = mat2(c, -s, s, c);

    return (p * m);
}

vec3 calcNormal( in vec3 pos, float e, vec3 dir)
{
    vec3 eps = vec3(e,0.0,0.0);

    return normalize(vec3(
           march(pos+eps.xyy, dir).w - march(pos-eps.xyy, dir).w,
           march(pos+eps.yxy, dir).w - march(pos-eps.yxy, dir).w,
           march(pos+eps.yyx, dir).w - march(pos-eps.yyx, dir).w ));
}

vec3	camera(vec2 uv)
{
    float   fov = 1.;
	vec3    forw  = vec3(0.0, 0.0, -1.0);
	vec3    right = vec3(1.0, 0.0, 0.0);
	vec3    up    = vec3(0.0, 1.0, 0.0);

    return (normalize((uv.x) * right + (uv.y) * up + fov * forw));
}

float sdSphere(vec3 pos, float radius)
{
    //return sqrt(pos.x*pos.x + pos.y*pos.y + pos.z*pos.z)-radius;
    return length(pos) - radius;
}


float distFunc(vec3 pos)
{
    vec3 spherePos = vec3(0.0, 0.0, 5.0);
    vec3 spherePos2 = spherePos + vec3(3.0 * sin(iTime), 3.0 * cos(iTime), 0.0);
    vec3 delta = spherePos - pos;
    vec3 delta2 = spherePos2 - pos;
    float s1 = sdSphere(delta, 1.0);
    float s2 = sdSphere(delta2, 0.5);
    return min(s1, s2);
}

vec2 rotateAroundPoint(vec2 p, vec2 o, float offset, float speed)
{
    vec2 newPos;
    newPos.x = sin((iTime * speed) + offset) * (p.x - o.x) - cos((iTime * speed) + offset) * (p.y - o.y) + o.x;
    newPos.y = cos((iTime * speed) + offset) * (p.x - o.x) + sin((iTime * speed) + offset) * (p.y - o.y) + o.y;
    return newPos;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 camPos = vec3(0.0, 0.0, 10.0);
    //camPos.xz = rotateAroundPoint(camPos.xz, vec2(0.0), 0.0, 1.0);
    //camPos.xz = vec2(2.0 * sin(iTime), 2.0 * cos(iTime));

    vec3 camTarget = vec3(0.0);
    vec3 worldUp = vec3(0.0, 1.0, 0.0);

    vec3 camDir = normalize(camTarget - camPos);

    vec3 camRight = normalize(cross(worldUp, camPos));
    vec3 camUp = cross(camDir, camRight); 

    vec3 lightPos = vec3(3.0 * sin(iTime), 0.0, 10.0);

    vec2 screenPos = -1.0 + (2.0 * fragCoord.xy / iResolution.xy);
    screenPos.x *= iResolution.x / iResolution.y;

    vec3 rayDir = normalize(camRight * screenPos.x + camUp * screenPos.y + camDir);

    const int MAX_ITER = 100;
    const float MAX_DIST = 20.0;
    const float EPSILON = 0.001;

    float totalDist = 0.0;
    vec3 pos = camPos;
    float dist = EPSILON;

    for(int i = 0; i < MAX_ITER; i++)
    {
        if(dist < EPSILON || totalDist > MAX_DIST)
            break;

        dist = distFunc(pos);
        totalDist += dist;
        pos += rayDir * dist;
    }

    if(dist < EPSILON)
    {
     	vec2 eps = vec2(0.0, EPSILON);
        vec3 normal = normalize(vec3(
            distFunc(pos + eps.yxx) - distFunc(pos - eps.yxx),
            distFunc(pos + eps.xyx) - distFunc(pos - eps.xyx),
            distFunc(pos + eps.xxy) - distFunc(pos - eps.xxy)));

        float diffuse = dot(normalize(lightPos-pos), normal);
        float spec = pow(diffuse, 50.0);
        vec3 ambient = vec3(0.05, 0.05, 0.05);
        vec3 diffuseColor = vec3(0.42, 0.25, 1.0) * diffuse;
        vec3 color = vec3(diffuseColor + spec + ambient);
        fragColor = vec4(color, 1.0);
    }
    else
    {
        fragColor = vec4(0.0);
    }

}

float dunion(float d1, float d2) {
    return min(d1, d2);
}

float dintersect(float d1, float d2) {
    return max(d1, d2);
}

float halfplane(vec2 p, vec2 n, float d) {
    return dot(n, p) + d;
}

float disk(vec2 p, vec2 c, float r) {
    return distance(c, p) - r;
}

float poly(vec2 p, float n, vec2 c, float r) {
    float pi = 3.1415926;
    p -= c;
    float b = 2.0*pi / n;
    float a = atan(p.y, p.x);
    a = mod(a + 0.5*b, b) - 0.5*b;
    return length(p)*cos(a) - r;
}

float roundrect(vec2 p, vec2 c, vec2 r) {
    p = abs(p - c) - r;
	if (p.x >= 0.0 && p.y >= 0.0)
        return length(p);
    else
        return max(p.x, p.y);
}

vec2 rotate(vec2 v, vec2 c, float a) {
    v -= c;
    vec2 w = vec2(cos(a), sin(a));
    return c + vec2(w.x * v.x - w.y * v.y, w.y * v.x + w.x * v.y);
}

float ramp(float x) {
    return x;
}

vec4 over(vec4 c1, vec4 c2) {
    return c1 + (1.0 - c1.a) * c2;
}

float scene(vec2 p) {
    float t = 0.5 * sin(iTime) + 0.5;
    float t2 = 0.5 * sin(2.0 * iTime) + 0.5;
    float boxdist = roundrect(p, vec2(1.45, 0.35 + 0.3 * t2), vec2(0.15 + 0.08 * t2, 0.15));
    p = rotate(p, vec2(0.5, 0.5), iTime);
    float sides = 3.0 + 3.0 * t;
    float radius = 0.1 + 0.15 * t2;
    float dist = dunion(boxdist,
      					dunion(poly(p, sides, vec2(0.3, 0.5), 0.2),
                        disk(p, vec2(0.9, 0.5), radius)));
    return dist;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float pixwidth = length(1.0 / iResolution.xy);
	vec2 pos = fragCoord.xy / iResolution.y;

    float aawidth = 1.0; // 0.0 to turn off aa or > 1.0 to see sdf falloff.
    float aa = aawidth * pixwidth;

    float dist = scene(pos);
    float dist2 = scene(pos + vec2(-0.02, 0.02));

    vec4 interior = vec4(0.9, 0.2, 0.2, 0.0);

    if (dist <= 0.0)
        interior.a = 1.0;
    else if (dist < aa)
        interior.a = ramp(1.0 - dist / aa);

    interior.rgb *= interior.a;

	vec4 outline = vec4(0.15, 0.15, 0.15, 0.0);
    float outline_radius = pixwidth * 10.0;
    if (dist >= -aa) {
        if (dist < 0.0)
            outline.a = ramp(1.0 - (-dist / aa));
        else if (dist <= outline_radius)
        	outline.a = 1.0;
		else if (dist < outline_radius + aa)
            outline.a = ramp(1.0 - (dist - outline_radius) / aa);
    }

    outline.rgb *= outline.a;

    vec4 shadow = vec4(0.1, 0.1, 0.1, 0.0);
    if (dist2 <= outline_radius)
        shadow.a = 1.0;
     else if (dist2 < outline_radius + aa)
        shadow.a = ramp(1.0 - (dist2 - outline_radius) / aa);

    shadow.a *= 0.2;
    shadow.rgb *= shadow.a;

    vec4 background = vec4(0.6, 0.6, 0.8, 1.0);

    fragColor = over(outline, over(interior, over(shadow, background)));
}

