#endif

vec3 plot( vec2 p, float ss )
{
    VoronoiData c = voronoi( p );

    // Colors:
    vec3 interior = vec3(.3,.2,.2);
    vec3 border = vec3(.5,.6,0);
    vec3 point = vec3(.025,.025,.0125);

    float d = length(c.mr);
    vec3 col =
        mix(
            mix(
                interior*(.63-1.1*c.md),
                border*.2+.25*c.md,
                smoothstep(.22,.04,c.md)
            ),
            point,
            smoothstep(.427,.05,d)
        );

    return col;
}

void mainImage00( out vec4 fragColor, in vec2 fragCoord )
{
    float sc = step(512., iResolution.y)*4. + 4.; // scale differently for fullscreen
	float ss = sc / iResolution.y; // size of 1 pixel
    vec2 uv = (fragCoord.xy - iResolution.xy*.5) * ss;
    fragColor = vec4(plot(uv, ss), 1.);
}




//attempting some distance estimated fur and shading with one extra DE calc
#define AUTO_OVERSTEP

#define time iTime
#define size iResolution

#define TAO 6.283
vec2 rotate(vec2 v, float angle) {return cos(angle)*v+sin(angle)*vec2(v.y,-v.x);}
vec2 kaleido(vec2 v, float power){return rotate(v,floor(.5+atan(v.x,-v.y)*power/TAO)*TAO/power);}

vec2 kaleido6(vec2 v){return rotate(v,floor(0.5+atan(v.x,-v.y)*0.95493)*1.0472);}
vec2 kaleido12(vec2 v){return rotate(v,floor(0.5+atan(v.x,-v.y)*1.90986)*0.5236);}
float rndStart(vec2 co){return 0.1+0.9*fract(sin(dot(co,vec2(123.42,117.853)))*412.453);}

vec3 mcol;//material color
mat2 r45=mat2(0.7071,0.7071,-0.7071,0.7071);
mat2 r30=mat2(0.866,0.5,-0.5,0.866);
mat2 rtrn=mat2(0.9689,-0.2474,0.2474,0.9689);

VoronoiData theVoro;
float DE(in vec3 z0){
    VoronoiData voro = voronoi( z0.xz );
    theVoro = voro;
    vec2 id = voro.mi;
    z0.xz = voro.mr;

	float cyl=length(z0.xz);
	float d=100.0,dt=cyl+z0.y*0.025;

	for(int i=0;i<2;i++){
		vec3 z=z0;
        z.xz=rotate(z.xz,id.x*2.+id.y*3.+float(i*2));
		z.y+=float(i)*(0.125+.0625*mod(id.x-id.y,2.0));
		float c=floor(z.y*4.0);
		//z.yz=rotate(z.yz,-z.z*0.79*(1.0+c*0.1));
		float bm=-z.y-2.0+cyl*0.01;
		z.y=mod(z.y,0.25)-0.05;
		if(i<=1)z.xz=z.xz*rtrn*float(i+i-1);
		z.xz=kaleido(z.xz,2.0-c);
		z.yz=rtrn*z.yz;
		bm=max(bm,-z.z+c*0.086);//0.065);
		dt=min(dt,max(max(abs(z.x),abs(z.y)),bm))-0.001-z.z*0.003;
		float c2=floor(z.z*16.0);
		z.z=mod(z.z,0.0625)-0.045;//0.049;
		z.xy=rotate(z.xy,c2*0.25);
		z.xy=kaleido12(z.xy);
		z.yz=z.yz*r30;
		d=min(d,max(max(max(abs(z.x),abs(z.z)),-z.y-0.05+c*0.005),bm));
	}
	if(dt<d){
		d=dt;
		mcol=vec3(0.5,0.1,0.0);
	}else{
		mcol=vec3(0.5,0.6,0.2);
		//mcol*=1.0+(-z0.y*0.5975)*abs(z0.x+z0.z)/cyl;
	}
    mcol*=cyl + 0.5 + z0.y*0.5;//kind of what iq suggested

	return max(0.0,max(d,max(z0.y,-z0.y-2.0)));
}

mat3 lookat(vec3 fw,vec3 up){
	fw=normalize(fw);vec3 rt=normalize(cross(fw,up));return mat3(rt,cross(rt,fw),fw);
}
float fog(float t){return exp(-t*0.02);}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	float zoom=2.0;
    float px=2.0/(size.y*zoom);//find the pixel size
    float ipx = 1.0/px;
	float tim=time;

    #ifndef NO_DEBUG
    float mark0 = fragCoord.x-iResolution.x*.5;
    float mark1 = abs(mark0)-iResolution.x*.1875;
    float mark2 = mark1-iResolution.x*.0625;
    float mark3 = mark1-iResolution.x*.1250;
    float mark4 = mark1-iResolution.x*.1875;
    float mark5 = mark1-iResolution.x*.2500;
	tim += mark0>0.?30.:0.;
    #endif

    tim *= 0.3;
	//position camera
	vec3 ro=vec3(0.5*sin(tim*0.43),18.*(sin(tim*.4)+.975),tim*20.);
	vec3 rd=normalize(vec3((2.0*fragCoord.xy-size.xy)/size.y,zoom));
	rd=lookat(vec3(-1.15*4.+8.*0.75*sin(tim),1.-1.5*sin(tim*.02),tim*20.+2.0)-ro,vec3(0.0,1.0,0.0))*rd;
	//ro=eye;rd=normalize(dir);
	vec3 ld=normalize(vec3(-0.4,0.75,-0.4));//direction to light
	vec3 bcol=1.0-clamp(vec3(rd.y,rd.y+0.1*exp(-abs(rd.y*15.0)),0.5*exp(-abs(rd.y*5.0))),0.,1.);//backcolor
	//march

	fragColor=vec4(0,0,0,1);

	float tG=abs((-2.0-ro.y)/rd.y);
    float d;
    float pd=10.0;
    float os=0.0;
    float steps=0.0;
	vec2 g=ro.xz+rd.xz*tG;
    float noise = rndStart(fragCoord.xy);
    const float rndBands = 1.;
	float t=floor(rndBands*DE(ro)*noise+.5)/rndBands;
	float MIN_DIST=px*0.1;
    vec3 pos;
	vec4 col=vec4(0.0);//color accumulator
	for(int i=0;i<78;i++){
        pos = ro+rd*t;
		d=DE(pos);
		float d1=max(d,px*t*0.5);
#ifdef AUTO_OVERSTEP
		if(d1>os){		//we have NOT stepped over anything
			if(t>tG)break;
			os=0.28*d1*d1/pd;//calc overstep based on ratio of this step to last
			steps=d1+os;	//add in the overstep
			pd=d1;	//save this step length for next calc
		}else{
			steps=-os;d1=1.0;pd=10.0;os=0.0;//remove ALL of overstep
		}
#else
			steps=d1;
#endif
		if(d1<px*t){
			vec3 scol=mix(mcol,bcol,clamp(min(t*0.05,1.0),0.,1.));
			float d2=DE(pos+ld*px*t);
			float shad=1.4-0.5*clamp((d2/d)+1.,0.,10.);
			scol=scol*shad+vec3(0.02,0.05, -0.125)*(shad+1.5);
            vec2 q = mod(pos.xz,2.0)-vec2(1.0);
            scol *= clamp(length(q) + 0.8 + pos.y*0.5,0.,1.);
			float alpha=(1.0-col.w)*clamp(1.0-d1/(px*t),0.,1.);
            //alpha=1.;
			col+=vec4(clamp(scol,0.0,1.0),1.0)*alpha;
			if(col.w>0.99)break;
		}
		t+=steps;
	}


    float treeMask = col.a;
    float horizon = .5+.5*cos(radians(clamp(rd.y*12.*180.,-180.,180.)));
    float attenFar = clamp(log(max(1.0,(1./30.)*mix(t,t-25.,horizon)))*2.5,0.,1.);//smoothstep(50.0,75.0,t);
    float distantFog = (1.0-0.292*attenFar);
    float onlyFog = fog(abs(t));
    float fogNear=1.92-.325*col.w-col.w*onlyFog;
    float farFog = 1.0-fogNear;
    float treeMaskFar = 1.0-treeMask*clamp(fogNear*10.,0.,1.);
    float horizonFog = pow(horizon*.99,16.)*treeMaskFar;
    float groundCover = fogNear*(1.0-col.a)*distantFog;
    vec4 groundCol = vec4(0,0,0,clamp(-64.0*rd.y,0.,1.));
	//color the ground 
	if(groundCol.a>0.0){
        vec3 gcol = plot(g,px);
		ro+=rd*tG;
		float s=1.0,dst=0.1;
		t=DE(ro)*rndStart(fragCoord.xy);
		for(int i=0;i<4;i++){
			float d=max(0.0,DE(ro+ld*t)*1.5)+0.05;
			s=min(s,3.0*d/t);
			t+=dst;
            dst*=2.0;
		}
		gcol*=0.2+0.8*s;
        //col.rgb+=clamp(gcol*fogNear*(1.0-col.a)*(1.0-0.292*attenFar),0.,1.);
        //col.a+=(1.0-col.a)*clamp(fogNear*(1.0-col.a)*(1.0-0.292*attenFar),0.,1.);
        groundCol.rgb+= clamp(gcol*groundCover,0.,1.);
        groundCol.a+=(1.0-col.a)*clamp(groundCover,0.,1.);
        //groundCol.rgb = clamp(gcol*fogNear*(1.0-col.a)*(1.0-0.292*attenFar),0.,1.);
    }
    float groundMask = (1.-col.a);
    col+=groundCol*groundMask;
    float skyMask = (1.-col.a);
    //col.a+=(1.0-col.a)*clamp(fogNear*(1.0-col.a)*(1.0-0.292*attenFar),0.,1.);
    col.rgb += clamp(bcol*skyMask,0.,1.);
    //col.rgb = mix(col.rgb,bcol,attenFar);
    //col.rgb = mix(col.rgb,bcol,attenFar*(1.0-col.a));
    col.rgb = mix(col.rgb,bcol,horizonFog);
    col.rgb += bcol*horizon*.5;
    //col.rgb = mix(col.rgb,bcol,attenFar*(1.0-col.a)*(1.0-horizonFog)+horizonFog);
    float noSkyMask = clamp(1.-onlyFog,0.,1.);
    float groundedFog = clamp((fogNear-.8)*(treeMask+horizon*.5)*1.75,0.,1.)*.25;
    col.rgb += bcol*(groundedFog);

	fragColor=vec4(col.rgb,1.0);
    #ifndef NO_DEBUG
  	fragColor.rgb=mark1>0.?vec3(0,0,1)*vec3(groundCover,groundMask,treeMaskFar):fragColor.rgb;
  	fragColor.rgb=mark2>0.?vec3(1,1,1)*vec3(attenFar,distantFog,farFog):fragColor.rgb;
  	fragColor.rgb=mark3>0.?vec3(0,1,0)*vec3(onlyFog,fogNear,treeMask):fragColor.rgb;
  	fragColor.rgb=mark4>0.?vec3(1,1,1)*vec3(horizon,horizonFog,skyMask):fragColor.rgb;
  	fragColor.rgb=mark5>0.?vec3(0,1,0)*vec3(noSkyMask,groundedFog,skyMask):fragColor.rgb;
    #endif

}

// Author: ocb
// Title: Trip in Tron
// Checking basic primitives (sphere, cone, cylinder, 3D rect) in Ray Intercepting (direct calculation of ray impact on object)
// The 2 solutions of shpere/ray interception is calculated to allow the inside view of sphere (use for the shock wave)
// pulses are calculated in 3D using max distance inside the object (bottom of parabol).

// Use mouse (clic and right/left) to change the max camera travel

#define PI 3.141592653589793
#define INFINI 1000000000.
#define maxSph 3
#define maxCone 2
#define maxRec 2



float random (in vec2 st,float time) { 						
    return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*43758.5453123+time);
}


// from TheBookOfShader
vec2 truchetPattern(in vec2 st, in float i){

    if (i > 0.75) {
        st = vec2(1.) - st;
    } else if (i > 0.5) {
        st = vec2(1.0-st.x,st.y);
    } else if (i > 0.25) {
        st = 1.0-vec2(1.0-st.x,st.y);
    }
    return st;
}

// tower skin
float dataFlow(vec2 o, vec2 dim, vec2 pos){
    float taux = 0.;
    vec2 p = pos - o;

 	float i = floor( abs(p.x)/dim.x * 20.);
    float f = fract( abs(p.x)/dim.x * 20.);

    taux =  step(.8*(sin(iTime*.1)+1.)/2.+.1, random(vec2(i,p.y/2000000.),iTime*.1))*step(.8,random(vec2(p.y/1000000.),1.))  *step(.1,f)*(1.-step(.9,f));
    taux += smoothstep(.9,1.,p.x/dim.x)+(1.-smoothstep(.0,.1,p.x/dim.x)) ;    

    return taux;
}


float groundImpact(in vec3 pos,in float gndSurface, in vec3 N_ray){
    float t = INFINI;
    t = (gndSurface-pos.y)/N_ray.y;

    if (t <= 0.){
        t = INFINI;
    }
    return t;
}


vec2 sphereImpact(in vec3 pos, in vec3 sphO, in float sphR, in vec3 N_ray){

    float t = INFINI;
    float t1, t2;
    vec3 delta = pos - sphO;
    float dmin = 0.;

    // Sphere interception
    // pre-calculation
    float dot_delta_N_ray = dot(delta, N_ray);
    float c = dot(delta,delta) - sphR*sphR;

    float discr = dot_delta_N_ray*dot_delta_N_ray - c;

    if (discr >= 0.){
       	float sqrt_discr = sqrt(discr);
        t1= (-dot_delta_N_ray + sqrt_discr);
        t2= (-dot_delta_N_ray - sqrt_discr);
        t = min(t1,t2) ;
        if (t <= 0.001){
            t = max(t1,t2);
            if (t <= 0.001) t = INFINI;
        } 
    }

    dmin = abs(c - dot_delta_N_ray*dot_delta_N_ray)/sphR;

    return vec2(t, dmin);
}

float rectImpact(in vec3 pos, in vec3 recO, in vec3 recD, in vec3 N_ray, inout vec3 norm){

    float t = INFINI;
    float t1,t2,t3;
    float d1,d2,d3;


    	if(recO.z+recD.z-pos.z<0.){
        	t1 = (recO.z+recD.z-pos.z)/N_ray.z;
        	d1 = 1.;
    	}
    	else if(recO.z-pos.z>0.000){
    		t1 = (recO.z-pos.z)/N_ray.z;
    		d1 = -1.;
		}
    	else{t1=-1.;}

    	if(recO.y+recD.y-pos.y<0.){
        	t2 = (recO.y+recD.y-pos.y)/N_ray.y;
        	d2 = 1.;
    	}
    	else if(recO.y-pos.y>0.){
        	t2 = (recO.y-pos.y)/N_ray.y;
        	d2 = -1.;
    	}
    	else{t2=-1.;}

    	if(recO.x+recD.x-pos.x<0.){
        	t3 = (recO.x+recD.x-pos.x)/N_ray.x;
        	d3 = 1.;
    	}
    	else if(recO.x-pos.x>0.){
        	t3 = (recO.x-pos.x)/N_ray.x;
        	d3 = -1.;
    	}
    	else{t3=-1.;}


    	vec3 pos1 = pos + N_ray*t1;
    	vec3 pos2 = pos + N_ray*t2;
    	vec3 pos3 = pos + N_ray*t3;

    	if(t1>0.001 && all(bvec2 (step(recO.xy,pos1.xy)*(1.-step(recO.xy+recD.xy,pos1.xy))))){
        	t = t1;
        	norm = vec3 (0.,0.,d1);
    	}
		else
    	if(t2>0.001 && all(bvec2 (step(recO.xz,pos2.xz)*(1.-step(recO.xz+recD.xz,pos2.xz))))){
        	t = t2;
        	norm = vec3 (0.,d2,0.);
    	}
    	else
    	if(t3>0.001 && all(bvec2 (step(recO.yz,pos3.yz)*(1.-step(recO.yz+recD.yz,pos3.yz))))){
        	t = t3;
        	norm = vec3 (d3,0.,0.);
    	}

    return t;
}

vec2 cylinderImpact(in vec2 pos, in vec2 cylO, in float cylR, in vec2 N_ray){
    float t=INFINI, minDist=0.;
    vec2 delta = pos - cylO;

    // circle interception
    // pre-calculation
    float dot_N_ray_Sq = dot(N_ray,N_ray);
    float dot_delta_N_ray = dot(delta, N_ray);
    float c = dot(delta,delta) - cylR*cylR;

    float discr = dot_delta_N_ray*dot_delta_N_ray - dot_N_ray_Sq*c;

    if (discr >= 0.){
        float sqrt_discr = sqrt(discr);
        t= min((-dot_delta_N_ray + sqrt_discr)/(dot_N_ray_Sq),
        		(-dot_delta_N_ray - sqrt_discr)/(dot_N_ray_Sq));
        if (t < 0.001) t=INFINI;
    }

    minDist = abs(c-dot_delta_N_ray*dot_delta_N_ray/dot_N_ray_Sq)/cylR;

	return vec2(t,minDist);
}

vec2 coneImpact(in vec3 pos, in vec3 coneO, in float coneH, in float coneR, in vec3 N_ray){
    float t = INFINI, minDist=INFINI;
    vec3 delta = pos - coneO;

    float Dy = coneH - delta.y;
    float r2 = coneR*coneR/(coneH*coneH);

    // circle interception
    // pre-calculation
    float dot_N_ray_Sq_XZ = dot(N_ray.xz,N_ray.xz);
    float dot_delta_N_ray_XZ = dot(delta.xz, N_ray.xz);
    float c = dot(delta.xz,delta.xz) - r2*Dy*Dy;
    float calc = dot_delta_N_ray_XZ + r2*Dy*N_ray.y;

    float discr = calc*calc - (dot_N_ray_Sq_XZ - r2*N_ray.y*N_ray.y) * c;

    if (discr >= 0.){
        float sqrt_discr = sqrt(discr);
        float t1 = (-calc + sqrt_discr)/(dot_N_ray_Sq_XZ - r2*N_ray.y*N_ray.y);
        float t2 = (-calc - sqrt_discr)/(dot_N_ray_Sq_XZ - r2*N_ray.y*N_ray.y);

        if (t1 <= 0.001) t1 = INFINI;
        else if (-N_ray.y*t1 + Dy < 0.) t1 = INFINI;
        else if (-N_ray.y*t1 + Dy > coneH) t1 = INFINI;

        if (t2 <= 0.001) t2 = INFINI;
        else if (-N_ray.y*t2 + Dy < 0.) t2 = INFINI;
        else if (-N_ray.y*t2 + Dy > coneH) t2 = INFINI;

        t = min(t1,t2);  
        if (t < INFINI) minDist = abs(c-calc*calc/(dot_N_ray_Sq_XZ - r2*N_ray.y*N_ray.y))/(coneR/coneH);
    }

	return vec2(t,minDist);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy-.5;
    st.x *= iResolution.x/iResolution.y;

    // object def

    //sphere
    vec3 sphO[maxSph];
    float sphR[maxSph];

    sphO[0] = vec3(40.*sin(iTime*.45),5.*sin(iTime*.2)+30.,60.*cos(iTime*.3));
    sphR[0] = 5.;
    sphO[1] = vec3(50.*sin(iTime*.4),8.*sin(iTime*.3)+35.,50.*cos(iTime*.35));
    sphR[1] = 4.;
    sphO[2] = vec3(60.*sin(iTime*.35),5.*sin(iTime*.2)+40.,30.*cos(iTime*.4));
    sphR[2] = 3.;

    //cube
    vec3 recO[maxRec];
    vec3 recD[maxRec];
    vec3 normRect[maxRec];

    recO[0] = vec3(-20,0.,-20.);
    recD[0] = vec3(5.,4000.,5.);
    recO[1] = vec3(20,0.,20.);
    recD[1] = vec3(5.,4000.,5.);

    //cylinder
    vec3 XcylO = vec3(0.,10.,0.);
    float XcylR = 1.;
    vec3 ZcylO = vec3(0.,10.,0.);
    float ZcylR = 1.;

    // sphere for impact
    vec3 sphXO = XcylO;
    float sphXR = 1000.*fract(iTime*.1);

    //cone

    vec3 coneO[maxCone];
    float coneH[maxCone];
    float coneR[maxCone];

    coneO[0] = vec3(20.,0.,-20.);
    coneH[0] = 40.;
    coneR[0] = 6.;
    coneO[1] = vec3(-20.,0.,20.);
    coneH[1] = 40.;
    coneR[1] = 6.;


    // camera target is switching from shere1 and cylinders crossing
    vec3 camTarget = sphO[0]*(1.+sin(iTime*.2))/2. + XcylO*(1.+sin(iTime*.2+PI))/2.;
    //vec3 camTarget = recO[0]+vec3(0.,0.,0.);
    //vec3 camTarget = coneO[0]+ vec3(0.,20.,0.);

    int hitObj = -1000;	// -1000 is Sky, -1 is Ground

    // camera def
    float focal = 1.;
    //rau, alpha, theta cam position
    float rau = (1.-iMouse.x/iResolution.x)*500.*(sin(iTime/11.)+1.) + 10.,
          alpha = -iTime/5.,
          theta = (sin(iTime/7.)/2.+0.5)*(PI/2.-.3)+0.05;

    //float cosT = cos(theta), sinT = sin(theta), cosA = cos(alpha), sinA = sin(alpha);	// pre-calculate  

    // cam pos
    vec3 screenPos = rau*vec3(-cos(theta)*sin(alpha),sin(theta),cos(theta)*cos(alpha)) + camTarget;	
    vec3 pos = screenPos;

    // 2 rotations. working. more elegant solution below (iq)
	//vec3 N_ray = normalize(vec3(st.x*cosA - (-st.y*sinT - focal*cosT)*sinA,				// ray rotation
    //                             st.y*cosT - focal*sinT,
    //                             st.x*sinA + (-st.y*sinT - focal*cosT)*cosA));

    //screen coordinates
	vec3 ww = normalize( camTarget - screenPos );
    vec3 uu = normalize(cross(ww,vec3(0.0,1.0,0.0)) ) ;
    vec3 vv = cross(uu,ww);
	// create view ray
	vec3 N_ray = normalize( st.x*uu + st.y*vv + focal*ww );


    float ground = 0.;
    vec3 N_normPos = vec3(0.);


    vec3 color = vec3(.0);

    vec2 infoCone[maxCone];
        infoCone[0] = vec2 (INFINI);
        infoCone[1] = vec2 (INFINI);

    for (int rayNbr = 0; rayNbr <4; rayNbr++){		// Max 4 reflects

    	float t = INFINI; 	// parametric distance
    	float temp = INFINI;
        // result infos for sphere, cylinder X and Z
        // info contain t and max distance reach inside the obj.
        vec2 infoS = vec2(INFINI);
        vec2 infoX = vec2 (INFINI), infoZ = vec2 (INFINI);

        float explDist = 0.;
        hitObj = -1000;

        // explosion sphere

        infoS = sphereImpact(pos, sphXO, sphXR, N_ray);
    	if(infoS.x<INFINI){
            t = infoS.x;
            hitObj = 50;
            explDist = infoS.y;
        }

    	// sphere obj impact
    	for (int i = 0; i<maxSph; i++){
    		infoS = sphereImpact(pos, sphO[i], sphR[i], N_ray);
    		if(infoS.x<t){
            	t = infoS.x;
            	hitObj = i;
        	}
    	}

        //rect  obj impact
        for (int i = 0; i<maxRec; i++){
        	temp = rectImpact(pos,recO[i], recD[i],N_ray, normRect[i]);
        	if(temp<t){
            	t = temp;
             	hitObj = 10+i;
            }
        }

        infoX = cylinderImpact(pos.yz, XcylO.yz, XcylR, N_ray.yz);
        if(infoX.x<t){
            t = infoX.x;
            hitObj = 20;
        }

        infoZ = cylinderImpact(pos.xy, ZcylO.xy, ZcylR, N_ray.xy);
        if(infoZ.x<t){
            t = infoZ.x;
            hitObj = 21;
        }

        for (int i = 0; i<maxCone; i++){ 
        	infoCone[i] = coneImpact(pos, coneO[i], coneH[i], coneR[i], N_ray);
        	if(infoCone[i].x<t){
                t = infoCone[i].x;
                hitObj = 30+i;
            }
        }


    	// ground impact
    	temp = groundImpact(pos, ground, N_ray );
    	if (temp<t){
        	t = temp;
        	hitObj = -1;
    	}



        if (hitObj >= 0){	// if Object hit, do raytrace again

            pos += t*N_ray;	// with new pos
            				// and new ray (calculated below after finding Normal vec

            if (hitObj == 0) N_normPos = normalize(pos - sphO[0]);
  			else if (hitObj == 1) N_normPos = normalize(pos - sphO[1]);
          	else if (hitObj == 2) N_normPos = normalize(pos - sphO[2]);

            else if (hitObj == 10){
                N_normPos = normRect[0];
                if (bool(normRect[0].x)) color.gb += .3*dataFlow( recO[0].zy, recD[0].zy, pos.zy);
                else color.gb += .3*dataFlow( recO[0].xy, recD[0].xy, pos.xy);
                if (N_ray.y <0.) color.b += min(.2,-N_ray.y);
                color.b += .2*(1.-smoothstep(0.,10.,pos.y));
            }
            else if (hitObj == 11){
                N_normPos = normRect[1];
            	if (bool(normRect[1].x)) color.gb += .3*dataFlow( recO[1].zy, recD[1].zy, pos.zy);
                else color.gb += .3*dataFlow( recO[1].xy, recD[1].xy, pos.xy);
                if (N_ray.y <0.) color.b += min(.2,-N_ray.y);
                color.b += .2*(1.-smoothstep(0.,10.,pos.y));
            }
            else if (hitObj == 20){
                N_normPos = normalize(vec3(0.,pos.y-XcylO.y,pos.z-XcylO.z));
                color.r += max(.0,pow(infoX.y,2.)-smoothstep(.0, .8, fract(iTime*.1+pos.x*0.00025)));
                //color.r += min(1.,.05/infoX.y);
            }
            else if (hitObj == 21){
                N_normPos = normalize(vec3(pos.x-ZcylO.x,pos.y-ZcylO.y,0.));
                color.gb += max(0.,infoZ.y-smoothstep(0., .4, fract(iTime*3.+pos.z*0.01)));
                color.gb += min(1.,.1/infoZ.y);
            } 

            else if (hitObj == 30){
                N_normPos = normalize(vec3(pos.x - coneO[0].x,
                                           coneR[0]*coneR[0]/(coneH[0]*coneH[0])*(coneH[0] + coneO[0].y - pos.y),
                                           pos.z-coneO[0].z));
                if (infoCone[0].y != INFINI) color.b += .4*min(1.,30./pow(infoCone[0].y,2.));
                color.b += .4*(1.-smoothstep(0.,10.,pos.y));

            }
            else if (hitObj == 31){
                N_normPos = normalize(vec3(pos.x - coneO[1].x,
                                           coneR[1]*coneR[1]/(coneH[1]*coneH[1])*(coneH[1] + coneO[1].y - pos.y),
                                           pos.z-coneO[1].z));
				if (infoCone[1].y != INFINI) color.b += .4*min(1.,30./pow(infoCone[1].y,2.));
                color.b += .4*(1.-smoothstep(0.,10.,pos.y));
            }

            else if (hitObj == 50){
                color.r += .5*min(1.,10000./explDist/pos.y/pos.y)*min(.9, smoothstep(0.,1.,(1.-fract(iTime*.1))));
            }

            if(hitObj != 50){
            	N_ray = reflect(N_ray,N_normPos);			// and new ray
        		//color += vec3(.0,.05,.1);
            }
        }

        else if (hitObj == -1) {
            pos += t*N_ray;		// ground: stop ray trace
            break;
        }
        else break;				// Sky: stop raytrace
    }

    if (hitObj >= 0){			// if ray still bouncing between 2 spheres, ray must be finished
    	float t = groundImpact(pos, ground, N_ray );	//consider no more objects
    	if (t == INFINI) hitObj = -1000;				// ray finish in sky
    	else {											// or on ground
        	hitObj = -1;
        	pos += t*N_ray;
    	}
    }

    if (hitObj == -1) {	// ground hit

        vec2 ipos = floor(vec2(pos.x,pos.z)*.01);  // integer
    	vec2 fpos = fract(vec2(pos.x,pos.z)*.01);  // fraction
		vec2 tile = truchetPattern(fpos, random(ipos, iTime*.05));		// generate Maze
        vec2 tileXL = truchetPattern(fract(vec2(pos.x,pos.z)*.5), random( floor(vec2(pos.x,pos.z)*.5) , iTime*.1 ));		// used for impact effect 

        // Maze		//min(1.,(-N_ray.y)*5.) = distance fog to limit aliasing
        vec2 p = pos.xz+20.*iTime;
    	vec2 vapor = max(vec2(0.),1.+.3*sin(.03*p)+.2*sin(.1*p));
        float v = vapor.x*vapor.y;
        color.b += .3*v*min(1.,(-N_ray.y)*5.)*(smoothstep(tile.x-0.02*v,tile.x,tile.y) - smoothstep(tile.x,tile.x+0.02*v,tile.y));
        color.b += v*min(1.,(-N_ray.y)*5.)*.005/(length(tile-fract(iTime+random(ipos,1.)))+.01);	// Head on top of Truchet pattern

        color.b += .7*(1.-smoothstep(coneR[0],coneR[0]*3.,length(coneO[0].xz-pos.xz)))
            			*(smoothstep(tileXL.x-0.1,tileXL.x,tileXL.y)- smoothstep(tileXL.x,tileXL.x+0.1,tileXL.y));
        color.b += .7*(1.-smoothstep(coneR[1],coneR[1]*3.,length(coneO[1].xz-pos.xz)))
            			*(smoothstep(tileXL.x-0.1,tileXL.x,tileXL.y)- smoothstep(tileXL.x,tileXL.x+0.1,tileXL.y));

        vec2 recC = recO[0].xz+recD[0].xz/2.;
        color.b += .7*(1.-smoothstep(recD[0].x,recD[0].x*3.,abs(recC.x-pos.x)))*(1.-smoothstep(recD[0].z,recD[0].z*4.,abs(recC.y-pos.z)))
            			*(smoothstep(tileXL.x-0.1,tileXL.x,tileXL.y)- smoothstep(tileXL.x,tileXL.x+0.1,tileXL.y));
        recC = recO[1].xz+recD[1].xz/2.;
        color.b += .7*(1.-smoothstep(recD[1].x,recD[1].x*3.,abs(recC.x-pos.x)))*(1.-smoothstep(recD[1].z,recD[1].z*4.,abs(recC.y-pos.z)))
            			*(smoothstep(tileXL.x-0.1,tileXL.x,tileXL.y)- smoothstep(tileXL.x,tileXL.x+0.1,tileXL.y));

    	color.r += .9* min(.9, smoothstep(.2,1.,(1.-fract(iTime*.1))*(.7*(smoothstep(tileXL.x-0.1,tileXL.x,tileXL.y)-
              .7*smoothstep(tileXL.x,tileXL.x+0.1,tileXL.y))+.3*(1.-smoothstep(.0,.2,length(tileXL-fract(iTime*5.)))) )*(1.-smoothstep(0.,600000.*fract(iTime*.1), pos.x*pos.x+ pos.z*pos.z)) ));  //ground impact

        color.b += .2- min(.2,-N_ray.y);

    }
    if (hitObj == -1000) {		// ray goes to the sky
        color += vec3(max((N_ray.x+1.)/2.*(-4.*N_ray.y+1.)/2.,0.),.1*(1.-N_ray.y),.2*(1.-N_ray.y));
        color += vec3(.0,.0,.1);
    }

    fragColor = vec4(color,1.0);
}

