vec2 bsa1, bsa2, bsa3;
float dstFar, tCur, spFlr, spShf, htShf, htFlr, rOut, wFlr, wWal;
int idObj;

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, a, rBar;
  dMin = dstFar;
  rBar = rOut - 2. * wFlr + 0.05;
  q = p;
  a = atan (q.z, - q.x) / (2. * pi);
  d = abs (length (q.xz) - rOut) - wWal;
  q.y = mod (q.y - 5.5 + 0.5 * spFlr, spFlr) - 0.5 * spFlr;
  q.xz = Rot2D (q.xz, 2. * pi * (floor (8. * a) + 0.5) / 8.);
  q.z = abs (q.z);
  d = max (d, - max ((length (q.zy) - 1.5),
     - min (abs (q.y) - 0.03, dot (q.xz, bsa3))));
  if (d < dMin) { dMin = d;  idObj = 1; }
  q = p;
  q.y = mod (q.y + 0.5 * spFlr, spFlr) - 0.5 * spFlr;
  d = PrCylAnDf (q.xzy, rOut - wFlr, wFlr, htFlr);
  if (d < dMin) { dMin = d;  idObj = 2; }
  q = p;
  q.xz = Rot2D (q.xz, 2. * pi * (floor (16. * a) + 0.5) / 16.);
  q.x -= - rBar;
  q.z = abs (q.z) - 0.25;
  d = length (q.xz) - 0.15;
  if (d < dMin) { dMin = d;  idObj = 3; }
  q = p;
  q.y = mod (q.y - 3. + 0.5 * spFlr, spFlr) - 0.5 * spFlr;
  d = PrTorusDf (q.xzy, 0.08, rBar);
  if (d < dMin) { dMin = d;  idObj = 4; }
  q = p;
  q.y = abs (abs (mod (q.y - 1.5 + 0.5 * spFlr, spFlr) - 0.5 * spFlr) - 0.6) - 0.3;
  d = PrTorusDf (q.xzy, 0.04, rBar);
  if (d < dMin) { dMin = d;  idObj = 4; }
  q = p;
  a = mod (a - 1./16. + 0.5, 1.) - 0.5;
  q.xz = Rot2D (q.xz, 2. * pi * ((floor (8. * a) + 0.5) / 8. + 1. / 16.));
  q.z = abs (q.z);
  d = max (abs (length (q.xz) - rOut + wWal + 0.3 * wFlr) - 0.3 * wFlr,
     dot (q.xz, bsa2));
  if (d < dMin) { dMin = d;  idObj = 5; }
  d = max (abs (length (q.xz) - rOut + wWal + 0.4 * wFlr) - 0.4 * wFlr,
     dot (q.xz, bsa1));
  d = max (d, min (abs (mod (mod (q.y, spFlr) + htFlr + 0.167 * spShf, spShf) -
     0.5 * spShf) - htShf, - dot (q.xz, bsa2)));
  if (d < dMin) { dMin = d;  idObj = 6; }
  q.y = mod (q.y + 0.5 * spFlr + htFlr, spFlr) - 0.5 * spFlr;
  d = max (PrTorusDf (q.xzy, 0.15, rOut - 1.5 * wFlr), dot (q.xz, bsa1));
  if (d < dMin) { dMin = d;  idObj = 7; }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 100; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  const vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy),
     ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec3 WoodCol (vec3 p, vec3 n)
{
  float f;
  p *= 4.;
  f = dot (vec3 (Fbm2 (p.zy * vec2 (1., 0.1)),
     Fbm2 (p.zx * vec2 (1., 0.1)), Fbm2 (p.xy * vec2 (1., 0.1))), abs (n));
  return mix (vec3 (0.8, 0.5, 0.3), vec3 (0.55, 0.35, 0.1), f);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 vn, col, q;
  vec2 gb;
  float dstObj, a, s, bh, spec;
  int idObjT;
  rOut = 20.;
  wFlr = 4.;
  wWal = 0.5;
  spFlr = 8.;
  htShf = 0.05;
  htFlr = 0.2;
  spShf = 0.167 * (spFlr - htFlr) - htShf;
  bsa1 = vec2 (sin (1.2 * 2. * pi / 32.), cos (1.2 * 2. * pi / 32.));
  bsa2 = vec2 (sin (0.95 * 1.2 * 2. * pi / 32.), cos (0.95 * 1.2 * 2. * pi / 32.));
  bsa3 = vec2 (sin (0.01 * 2. * pi / 32.), cos (0.01 * 2. * pi / 32.));
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += rd * dstObj;
    idObjT = idObj;
    vn = ObjNf (ro);
    idObj = idObjT;
    spec = 0.1;
    q = ro;
    a = 8. * atan (q.z, - q.x) / (2. * pi);
    gb = floor (vec2 (q.y / spFlr, a));
    if (idObj == 1) {
      col = vec3 (0.6, 0.6, 0.55);
      col *= 1. - 0.6 * smoothstep (0.15, 0.45, abs (0.5 - mod (a - 0.5, 1.)));
      q.xy = vec2 (5. * (mod (a, 1.) - 0.5), 0.5 * (mod (q.y, spFlr) - 2.5));
      if (abs (q.x) < 0.325 && abs (q.y) < 0.125) {
        if (ShowInt (vec2 (q.x - 0.3, q.y + 0.09),
           vec2 (0.6, 0.15), 4., mod (4242. + gb.x, 10000.)) != 0.)
           col = vec3 (0.8, 0.6, 0.);
      }
      vn = VaryNf (100. * ro, vn, 2.);
    } else if (idObj == 2) {
      if (abs (vn.y) > 0.9) {
        if (vn.y > 0.) col = vec3 (0.4, 0.2, 0.2) * (0.5 + 0.5 * smoothstep (0., 0.4,
           (1. - (rOut - length (ro.xz)) / (2. * wFlr))));
        else col = vec3 (0.8) *(1. - 0.4 * smoothstep (0.3, 0.5,
           abs (rOut - 1.5 * wFlr - length (ro.xz)) / wFlr));
        col *= 1. - 0.6 * smoothstep (0.15, 0.45, abs (0.5 - mod (a - 0.5, 1.)));
      } else col = vec3 (0.65, 0.7, 0.7);
      vn = VaryNf (100. * ro, vn, 1.);
    } else if (idObj == 3) {
      col = vec3 (0.7);
      spec = 0.1;
    } else if (idObj == 4) {
      col = vec3 (0.5, 0.5, 0.55);
      spec = 0.8;
    } else if (idObj == 5) {
      q.y = mod (q.y, spFlr) + htFlr + 0.5 * spShf + 0.167 * spShf;
      gb = 0.5 * (gb + floor (vec2 (q.y / spShf, 48. * mod (a, 1.))));
      bh = (0.7 + 0.3 * Fbm2 (gb * vec2 (19., 31.))) * spShf;
      q.y = mod (q.y, spShf);
      if (q.y < bh) { 
        s = mod (48. * mod (a, 1.), 1.);
        col = vec3 (HsvToRgb (vec3 (Fbm2 (gb * vec2 (17., 11.)), 1.,
           SmoothBump (0.08, 0.92, 0.01, 0.1 + 0.9 * s) *
           (1. - 0.5 * step (abs (abs (q.y / bh - 0.5) - 0.35), 0.02)))));
        vn.xz = Rot2D (vn.xz, 2. * (s - 0.5));
        vn = VaryNf (20. * ro, vn, 1.);
      } else col = vec3 (0.1);
    } else if (idObj == 6) {
      col = WoodCol (vec3 (5. * (mod (a, 1.) - 0.5), ro.y, length (ro.xz)), vn);
    } else if (idObj == 7) {
      col = vec3 (1., 1., 0.6) * 0.5 * (1. - vn.y);
    }
    if (idObj != 7) {
      col = col * (0.2 + 0.8 * max (dot (vn, ltDir), 0.) +
         spec * pow (max (0., dot (ltDir, reflect (rd, vn))), 64.));
      col *= smoothstep (0.7, 0.85, dot (rd, - ltDir)) *
         (1. - 0.7 * smoothstep (0.5, 0.7, dstObj / 50.));
    } 
  } else {
    col = vec3 (0., 0., 0.07);
    rd = (rd + vec3 (1., 0.7, 0.3));
    for (int j = 0; j < 14; j ++) rd = 11. * abs (rd) / dot (rd, rd) - 3.;
    col += min (1., 1.5e-6 * pow (min (16., length (rd)), 5.)) *
       vec3 (0.7, 0.6, 0.6);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float el, az;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  az = -0.2 * tCur;
  el = -0.17 * pi * sin (0.2 * tCur);
  dstFar = 100.;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  rd = vuMat * normalize (vec3 (uv, 2.1));
  ro = vuMat * vec3 (0., 0., -10.);
  ro.y += tCur;
  ltDir = vuMat * vec3 (0., 0., -1.);
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

const vec4 cHashA4 = vec4 (0., 1., 57., 58.);
const vec3 cHashA3 = vec3 (1., 57., 113.);
const float cHashM = 43758.54;

vec4 Hashv4f (float p)
{
  return fract (sin (p + cHashA4) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 i = floor (p);
  vec2 f = fract (p);
  f = f * f * (3. - 2. * f);
  vec4 t = Hashv4f (dot (i, cHashA3.xy));
  return mix (mix (t.x, t.y, f.x), mix (t.z, t.w, f.x), f.y);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n)) * (1. / 1.9375);
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  float s;
  const vec3 e = vec3 (0.1, 0., 0.);
  s = Fbmn (p, n);
  g = vec3 (Fbmn (p + e.xyy, n) - s, Fbmn (p + e.yxy, n) - s,
     Fbmn (p + e.yyx, n) - s);
  return normalize (n + f * (g - n * dot (n, g)));
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

float DigSeg (vec2 q)
{
  return step (abs (q.x), 0.12) * step (abs (q.y), 0.6);
}

float ShowDig (vec2 q, int iv)
{
  float d;
  int k, kk;
  const vec2 vp = vec2 (0.5, 0.5), vm = vec2 (-0.5, 0.5), vo = vec2 (1., 0.);
  if (iv < 5) {
    if (iv == -1) k = 8;
    else if (iv == 0) k = 119;
    else if (iv == 1) k = 36;
    else if (iv == 2) k = 93;
    else if (iv == 3) k = 109;
    else k = 46;
  } else {
    if (iv == 5) k = 107;
    else if (iv == 6) k = 122;
    else if (iv == 7) k = 37;
    else if (iv == 8) k = 127;
    else k = 47;
  }
  q = (q - 0.5) * vec2 (1.5, 2.2);
  d = 0.;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q.yx - vo);
  k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q.xy - vp);
  k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q.xy - vm);
  k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q.yx);
  k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q.xy + vm);
  k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q.xy + vp);
  k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q.yx + vo);
  return d;
}

float ShowInt (vec2 q, vec2 cBox, float mxChar, float val)
{
  float nDig, idChar, s, sgn, v;
  q = vec2 (- q.x, q.y) / cBox;
  s = 0.;
  if (min (q.x, q.y) >= 0. && max (q.x, q.y) < 1.) {
    q.x *= mxChar;
    sgn = sign (val);
    val = abs (val);
    nDig = (val > 0.) ? floor (max (log (val) / log (10.), 0.)) + 1. : 1.;
    idChar = mxChar - 1. - floor (q.x);
    q.x = fract (q.x);
    v = val / pow (10., mxChar - idChar - 1.);
    if (sgn < 0.) {
      if (idChar == mxChar - nDig - 1.) s = ShowDig (q, -1);
      else ++ v;
    }
    if (idChar >= mxChar - nDig) s = ShowDig (q, int (mod (floor (v), 10.)));
  }
  return s;
}

