/*
   Astroid facts:
  ----------------
	- (a) Also called the tetracuspid because it has four cusps.
	- (b) Curve can be formed by rolling a circle of radius a/4 on the inside of a circle of radius a.
	- (c) The curve can also be formed as the envelop produced when a line segment is moved with
		  each end on one of a pair of perpendicular axes (glissette)
*/

#define res_            iResolution
#define time_           iTime
#define trace_len_      48
#define eps_            0.1

vec2  domain(vec2 uv, float s);
vec2  parametric(float t);
float trace(vec2 p, float t);

void mainImage(out vec4 o, vec2 f) {
	vec2 p = domain(f, 8.);

    // t -> [0., 5.]
    float t = abs(sin(time_)*5.);

    // Astroid traces
    float dtrace = smoothstep(0.008, 0.001, trace(p, t));
    float dtrace_s0 = smoothstep(0.16 + sin(time_)*.1, 0.001, trace(p, t));

    // Axis
    float ax = min(
        smoothstep(0., 0.02, abs(p.x)),
        smoothstep(0., 0.02, abs(p.y))
    );
    // Origin
    float org = smoothstep(0.09, 0., length(p)-.01);

    // Palette 
    vec3 gray   = vec3(.2);
    vec3 white  = vec3(5.);

    // Shading
    vec3 c  = vec3(0.);
    c = mix(c, gray, 1.-ax);
    c = mix(c, white, org);

    c = mix(c, white, dtrace);
    c = mix(c, vec3(0.5, 1., 1.1) + vec3(abs(sin(time_)), abs(sin(time_)), 0.), dtrace_s0);
    c.b += dtrace_s0;

    o = vec4(sqrt(c), 1.);
}

vec2 parametric(float t) {
	float a = 4.;
    return vec2(a*cos(t)*cos(t)*cos(t), a*sin(t)*sin(t)*sin(t));
}

float trace(vec2 p, float t) {
    float d = 1e10;
    float e = 0.;
    for(int i = 0; i < trace_len_; i++) {
    	d  = min(d, distance(p, parametric(t-e)));
        e += eps_; 
    }
    return d;
}

vec2 domain(vec2 uv, float s) {
    return (2. * uv.xy - res_.xy) / res_.y * s;
}

float D3DX_SRGB_to_FLOAT(float val)
{
    if( val < 0.04045 )
        val /= 12.92;
    else
        val = pow((val + 0.055)/1.055,2.4);
    return val;
}

vec3 D3DX_SRGB_to_FLOAT(vec3 val)
{
    return vec3(
        D3DX_SRGB_to_FLOAT(val.r),
        D3DX_SRGB_to_FLOAT(val.g),
        D3DX_SRGB_to_FLOAT(val.b)
    );
}

float signpower(float n, float p){
    return sign(n) * abs(pow(n,p));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = (fragCoord.xy - (iResolution.xy / 2.0));

    float pi = acos(-1.);
    float period = exp2(6.) * pi;
    float x = sin(uv.x / period) * period;
    float y = sin(uv.y / period) * period;
    float t = .0001 + iTime  / 20000.;

    float power = 5.;
    float red   = signpower(sin(( x*x+y*y)*t),power);
    float green = signpower(sin((2.*x*y ) *t),power);
    float blue  = signpower(sin(( x*x-y*y)*t),power);
    //float alpha = (sin(x*y*t*sin(x*t)*sin(y*t))+ 1.)/2.;


	float avg = (red + blue + green) / 3.;
    vec3 gray = vec3(avg, 0, avg) * 0.9;
    //vec3 rbg = vec3(red/1.5, green/2., blue);
    vec3 r;
    float mute = 0.9;
    if(red > 0.) {
       r = vec3(red, 0, 0);
    } else {
       r = vec3(0, -red, -red) * mute;
    }
    vec3 g;
    if(green > 0.) {
       g = vec3(0, green, 0);
    } else {
       g = vec3(-green, 0, -green) * mute;
    }

    vec3 b;
    if(blue > 0.) {
       b = vec3(0, 0, blue);
    } else {
       b = vec3(-blue, -blue, 0) * mute;
    }

    vec3 color = max(max(r,g),b);//r+g+b;// * vec3(0.9,0.8,1);

    color = D3DX_SRGB_to_FLOAT(color);

    fragColor = vec4(color, 1.);
}

/*

A quick experiment with rain drop ripples.

This effect was written for and used in the launch scene of the
64kB intro "H - Immersion", by Ctrl-Alt-Test.

 > http://www.ctrl-alt-test.fr/productions/h-immersion/
 > https://www.youtube.com/watch?v=27PN1SsXbjM

-- 
Zavie / Ctrl-Alt-Test

*/

// Maximum number of cells a ripple can cross.
#define MAX_RADIUS 2

// Set to 1 to hash twice. Slower, but less patterns.
#define DOUBLE_HASH 0

// Hash functions shamefully stolen from:
// https://www.shadertoy.com/view/4djSRW
#define HASHSCALE1 .1031
#define HASHSCALE3 vec3(.1031, .1030, .0973)

float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * HASHSCALE3);
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.xx+p3.yz)*p3.zy);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float resolution = 10. * exp2(-3.*iMouse.x/iResolution.x);
	vec2 uv = fragCoord.xy / iResolution.y * resolution;
    vec2 p0 = floor(uv);

    vec2 circles = vec2(0.);
    for (int j = -MAX_RADIUS; j <= MAX_RADIUS; ++j)
    {
        for (int i = -MAX_RADIUS; i <= MAX_RADIUS; ++i)
        {
			vec2 pi = p0 + vec2(i, j);
            #if DOUBLE_HASH
            vec2 hsh = hash22(pi);
            #else
            vec2 hsh = pi;
            #endif
            vec2 p = pi + hash22(hsh);

            float t = fract(0.3*iTime + hash12(hsh));
            vec2 v = p - uv;
            float d = length(v) - (float(MAX_RADIUS) + 1.)*t;

            float h = 1e-3;
            float d1 = d - h;
            float d2 = d + h;
            float p1 = sin(31.*d1) * smoothstep(-0.6, -0.3, d1) * smoothstep(0., -0.3, d1);
            float p2 = sin(31.*d2) * smoothstep(-0.6, -0.3, d2) * smoothstep(0., -0.3, d2);
            circles += 0.5 * normalize(v) * ((p2 - p1) / (2. * h) * (1. - t) * (1. - t));
        }
    }
    circles /= float((MAX_RADIUS*2+1)*(MAX_RADIUS*2+1));

    float intensity = mix(0.01, 0.15, smoothstep(0.1, 0.6, abs(fract(0.05*iTime + 0.5)*2.-1.)));
    vec3 n = vec3(circles, sqrt(1. - dot(circles, circles)));
    vec3 color = texture(iChannel0, uv/resolution - intensity*n.xy).rgb + 5.*pow(clamp(dot(n, normalize(vec3(1., 0.7, 0.5))), 0., 1.), 6.);
	fragColor = vec4(color, 1.0);
}

