    uv += vec2(sin(iTime)*.1, cos(iTime*3.)*.2);
    color += texture (iChannel1,  uv);

   	fragColor += texture(iChannel0, uv * .2+vec2(.3) )* vec4(uv,0.5+0.5*sin(iTime),1.0);
 	fragColor += color;   
    //fragColor = fragColor*0.5;
    fragColor = returnV(fragColor) / returnF(11.2f);
}

