#define E 2.7182818284
#define GR 1.61803398875

//-----------------UTILITY FUNCTIONS--------------

float saw(float x)
{
    float f = mod(floor(abs(x)), 2.0);
    float m = mod(abs(x), 1.0);
    return f*(1.0-m)+(1.0-f)*m;
}
vec2 saw(vec2 x)
{
    return vec2(saw(x.x), saw(x.y));
}

vec3 saw(vec3 x)
{
    return vec3(saw(x.x), saw(x.y), saw(x.z));
}

vec4 saw(vec4 x)
{
    return vec4(saw(x.x), saw(x.y), saw(x.z), saw(x.w));
}


//-----------------UTILITY MACROS-----------------

#define time ((saw(float(__LINE__)*PI*GR*E))*iTime+1000.0)
#define sphereN(uv) (clamp(1.0-length(uv*2.0-1.0), 0.0, 1.0))
#define clip(x) (smoothstep(0.25, .75, x))
#define TIMES_DETAILED (1.0)
#define angle(uv) (atan(uv.y, uv.x))
#define angle_percent(uv) ((angle(uv)/PI+1.0)/2.0)

#define flux(x) (vec3(cos(x*2.0*PI),cos(4.0*PI/3.0+x*2.0*PI),cos(2.0*PI/3.0+x*2.0*PI))*.5+.5)
#define circle(x) (vec2(cos((x)*2.0*PI),sin(2.0*PI*(x))))

#define rotatePoint(p,n,theta) (p*cos(theta)+cross(n,p)*sin(theta)+n*dot(p,n) *(1.0-cos(theta)))
#define ZIN (vec3(0.0, 0.0, 1.0))

#define MIN_DIM (min(iResolution.x, iResolution.y))

vec2 remap(vec2 uv, vec4 start, float r1, vec4 end, float r2)
{

    vec3 c1 = vec3(start.xy, 0.0);
    vec3 d1 = vec3(start.zw, 0.0);

    vec3 c2 = vec3(end.xy, 0.0);
    vec3 d2 = vec3(end.zw, 0.0);    

    uv = (rotatePoint(vec3((uv+c1.xy)*d1.xy, 0.0), ZIN, r1).xy);
    uv = (rotatePoint(vec3((uv+c2.xy)*d2.xy, 0.0), ZIN, r2).xy);


    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    float scale = 1.0;//4.0*(sin(time)+GR);

    float aspect = iResolution.x/iResolution.y;
    uv *= 2.0;
    uv -= 1.0;

    vec2 uv0 = uv;

    const int max_i = 4;

    float sum = 0.0;
	vec2 detail = vec2(1.0);
    vec2 last = uv;

    for(int i = 0 ; i < max_i; i++)
    {

        float j = float(i)/float(max_i);
        sum += smoothstep(0.0, 1.0/PI, 1.0-abs(uv.y-saw(uv.x*(1.0+sin(time)))))*sqrt(detail.x*detail.y);


        vec4 start = vec4(0.0, 0.0, 1.0, 1.0);
        vec4 end = vec4(circle(j), (2.0/GR-j/GR), 2.0/GR-j/GR);


        float theta1 = time;
        float theta2 = theta1+j;
        vec2 save = uv;
        detail *= abs(cos(uv*2.0*PI));
        uv = remap(sin(uv*2.0*PI), start, theta1, end, theta2);

        last =save;
    }

    float map = sum/float(max_i)*5.0;
    float b = smoothstep(0.0, 1.0/PI/E, saw(map));
    float w = 1.0-smoothstep(0.0, 1.0/PI/E, saw(map+time));
	fragColor = vec4(saw(uv), 0.0, 1.0);
	fragColor = vec4(flux(map)*b+w, 1.0);
}

