//Adapted from
//https://github.com/keijiro/ShaderSketches/blob/master/Rows.glsl

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	const float PI = 3.141592;
	float time = iTime;


    vec2 coord = gl_FragCoord.xy;
    vec2 size = iResolution.xx / vec2(60, 15);
    float y = coord.y / size.y;
    float scr = 3.0 + 3.0 * fract(floor(y) * 12433.34);
    float x = coord.x / size.x + scr * time;

    float t = time * 1.1;
    float t01 = fract(t);
    float phase = floor(x) * 2353.48272 + floor(y) * 2745.32782 + floor(t);

    float h = mix(
        fract(sin(phase    ) * 1423.84),
        fract(sin(phase + 1.0) * 1423.84),
        smoothstep(0.8, 1.0, t01) * 1.3 - smoothstep(0.5, 0.8, t01) * 0.3
    );

    float c1 = (0.4 - abs(0.4 - fract(x))) / 0.8 * size.x;
    float c2 = (h - fract(y)) * size.y;
    float c = clamp(c1, 0.0, 1.0) * clamp(c2, 0.0, 1.0);

    fragColor = vec4(c, c, c, 1);

}

#define MAXSTEPS 64

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 0.005 * (64. * pow(1.1 + sin(5. + (.5 * iTime)), .97));

	vec2 uv = zoom * ((-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y) + vec2(-.745,.186);

    // Mandelbrot Main
    vec2 z = vec2(0.);
    for (int i = 0; i < MAXSTEPS; i++) 
    {
        // z = z^2 + c
        float c = 2.0;
        z = vec2( z.x*z.x - z.y*z.y, c*z.x*z.y ) + uv;
    }

     vec2 col1 = vec2(smoothstep(0., 1., z));
     vec2 col2 = vec2(smoothstep(0., 1., -z));
     vec3 outCol = vec3(col1 + col2 + vec2(.5, .5), 0.98);

     fragColor = vec4(outCol, 1.0);
}

