vec4 Rect(vec2 uv, vec2 pos, float width, float height, vec4 color, out bool isThis)
{
    if((uv.x > (pos.x - (width / 2.0))) && (uv.x < (pos.x + (width / 2.0))) &&
       (uv.y > (pos.y - (height / 2.0))) && (uv.y < (pos.y + (height / 2.0))))
    {
        isThis = true;
        return color;
    }

    return vec4(0.0);
}

bool MouseCheck(vec2 pos, float width, float height)
{
    if((iMouse.x > (pos.x - (width / 2.0))) && (iMouse.x < (pos.x + (width / 2.0))) &&
       (iMouse.y > (pos.y - (height / 2.0))) && (iMouse.y < (pos.y + (height / 2.0))))
    {
        return true;
    }

    return false;
}

