float rayTrace(vec3 o, vec3 r, vec2 uvcloth){
     float t = 0.0;
    for(int i=0; i<64;i++){
        vec3 p;
        vec2 b = uvcloth;
       // clamp(b.x, 0.1,0.8);clamp(b.y, 0.4,0.7);
        float d = cloth(p, b);
        p = o + d*t;
        t+=d * 0.33333;
    }
    return t;
}
vec2 rot_uv(vec2 uv, float angle){
	float rad = KPT180*angle;
    return uv *= mat2(cos(rad), -sin(rad), sin(rad), cos(rad));
}
vec3 drawRectangle (in vec2 uvRectangle, float time, float scale, vec3 segmentColor,out float coefficientScale){


    uvRectangle = 2.0*uvRectangle - 1.0;
    uvRectangle.x *= iResolution.x/iResolution.y;
    uvRectangle.y *= iResolution.y/iResolution.x*3.0;
    uvRectangle.y += 0.7*fract(0.8*cos(0.8*cos((length(uvRectangle.x)*sin(time))))-2.5);
    uvRectangle.x -= 0.1*fract(2.1*sin(cos((length(uvRectangle.y)*sin(time))+0.1))-0.5);
    uvRectangle.x *= 0.32;
    coefficientScale = length(uvRectangle.y);
    uvRectangle = rot_uv(uvRectangle, 45.0);
    uvRectangle *=scale;

    vec3 r = normalize(vec3(uvRectangle,1.0));
    vec3 o = vec3(0.0,0.0,time);
    float t = rayTrace(o, r, uvRectangle);
    vec3 fc = vec3(t);

    if (fc.x<0.99)fc = vec3(0.0);

  	fc=vec3(1.0,1.0,1.0)-fc;

    fc*= segmentColor;
    fc = clamp(fc, 0.0, 1.0);
    return fc;
}

