#define t iTime
const int NUM_ITER = 5;
const int NUM_ITER_FBM = 5;

//const int NUM_ITER = 6;
//const int NUM_ITER_FBM = 3;

void doFractal( inout vec3 r, in int iter);
void finalOutput( inout vec4 c, in vec3 c1, in vec3 c2, in float vin, in vec3 r);

float hash(float n){
    return fract(sin(n) * 4121.15393);
}

float noise( in vec3 x ){
    vec3 p = floor(x);
    vec3 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*157.0 + 113.0*p.z;

    return mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                   mix( hash(n+157.0), hash(n+158.0),f.x),f.y),
               mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                   mix( hash(n+270.0), hash(n+271.0),f.x),f.y),f.z);
}
//Fbm function
float fbm(vec3 n) {
    float total = 0.0, amplitude = 1.0;
    //n.x+=iTime;
    for (int i = 0; i < NUM_ITER_FBM; i++) {
        total += noise(n) * amplitude;
        n += n;
        amplitude *= 0.5;
    }
    return total;
}
mat2 r2(float a){
    float s = sin(a);
    float c = cos(a);
    return mat2(s, -c, c, s);
}


void mainImage( out vec4 outFinal, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    float vinette = 1.-pow(length(uv*uv*uv*uv)*1.01,10.);

    outFinal = vec4(0.0);

    uv.x *= iResolution.x / iResolution.y;

    vec3 r = normalize(vec3(uv, 1.0 - dot(uv, uv)*.5));
    r.xy *= r2(-t*.25);
    r.yz *= r2(t*.5);
    r.zx *= r2(t*.75);

 	doFractal(r, NUM_ITER);

    vec3 c1 = vec3(noise(r*7.),
                   sin(fbm(r*.6)), 
                   cos(noise(r*8.)));

    //r+=abs(.1*t+r*2.23);

    vec3 c2 = vec3(cos(fbm(r*8.+noise(r*5.5))), 
                   cos(fbm(7.*r)), 
                   cos(r*6. - noise(5.*r)));

    finalOutput(outFinal, c1, c2, vinette, r);
}

void finalOutput( inout vec4 c, in vec3 c1, in vec3 c2, in float vin, in vec3 r){
    vec3 gammaVal = vec3(1.0/1.5);
    c =vec4(pow(mix( c2, c1, (noise(2.*r))), gammaVal) ,1.0)*vin;
    gammaVal = vec3(2.5/1.0);
    c = pow(sqrt(c), vec4(gammaVal, 1.0));
}

//The Fractal
void doFractal( inout vec3 rayIN, in int iter ){
    for(int i = 0; i<NUM_ITER; i++){

        rayIN.xz*=r2(t*.16);

        rayIN = abs(rayIN) / dot(rayIN,rayIN);
        rayIN.x = abs(rayIN.x+cos(t*.2)*.75);
        rayIN.x = abs(rayIN.x-.8);

        rayIN.xy = abs(r2(-t*.1)*rayIN.xy);
        rayIN.y = abs(rayIN.y-.5);
        rayIN.y = abs(rayIN.y+.03+sin(t)*.25);

        rayIN.zy = abs(r2(-t*.25)*rayIN.zy);
        rayIN.z = abs(rayIN.z-.5);
        rayIN.z = abs(rayIN.z+.03+sin(-t)*.15);

        /*
        rayIN.xz*=r2(t*.16);

        rayIN = abs(rayIN) / dot(rayIN,rayIN);
        rayIN.x = abs(rayIN.x+cos(t*.2)*.75);
        rayIN.x = abs(rayIN.x-.8);

        rayIN.xy = abs(r2(-t*.1)*rayIN.xy);
        rayIN.y = abs(rayIN.y-.5);
        rayIN.y = abs(rayIN.y+.03+sin(t)*.25);

        rayIN.zy = abs(r2(t*.25)*rayIN.zy);
        rayIN.z = abs(rayIN.z-.5);
        rayIN.z = abs(rayIN.z+.03+sin(-t)*.15);
		*/
    }   
    rayIN = abs(rayIN) / float(NUM_ITER);
}

#define time iTime

vec4 _texture(vec3 pos, float material)
{

    if (material==0.) // ball
    {
    	vec4 col = 0.9+0.4*textureLod(iChannel0, (0.1*(pos).xy + 0.1*(pos).yz * 0.1*pos.zx), 0.0);
        return col * col;
    }
    else if (material==1.) // ground
    {
        return textureLod(iChannel1, (0.1*pos.xy + 0.1*pos.yz + vec2(0.0,time)), 0.0) + vec4(0.0,sin(time),1.0,0.0);
    }
    else if (material == 2.) // sky
    {
        return 0.5*textureLod(iChannel2, 0.01*time + 0.002*pos.xy, 0.0) + 0.5*textureLod(iChannel1, 0.1*time+0.003*pos.yx, 0.0);
    }
    else // default
    {
        return vec4(0.);
    }
}

vec3 sphereCenter()
{
  return vec3(cos(-time), mod(3.*-time,30.)-20., sin(-time)+5.);   
}

vec3 lightPos()
{
    return vec3(sin(time),0.,1.);
}

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

vec2 world_dist(vec3 pos)
{
    vec3 c = sphereCenter();
    vec3 rel_pos = pos - c;
    float spherecenterdist = length(rel_pos);
    float displacement = 0.2*length(_texture(pos,1.));
    float heightfield = pos.y + 1.0 + 0.2*sin(5.*spherecenterdist) / spherecenterdist + displacement;
    float spheredisplacement = 0.5*length(sin(2.0*time) + _texture(1.*rel_pos,0.));
    float spheredist = spherecenterdist - spheredisplacement;
    float dist = smin(heightfield,spheredist,2.9);
    float mat = heightfield < spheredist ? 1. : 0.;
    return vec2(dist,mat);
}

vec3 normal(vec3 pos)
{
    vec3 e = vec3(0.0001,0.0,0.0);
    float d1 = world_dist(pos+e.xyz).x - world_dist(pos-e.xyz).x;
    float d2 = world_dist(pos+e.yxz).x - world_dist(pos-e.yxz).x;
    float d3 = world_dist(pos+e.yzx).x - world_dist(pos-e.yzx).x;
    return normalize(vec3(d1,d2,d3));
}

void rayMarch(vec3 r, vec2 uv, out vec4 fragColor)
{
    // distance to object + material of closest object
    vec2 _d = world_dist(r);

    // distance from camera focal point
    float d = 1.0;

    vec3 rayPos = r * d;

    // march
    for(int march = 0; march <= 128; march++)
    {
        // if more than 20 units from camera...
        if (d > 20.)
        {
            // sky
            fragColor = _texture(vec3(uv,0.),1.);
            return;
        }

        // if less than eps units from object
        if (_d.x < 0.05)
        {
            // calculate illumination
            vec3 lightWorldVec = lightPos()-(rayPos);
            float lightDist = length(lightWorldVec);
            float brightness = 0.5 + (0.25/lightDist) * dot(normal(rayPos),lightWorldVec);
            vec3 relPos = rayPos - sphereCenter();
            float distFromSphere = length(relPos);

            if (_d.y == 0.)
            {
                fragColor = brightness * _texture(relPos,_d.y);
            }
            else
            {
                float _mix = 1./length(distFromSphere);
                fragColor =_mix + brightness * _texture(rayPos, _d.y) + _mix*_mix*_texture(relPos,2.);
            }

            // sample the material of the closest object

            return;
        }

        // otherwise, recalculate distance to closest object
        _d = world_dist(rayPos);

        // move ray to 
		d += 0.7*_d.x;
        rayPos = r * d;
    }
    fragColor.x = 1.;
}

