#define PI 3.14159

float circle(vec2 coord, float radius, float aa)
{
    return smoothstep(radius-aa, radius+aa, sqrt(dot(coord,coord)));
}

float hash(vec2 p)  // replace this by something better
{
    p  = 50.0*fract( p*0.3183099 + vec2(0.71,0.113));
    return -1.0+2.0*fract( p.x*p.y*(p.x+p.y) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= vec2(0.5);

    float pixelSize = 1.0/iResolution.x;
    vec2 uvvign = uv;
    uvvign += vec2(hash(uv*11.17), hash(uv*9.98))*pixelSize * 10.0;
    float vignette = 1.7 - sqrt(dot(uv+uvvign,uv+uvvign));
	uv.y *= iResolution.y / iResolution.x;

    float randomIncl = iMouse.y/iResolution.y * 0.1;
    float loading = max(iMouse.x/iResolution.x-0.9,0.0) * 100.0 + 1.0;
    float loadingThes = 0.8 - iMouse.x/iResolution.x - 0.2;
    float col = 1.0;
    for (int i = 0;i<10;i++)
    {
        float t = float(i) / 10.0 + iTime * 0.2;
        vec2 coord = vec2(cos(t * PI * 2.0) * 0.25, sin(t * PI * 2.0) * 0.05); 

        float radius = sin(t * 21.11) * 0.2+0.8;
        radius *= clamp((float(i)-loadingThes*10.0), 0.0, 1.0);
        radius *= 1.0 + coord.y * 14.0;

        radius *= loading;
        coord.y += sin(t * 3.6 * PI) * randomIncl;
        col = min(col, circle(uv + coord, radius * 0.05, pixelSize));
    }

    fragColor = vec4(0.3,0.3,0.3,1.0) * vignette * col;
}

