vec4 mainimg(in vec4 fragColor, in vec2 fragCoord ) {


    //regulate viewport and FOV settings <1., >=1.>
    vec2 cod = (fragCoord.xy - soff)/min(soff.x,soff.y)*FOV;
    //rotate viewport aim vector and normalize to get pixel x offset vector
    //vec3 vox = normalize(vec3(vpr.y, -vpr.x, 0.));
    vec3 vox = normalize(cross(vpr, vec3(0.0,0.0,1.0)));
    //rotate viewport aim vector and normalize to get pixel y offset vector
    //vec3 voy = normalize(vec3(vpr.xy*vpr.z,length(vpr.xy)));
    vec3 voy = normalize(cross(vpr, vec3(-vpr.y,vpr.x,0.0)));

    //set pxp to the 3d position of current pixel, 1. away from vp origin
    vec3 pxp = normalize(vpp)*(length(vpp)-1.);
    //add pixel x offest to position
    pxp += vox * cod.x;
    //add pixel y offset to position
    pxp += voy * cod.y;

    //calculate vp origin to pixel location vector (for direction)
    //no need to normalize, this is done next
    ray = pxp - vpp;
    //make ray a normalized vec2 with a slope as z
    //these become t (vec2 multiplier) and z (float multiplier)
    ray = ray/length(ray.xy);

    //calculate zero using newton's method
    float cx = gzero();

    //intersection as x and y coords relative to vp origin, and z as pixel depth
    vec3 intc = vec3(ray.xy*cx,g(cx));
    //float depth = distance(intc);

    //absolute intersection position
    vec3 abp = vec3(vpp.xy+cx*ray.xy,0.);
    abp.z = f(abp.xy);

    //finally, set the color as a function of the z (=height=f(intersection))
    //of the final intersection point
    //fragColor = getc(abp.z);
    //fragColor = getc(df(abp.xy,));

    /*Deprecated beyond this point, for now*/

    //if(length(abp.xy - target.xy) < VIEWRAD) {
    if(cx>0.) {
        fragColor = getc(abp.z);
    } else {
        fragColor = vec4(0.,0.,0.,1.);
    }
    return fragColor;
}

void perframe(vec2 fragCoord) {
	//center screen coordinates
    soff = iResolution.xy * 0.5;
    //target for viewport to point at
    target = vec3(0.,0.,0.);
    //set screen x rotation to mouse position (percentage * 2pi)
    va.x = iMouse.x/iResolution.x*TAU;
    va.y = iMouse.y/iResolution.y*PI/1.5-PI/2.;
    //calculate final viewport position from rotation and distance from target
    vpp = vec3(sin(va.y)*vec2(cos(va.x),sin(va.x)),cos(va.y))*VIEWDIST;
    //direction vector from viewport to target
    //vpr = normalize(target-vpp);
    //vpp = vec3(0.0, 0.0, 0.0);
    vpr = normalize(vec3( sin(va.y) * vec2(cos(va.x), sin(va.x)), cos(va.y) ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    perframe(fragCoord);
    #ifdef SS
    fragColor = vec4(0.);
    for(float x = 0.; x < 1.; x += 1./float(SSA)) {
        for(float y = 0.; y < 1.; y += 1./float(SSA)) {
        	fragColor += mainimg(fragColor, fragCoord+vec2(x,y));
    	}
    }
    fragColor /= float(SSA*SSA);
    #else
    fragColor = mainimg(fragColor, fragCoord);
    #endif
}

