void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    time = iTime*0.2;
	vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;

    #ifdef dizzy
    uv *= rot(0.5*(length(uv))*sin(time)+time*0.2);
    #endif

    vec3 cam = vec3(cos(time)*3.0+1.5,1.5,1.5+sin(time)*3.0);
    //cam.y = clamp(0.5+abs(fract(cam.z/6.0)-0.5)*6.0,1.5,2.5);
    vec3 pos = cam;
    vec3 dir = normalize(vec3(uv,-1.0));

    dir.xz *= rotmat(time);

    vec4 box = iBox(pos-1.5, dir, vec3(1.5));
    if (abs(box.x) > 0.01) {
    	pos += dir*max(box.x,0.0)-box.yzw*0.00001;

        vec3 invdir = 1.0/abs(dir);
        vec3 signdir = sign(dir);
        vec3 floorpos = floor(pos);
        vec3 mask = abs(box.yzw);

        float dist = box.x;
        vec3 localpos = pos;
        vec3 localfloor = floorpos;
        float stepsize = 1.0;
        float count = 0.0;
		bool hit = true;
        for (int i = 0; i < steps; i++) {
            if (count > float(detail)||dist > 8.0) break;
            bool len = map(localfloor)<0.0;

            if((max(max(localfloor.x,localfloor.y),localfloor.z) > 2.5||min(min(localfloor.x,localfloor.y),localfloor.z) < -0.5)) {
                if (count < 0.5) {
                    hit = false;
                    break;
                } else {
                    stepsize *= 3.0;
                    count--;
                    floorpos -= localfloor*stepsize/3.0-mask*signdir*stepsize;
                    pos+= signdir*0.00001*mask;
                    localpos = mod(pos/stepsize-signdir*mask,3.0)+signdir*mask;

                    localfloor = floor(localpos);
                }
            } else
            if (!len && count <= float(detail)) {

                stepsize /= 3.0;
                count++;
                localpos = localpos*3.0-localfloor*3.0;
                vec3 pos2d = floor(localpos)*(1.0-mask)+mask*(-sign(dir)+1.0);
                localfloor = pos2d;
                floorpos += localfloor*stepsize;
            } else {


                vec3 dists = abs((signdir*0.5+0.5)-(localpos-localfloor))*invdir;

                float m = min(min(dists.x,dists.y),dists.z);

                mask = step(dists,vec3(m));

                dist += m*stepsize;
                pos += m*dir*stepsize;
                localpos += m*dir;
                localfloor += mask*signdir;
                floorpos += mask*signdir*stepsize;
            }
        }
        if (dist < 7.9&&hit) {
            fragColor = vec4(abs(.5+.5*mask), 1);
        }
    }
    /*if (all(lessThan(fragCoord,vec2(50.0,50.0)))) {

        fragColor = vec4(1.0-num(fragCoord-vec2(37.5,22.5),20.0,int(1.0/iTimeDelta)));
    }*/
}

