const float pi = 3.14159;

vec3 ltDir;
vec2 bsa1, bsa2;
float dstFar, tCur, szRg, rdRm, htRm, rdCl, htCl, wdDr, spShf, htShf, rdLt, htLt;
int idObj;
const int idRm = 1, idCol = 2, idChr = 3, idBks = 4, idShlf = 5, idNm = 6, idLt = 7;

float ObjDf (vec3 p)
{
  vec3 q, qq;
  vec2 w;
  float dMin, d, a, r;
  dMin = dstFar;
  q = p;
  q.xz = mod (q.xz + szRg, 2. * szRg) - szRg;
  r = length (q.xz);
  a = (r > 0.) ? atan (q.z, - q.x) / (2. * pi) : 0.;
  d = max (r - rdRm, - q.y - htRm);
  qq = q;  qq.y -= - htCl;
  d = max (d, length (qq) - rdCl);
  qq = q;  qq.y -= -0.2;
  qq = abs (qq);
  w = vec2 (min (qq.x, qq.z), qq.y) - vec2 (wdDr, htRm - 0.2);
  d = - min (d, min (max (w.x, w.y), 0.));
  if (d < dMin) { dMin = d;  idObj = idRm; }
  qq.xz += step (qq.x, qq.z) * (qq.zx - qq.xz) - vec2 (rdRm, wdDr);
  d = PrCylDf (qq.xzy, 0.3, htRm - 0.2);
  if (d < dMin) { dMin = d;  idObj = idCol; }
  qq = q;  qq.y -= 0.7 - htRm;
  d = PrRCylDf (qq.xzy, 2.5, 0.1, 0.7);
  if (d < dMin) { dMin = d;  idObj = idChr; }
  qq = q;  qq.y -= 1.65 - htRm;
  d = PrCylDf (qq.xzy, 1., 0.25);
  if (d < dMin) { dMin = d;  idObj = idNm; }
  q.xz = Rot2D (q.xz, 2. * pi * ((floor (4. * a) + 0.5) / 4.));
  q.z = abs (q.z);
  d = max (abs (r - rdRm + 0.6) - 0.6, dot (q.xz, bsa2));
  d = max (d, abs (q.y) - htRm);
  if (d < dMin) { dMin = d;  idObj = idBks; }
  d = max (abs (r - rdRm + 0.9) - 0.9, dot (q.xz, bsa1));
  d = max (d, min (abs (mod (q.y + 0.5 * spShf, spShf) -
     0.5 * spShf) - htShf, - dot (q.xz, bsa2)));
  d = max (d, abs (q.y) - htRm);
  if (d < dMin) { dMin = d;  idObj = idShlf; }
  qq = q;  qq.y -= rdCl - htCl - 0.4;
  d = PrCylDf (qq.xzy, 2., 0.15);
  qq = q;  qq.y -= htLt;
  d = min (d, max (PrTorusDf (qq.xzy, 0.15, rdLt), dot (q.xz, bsa1)));
  if (d < dMin) { dMin = d;  idObj = idLt; }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  const vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy),
     ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec3 WoodCol (vec3 p, vec3 n)
{
  float f;
  p *= 4.;
  f = dot (vec3 (Fbm2 (p.zy * vec2 (1., 0.1)),
     Fbm2 (p.zx * vec2 (1., 0.1)), Fbm2 (p.xy * vec2 (1., 0.1))), abs (n));
  return 0.7 * mix (vec3 (0.8, 0.5, 0.3), vec3 (0.55, 0.35, 0.1), f);
}

vec3 FloorCol (vec2 p)
{
  return mix (vec3 (0.8, 0.4, 0.2), vec3 (0.5, 0.25, 0.1),
     Fbm2 (20. * vec2 (1., 0.1) * p.xy)) *
     (0.5 + 0.5 * SmoothBump (0.03, 0.97, 0.01, mod (3. * p.x, 1.)));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 vn, col, q;
  vec2 gbRm, gbBk;
  float dstObj, a, s, bh, r, cRm;
  int idObjT;
  bool isLit;
  rdRm = 18.;
  rdCl = 1.3 * rdRm; 
  htCl = sqrt (rdCl * rdCl - rdRm * rdRm) - htRm - 1.;
  rdLt = 0.9 * rdRm;
  htLt = sqrt (rdCl * rdCl - rdLt * rdLt) - htCl;
  wdDr = 2.5;
  htShf = 0.07;
  spShf = 0.167 * (2. * htRm - htShf);
  bsa1 = vec2 (sin (1. * 2. * pi / 16.), cos (1. * 2. * pi / 16.));
  bsa2 = vec2 (sin (0.97 * 1. * 2. * pi / 16.), cos (0.97 * 1. * 2. * pi / 16.));
  isLit = true;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    if (idObj == idNm) {
      if (ro.y + rd.y * dstObj > 1.9 - htRm - 0.01) {
        ro += rd * dstObj;
        rd = reflect (rd, vec3 (0., 1., 0.));
        ro += 0.01 * rd;
        dstObj = ObjRay (ro, rd);
      }
    }
  }
  if (dstObj < dstFar) {
    ro += rd * dstObj;
    idObjT = idObj;
    vn = ObjNf (ro);
    idObj = idObjT;
    q = ro;
    gbRm = floor (q.xz / (2. * szRg) + 0.5);
    cRm = mod (31. * Fbm2 (gbRm * vec2 (17., 11.)), 1.);
    q.xz = mod (q.xz + szRg, 2. * szRg) - szRg;
    r = length (q.xz);
    a = (r > 0.) ? atan (q.z, - q.x) / (2. * pi) : 0.;
    if (idObj == idRm) {
      a *= 4.;
      if (ro.y < - (htRm - 0.001)) {
        if (r > 2. * wdDr) col = FloorCol ((min (abs (q.x), abs (q.z)) < wdDr) ?
           ((abs (q.x) < abs (q.z)) ? ro.xz : ro.zx) :
           vec2 (r, 20. * (mod (a, 1.) - 0.5)));
        else col = HsvToRgb (vec3 (cRm, 1., 0.7)) *
           (1. - 0.1 * smoothstep (0.4, 0.6, Fbm2 (3. * ro.xz)));
        s = mix (1. - 0.3 * smoothstep (0.3, 0.6, r / rdRm),
           0.8 + 0.2 * smoothstep (0.1, 0.3, abs (0.5 - mod (a - 0.5, 1.))),
           smoothstep (0.7, 0.85, r / rdRm));
        col *= s;
        isLit = false;
      } else if (r > rdRm + 0.001) {
        col = vec3 (0., 0., 0.3 + 0.5 * (htRm + ro.y) / (2. * htRm - 0.4));
        isLit = false;
      } else if (ro.y < htRm - 0.4) {
        col = vec3 (0.6, 0.6, 0.7);
        if (r < rdRm)
           vn.xz = Rot2D (vn.xz, 0.5 * (mod (100. * mod (a, 1.), 1.) - 0.5));
      } else if (ro.y < htRm + 1.001) {
        col = vec3 (0.6, 0.6, 0.7);
      } else {
        s = mix (1. - 0.3 * smoothstep (0.4, 0.7, r / rdRm),
           0.7 + 0.3 * smoothstep (0.2, 0.3, abs (0.5 - mod (a - 0.5, 1.))),
           smoothstep (0.75, 0.95, r / rdRm));
        col = vec3 (0., 0., 0.3) + vec3 (0.4, 0.4, 0.3) * s;
        s = 0.;
        rd = (normalize (q) + vec3 (1., 0.7, 0.3) +
           0.1 * normalize (vec3 (gbRm.x, 0.1, gbRm.y)));
        for (int j = 0; j < 19; j ++) rd = abs (rd) / dot (rd, rd) - 0.9;
        s += min (1., 0.5e-3 * pow (min (6., length (rd)), 5.));  
        col += s * vec3 (0.4, 0.4, 0.15);
        isLit = false;
      }
      if (isLit) vn = VaryNf (100. * ro, vn, 2.);
    } else if (idObj == idCol) {
      a = abs (1. - 2. * mod (atan (vn.z, vn.x) / (2. * pi), 1.));
      col = HsvToRgb (vec3 (cRm, 1., 0.9)) *
         (0.8 + 0.2 * smoothstep (0.3, 0.7,
         Fbm2 (gbRm + vec2 (5. * a, 10. * ro.y + 0.3 * sin (8. * pi * a)))));
    } else if (idObj == idChr) {
      col = vec3 (0.3, 0.2, 0.);
      if (r > 0.2) col *=
         0.4 + 0.6 * SmoothBump (-0.4, 0.4, 0.05, mod (4. * a + 0.5, 1.) - 0.5);
    } else if (idObj == idBks) {
      a = 120. * mod (mod (4. * a + 1./8., 4.), 4.);
      gbBk = floor (vec2 (q.y / spShf, a));
      bh = (0.7 + 0.3 * Fbm2 ((gbRm + gbBk) * vec2 (19., 31.))) * spShf;
      q.y = mod (q.y, spShf);
      if (q.y < bh) { 
        s = mod (a, 1.);
        col = vec3 (HsvToRgb (vec3 (mod (cRm +
           0.4 * (Fbm2 (gbBk * vec2 (17., 11.)) - 0.5), 1.), 1.,
           0.9 * SmoothBump (0.08, 0.92, 0.01, 0.1 + 0.9 * s) *
           (1. - 0.3 * step (abs (abs (q.y / bh - 0.5) - 0.35), 0.02)))));
        vn.xz = Rot2D (vn.xz, 2. * (s - 0.5));
        vn = VaryNf (20. * ro, vn, 1.);
      } else col = vec3 (0.1);
    } else if (idObj == idShlf) {
      q = vec3 (5. * (mod (4. * a, 1.) - 0.5), ro.y, r);
      col = WoodCol (q, vn);
    } else if (idObj == idLt) {
      col = vec3 (1., 1., 0.6) * 0.5 * (1. - vn.y);
      isLit = false;
    } else if (idObj == idNm) {
      col = vec3 (0.8, 0.8, 0.);
      q.xy = vec2 (-2. * (mod (3. * a + 0.5 * tCur + 0.5, 1.) - 0.5),
         ro.y - (1.65 - htRm));
      if (abs (q.x) < 0.6 && abs (q.y) < 0.2) {
        if (ShowInt (vec2 (q.x - 0.45, q.y + 0.13),
           vec2 (1., 0.25), 4., dot (mod (vec2 (42., 24.) + gbRm, 100.),
           vec2 (100., 1.))) != 0.) col = vec3 (0.8, 0., 0.);
      }
      isLit = false;
    }
    if (isLit) col = col * (0.5 + 0.5 * max (dot (vn, ltDir), 0.) +
       0.1 * pow (max (0., dot (ltDir, reflect (rd, vn))), 64.));
    col = mix (col, vec3 (0.1, 0.1, 0.15), smoothstep (0.6, 1., dstObj / dstFar));
  } else {
    col = 0.5 * vec3 (0.6, 0.6, 0.7);
  }
  return clamp (col, 0., 1.);
}

vec3 TrackPath (float t)
{
  vec3 p;
  float s, cCount;
  t *= 10.;
  p.y = htRm * (0.1 * SmoothBump (0.2, 0.8, 0.1, mod (0.08 * t, 1.)) + 0.1);
  s = mod (t, 11.);
  if (s < 7.) p.xz = (s < 4.) ? vec2 (0., s) : vec2 (s - 4., 4.);
  else p.xz = (s < 9.) ? vec2 (3., 11. - s) : vec2 (12. - s, 2.);
  cCount = floor (t / 11.);
  if (mod (cCount, 2.) == 0.) p.x *= -1.;
  else p.x -= 1.;
  p.z += 2. * cCount;
  p.xz *= 2. * szRg;
  return p;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, fpF, fpB, vd;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, spd;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  szRg = 20.;
  htRm = 4.;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.7 * pi * mPtr.y;
  }
  spd = 0.02;
  fpF = TrackPath (spd * tCur + 0.02);
  fpB = TrackPath (spd * tCur - 0.02);
  ro = 0.5 * (fpF + fpB);
  vd = fpF - fpB;
  ori = vec2 (el, az - (atan (vd.z, vd.x) - 0.5 * pi));
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  rd = vuMat * normalize (vec3 (uv, 1.7));
  dstFar = 400.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRCylDf (vec3 p, float r, float rt, float h)
{
  vec2 dc;
  float dxy, dz;
  dxy = length (p.xy) - r;
  dz = abs (p.z) - h;
  dc = vec2 (dxy, dz) + rt;
  return min (min (max (dc.x, dz), max (dc.y, dxy)), length (dc) - rt);
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

const vec4 cHashA4 = vec4 (0., 1., 57., 58.);
const vec3 cHashA3 = vec3 (1., 57., 113.);
const float cHashM = 43758.54;

vec4 Hashv4f (float p)
{
  return fract (sin (p + cHashA4) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec4 t;
  vec2 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv4f (dot (ip, cHashA3.xy));
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n)) * (1. / 1.9375);
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  float s;
  const vec3 e = vec3 (0.1, 0., 0.);
  s = Fbmn (p, n);
  g = vec3 (Fbmn (p + e.xyy, n) - s, Fbmn (p + e.yxy, n) - s,
     Fbmn (p + e.yyx, n) - s);
  return normalize (n + f * (g - n * dot (n, g)));
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

float DigSeg (vec2 q)
{
  return step (abs (q.x), 0.12) * step (abs (q.y), 0.6);
}

#define DSG(q) k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q)

float ShowDig (vec2 q, int iv)
{
  float d;
  int k, kk;
  const vec2 vp = vec2 (0.5, 0.5), vm = vec2 (-0.5, 0.5), vo = vec2 (1., 0.);
  if (iv == -1) k = 8;
  else if (iv < 2) k = (iv == 0) ? 119 : 36;
  else if (iv < 4) k = (iv == 2) ? 93 : 109;
  else if (iv < 6) k = (iv == 4) ? 46 : 107;
  else if (iv < 8) k = (iv == 6) ? 122 : 37;
  else             k = (iv == 8) ? 127 : 47;
  q = (q - 0.5) * vec2 (1.5, 2.2);
  d = 0.;
  kk = k;
  DSG (q.yx - vo);
  DSG (q.xy - vp);
  DSG (q.xy - vm);
  DSG (q.yx);
  DSG (q.xy + vm);
  DSG (q.xy + vp);
  DSG (q.yx + vo);
  return d;
}

float ShowInt (vec2 q, vec2 cBox, float mxChar, float val)
{
  float nDig, idChar, s, sgn, v;
  q = vec2 (- q.x, q.y) / cBox;
  s = 0.;
  if (min (q.x, q.y) >= 0. && max (q.x, q.y) < 1.) {
    q.x *= mxChar;
    sgn = sign (val);
    val = abs (val);
    nDig = (val > 0.) ? floor (max (log (val) / log (10.), 0.)) + 1. : 1.;
    idChar = mxChar - 1. - floor (q.x);
    q.x = fract (q.x);
    v = val / pow (10., mxChar - idChar - 1.);
    if (sgn < 0.) {
      if (idChar == mxChar - nDig - 1.) s = ShowDig (q, -1);
      else ++ v;
    }
    if (idChar >= mxChar - nDig) s = ShowDig (q, int (mod (floor (v), 10.)));
  }
  return s;
}

