#define res_            iResolution
#define time_           iTime
#define trace_len_      830
#define eps_            0.01

vec2  domain(vec2 uv, float s);
vec2  parametric(float t);
float trace(vec2 p, float t);

void mainImage(out vec4 o, vec2 f) {
	vec2 p = domain(f, 8.);

    // t -> [0., 5.]
    float t = abs(sin(time_)*5.);
    // Distance from point in space to point at parametrized curve
    // on time t_i and trace_len_ points backward in time giving 
    // a path of points to draw circles at (curves "trace")
    float dtrace = smoothstep(0.08, 0., trace(p, t));

    // x - y diagonal line acting as many times intersected asimptote
    float asymptote = smoothstep(0.05, 0., abs(p.x-p.y));
    // Axis
    float ax = min(
        smoothstep(0., 0.02, abs(p.x)),
        smoothstep(0., 0.02, abs(p.y))
    );
    // Origin
    float org = smoothstep(0.09, 0., length(p)-.02);

    // Palette 
    vec3 orange = vec3(257., 111., 24.) / 255.;
    vec3 gray   = vec3(.2);
    vec3 white  = vec3(5.);
    vec3 red    = vec3(1., 0., 0.);

    // Shading
    vec3 c  = vec3(0.);
    c = mix(c, gray, asymptote);
    c = mix(c, orange, dtrace);
    c = mix(c, gray, 1.-ax);
    c = mix(c, red, org);

    o = vec4(sqrt(c), 1.);
}

vec2 parametric(float t) {
	float n = 22.;
    return vec2(t + cos(n*t) / t, t + sin(n*t) / t);
}

float trace(vec2 p, float t) {
    float d = 1e10;
    float e = 0.;
    for(int i = 0; i < trace_len_; i++) {
    	d  = min(d, distance(p, parametric(t-e)));
        e += eps_; 
    }
    return d;
}

vec2 domain(vec2 uv, float s) {
    return (2. * uv.xy - res_.xy) / res_.y * s;
}

