// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

/*
  Hexagonal grid line of sight (all cells that line intersects - unlike Bresenham
  which only shows one cell per row/column).

  The hexagonal grid tutorial http://www.redblobgames.com/grids/hexagons
  covers everything except the line-of-sight problem.

  Mouse moves line end.
*/

#define SQRT3 1.7320508

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./SQRT3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (SQRT3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float HexEdgeDist (vec2 p)
{
  p = abs (p);
  return (SQRT3/2.) - p.x + 0.5 * min (p.x - SQRT3 * p.y, 0.);
}

bool HexOnLine (vec2 v, vec2 b)
{
  vec3 w;
  w = abs (vec3 (v.x, 0.5 * (v.x + SQRT3 * v.y), 0.5 * (v.x - SQRT3 * v.y)));
  return (abs (b.x * v.y - b.y * v.x) < max (w.x, max (w.y, w.z)));
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  vec4 mPtr;
  vec3 col;
  vec2 canvas, uv, va, cId, pMid, s1, s2, ds, db, r;
  float tCur, scale, dEdge, lenLn, d, a;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  scale = 10.;
  a = 0.3 * tCur;
  va = vec2 (canvas.x / canvas.y, 1.);
  r = vec2 (cos (a), sin (a)) * va;
  s1 = (mPtr.z > 0.) ? 2. * mPtr.xy * va : - 0.4 * r;
  s2 = s1 + 1.3 * r;
  ds = s2 - s1;
  lenLn = length (ds);
  cId = PixToHex (scale * uv);
  pMid = HexToPix (cId) / scale;
  db = pMid - s1;
  d = (lenLn > 0.) ? dot (db, ds) / lenLn : 0.;
  dEdge = HexEdgeDist (scale * (uv - pMid));
  if (all (equal (PixToHex (scale * s1), cId)) ||
      all (equal (PixToHex (scale * s2), cId)) ||
      d > 0. && d < lenLn && HexOnLine (ds, scale * db)) {
    col = HsvToRgb (vec3 (1., 1. - 1.2 * dEdge,
       min (1. + 0.2 * sin (50. * dEdge), 1.)));
    col = mix (col, vec3 (0., 0., 1.), step (length (uv - pMid), 0.015));
  } else col = vec3 (0.2);
  col = mix (vec3 (0., 1., 0.), col, step (0.04, dEdge));
  db = uv - s1;
  d = (lenLn > 0.) ? dot (db, ds) / lenLn : 0.;
  if (d > 0. && d < lenLn) col = mix (col, vec3 (1., 1., 0.),
     (1. - smoothstep (0.003, 0.004, sqrt (max (0., dot (db, db) - d * d)))));
  if (mPtr.z > 0. && scale * length (uv - 2. * mPtr.xy * va) < 0.15) col *= 2.;
  fragColor = vec4 (clamp (col, 0., 1.), 1.);
}

