	//return abs(sdBox(p, vec3(0.9, .10, 0.82))); //long jagged rocks

    return dot(p, p);

    // Other metrics to try.

    //p = abs(fract(p)-.5);
    //return dot(p, vec3(.5));

    //p = abs(fract(p)-.5);
    //return max(max(p.x, p.y), p.z);

