void mainImage( out vec4 fragColor, in vec2 fragCoord ){


	// Screen coordinates.
	vec2 uv = (fragCoord - iResolution.xy*0.5)/iResolution.y;

	// Camera Setup.
	vec3 lookAt = vec3(0.0, 0.0, iTime*6.);  // "Look At" position.
	vec3 camPos = lookAt + vec3(0.0, 0.1, -0.5); // Camera position, doubling as the ray origin.

    // Light positioning. One is a little behind the camera, and the other is further down the tunnel.
 	vec3 light_pos = camPos + vec3(0.0, 0.125, 4.125);// Put it a bit in front of the camera.
	vec3 light_pos2 = camPos + vec3(0.0, 0.0, 8.0);// Put it a bit in front of the camera.

	// Using the Z-value to perturb the XY-plane.
	// Sending the camera, "look at," and two light vectors down the tunnel. The "path" function is 
	// synchronized with the distance function. Change to "path2" to traverse the other tunnel.
	lookAt.xy += path(lookAt.z);
	camPos.xy += path(camPos.z);
	light_pos.xy += path(light_pos.z);
	light_pos2.xy += path(light_pos2.z);

    // Using the above to produce the unit ray-direction vector.
    float FOV = PI/3.; // FOV - Field of view.
    vec3 forward = normalize(lookAt-camPos);
    vec3 right = normalize(vec3(forward.z, 0., -forward.x )); 
    vec3 up = cross(forward, right);

    // rd - Ray direction.
    vec3 rd = normalize(forward + FOV*uv.x*right + FOV*uv.y*up);

    // Swiveling the camera from left to right when turning corners.
    rd.xy = rot2( path(lookAt.z).x/32. )*rd.xy;

    // Standard ray marching routine.
    float t = trace(camPos, rd);

    // The final scene color. Initated to black.
	vec3 sceneCol = vec3(0.);

	// The ray has effectively hit the surface, so light it up.
	if(t < FAR){


    	// Surface position and surface normal.
	    vec3 sp = t * rd+camPos;
	    vec3 sn = calcNormal(sp);

        // Texture scale factor.
        const float tSize0 = 1./1.; 
        const float tSize1 = 1./1.;

    	// Texture-based bump mapping.
	    //if (sp.y<-(FH-0.005)) sn = doBumpMap(iChannel1, sp*tSize1, sn, 0.025); // Floor.
	    //else sn = doBumpMap(iChannel0, sp*tSize0, sn, 0.025); // Walls.

        sn = doBumpMap(iChannel1, sp*tSize0, sn, 0.02);
        //sn = doBumpMap(sp, sn, 0.01);

	    // Ambient occlusion.
	    float ao = calculateAO(sp, sn);

    	// Light direction vectors.
	    vec3 ld = light_pos-sp;
	    vec3 ld2 = light_pos2-sp;

        // Distance from respective lights to the surface point.
	    float lDdist = max(length(ld), 0.001);
	    float lDdist2 = max(length(ld2), 0.001);

    	// Normalize the light direction vectors.
	    ld /= lDdist;
	    ld2 /= lDdist2;

	    // Light attenuation, based on the distances above. In case it isn't obvious, this
        // is a cheap fudge to save a few extra lines. Normally, the individual light
        // attenuations would be handled separately... No one will notice, or care. :)
	    float atten = 1./(1. + lDdist*.125 + lDdist*lDdist*.05);
        float atten2 =  1./(1. + lDdist2*.125 + lDdist2*lDdist2*.05);

    	// Ambient light.
	    float ambience = 0.75;

    	// Diffuse lighting.
	    float diff = max( dot(sn, ld), 0.0);
	    float diff2 = max( dot(sn, ld2), 0.0);

    	// Specular lighting.
	    float spec = pow(max( dot( reflect(-ld, sn), -rd ), 0.0 ), 64.);
	    float spec2 = pow(max( dot( reflect(-ld2, sn), -rd ), 0.0 ), 64.);

    	// Curvature.
	    float crv = clamp(curve(sp, 0.125)*0.5+0.5, .0, 1.);

	    // Fresnel term. Good for giving a surface a bit of a reflective glow.
        float fre = pow( clamp(dot(sn, rd) + 1., .0, 1.), 1.);


        vec3 texCol = tex3D(iChannel0, sp*tSize0, sn);

        //texCol = min(texCol*1.5, 1.);
        //texCol = vec3(1)*dot(texCol, vec3(0.299, 0.587, 0.114));
        //texCol = smoothstep(-.0, .6, texCol); // etc.

        //texCol = texCol*vec3(1., .5, .2); 
        int id = cellTileID(sp/2.5);
        if(id == 4) texCol = texCol*vec3(1., .5, .3); 
        if(id == 3) texCol = texCol*.5 + texCol*vec3(.5, .25, .15); 


    	// Darkening the crevices. Otherwise known as cheap, scientifically-incorrect shadowing.	
	    float shading =  crv*0.75+0.25; 

        //Dain: put some green in the cracks
        vec3 crevColor = tex3D(iChannel2, sp*tSize0, sn);
        texCol = mix(texCol, crevColor, pow(1.0 - crv, 16.0));
       // ambience *= crv;
    	// Combining the above terms to produce the final color. It was based more on acheiving a
        // certain aesthetic than science.
        //
        // Shiny.
        sceneCol = (texCol*(diff + ambience + spec) + spec*vec3(.7, .9, 1))*atten;
        sceneCol += (texCol*(diff2 + ambience + spec2) + spec2*vec3(.7, .9, 1))*atten2;
        //
        // Other combinations:
        //
        // Glow.
        //float gr = dot(texCol, vec3(0.299, 0.587, 0.114));
        //sceneCol = (gr*(diff + ambience*0.25) + spec*texCol*2. + fre*crv*texCol.zyx*2.)*atten;
        //sceneCol += (gr*(diff2 + ambience*0.25) + spec2*texCol*2. + fre*crv*texCol.zyx*2.)*atten2;

        // Shading.
        sceneCol *= shading*ao;

        //Dain: turned off the lines to show the smooth surface
        // Drawing the lines on the surface.      
       // sceneCol *= clamp(abs(curve(sp, 0.035)), .0, 1.)*.5 + 1.;  // Glow lines.
       // sceneCol *= 1. - smoothstep(0., 4., abs(curve(sp, 0.0125)))*vec3(.82, .85, .88); // Darker.


	}

    // Some simple post processing effects.
    //float a = dot(sceneCol, vec3(0.299, 0.587, 0.114));
    //sceneCol = min(vec3(a*3., pow(a, 2.5)*2., pow(a, 6.)), 1.); // Fire palette.
    //sceneCol = floor(sceneCol*15.999)/15.; // Oldschool effect.  
    // Oldschool hatching effect. Uncomment the "ch" function to use this one.
    //sceneCol = ch(clamp(sceneCol, 0., 1.), fragCoord); 

	fragColor = vec4(sqrt(clamp(sceneCol, 0., 1.)), 1.0);


}

const float PI = 3.14159265358979323;
vec2 vTexCoord;

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float srand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453) * 2.0 - 1.0;
}

float rand(float seed){
    vec2 co = vec2(seed, seed + 100.0);
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float srand(float seed){
    vec2 co = vec2(seed, seed + 100.0);
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453) * 2.0 - 1.0;
}

vec3 getRandomVector(vec2 co){

    vec3 dir = vec3(srand(co + vec2(0.3, -0.3)), srand(co + vec2(-0.2, -0.1)), srand(co + vec2(-0.1, -0.4)));
    float r = rand(co);

    return r * normalize(dir);

}

vec2 getRandomVector2(vec2 co) {
    vec2 dir = vec2(srand(co + vec2(0.3, -0.3)), srand(co + vec2(-0.2, -0.1)));
    float r = rand(co);

    return r * normalize(dir);
}

struct Ray {
    vec3 o; // origin
    vec3 d; // direction looking at
};

struct Sphere {
    // geometry
    float r; // radius
    vec3 c;  // center

    // material
    int mat; // 0: lambert, 1: metal
    vec3 albedo; // for attenuation
    float fuzz;  // for metal reflection
    float ref_idx; // for dielectric refraction ratio
};

struct Intersection {
    bool hit;
    vec3 p;
    vec3 n;
    vec3 color;
    float t;
    Sphere rec;
};

void scatterLambert(inout Ray R, in Intersection I){
    R.o = I.p;
    R.d = I.n + getRandomVector(vTexCoord + R.d.yx * 10.0);
}

void scatterMetal(inout Ray R, in Intersection I) {
    R.o = I.p;
    R.d = reflect(R.d, I.n) + I.rec.fuzz * getRandomVector(vTexCoord + R.d.yx * 10.0);;
}

float schlick(float cosine, float ref_idx) {
    float r0 = (1.0 - ref_idx) / (1.0 + ref_idx);
    r0 = r0 * r0;
    return r0 + (1.0 - r0) * pow(1.0 - cosine, 5.0);
}

void scatterDielectric(inout Ray R, in Intersection I) {
    vec3 outward;
    vec3 reflected = reflect(R.d, I.n);
    float ni;
    float cosine;
    float reflect_prob;

    if (dot(R.d, I.n) > 0.0) {
        outward = - I.n;
        ni = I.rec.ref_idx;
        cosine = dot(R.d, I.n) / length(R.d);
        cosine = sqrt(1.0 - I.rec.ref_idx * I.rec.ref_idx * (1.0 - cosine * cosine));
    } else {
        outward = I.n;
        ni = 1.0 / I.rec.ref_idx;
        cosine = - dot(R.d, I.n) / length(R.d);
    }

    vec3 refracted = refract(R.d, outward, ni);

    if (length(refracted) > 0.0) {
        reflect_prob = schlick(cosine, I.rec.ref_idx);
    } else {
        reflect_prob = 1.0;
    }

    R.o = I.p;
    if (rand(vTexCoord + R.d.yx * 10.0) < reflect_prob) {
        R.d = reflected;
    } else {
        R.d = refracted;
    }
}

void initSphere(inout Sphere s, in float r, in vec3 c) {
    s.r = r;
    s.c = c;
}

void setMaterial(inout Sphere s, in int m, in vec3 col, in float f, in float ref) {
    s.mat = m;
    s.albedo = col;
    s.fuzz = f;
    s.ref_idx = ref;
}

void calcColor(inout Ray R, inout Intersection I) {

    I.color *= I.rec.albedo;

    if (I.rec.mat == 0) {
        scatterLambert(R, I);
    } else if (I.rec.mat == 1) {
        scatterMetal(R, I);
    } else if (I.rec.mat == 2) {
        scatterDielectric(R, I);
    }

}

void intersectSphere(inout Ray R, in Sphere S, inout Intersection I){

    vec3 L = R.o - S.c;

    float a = dot(R.d, R.d);
    float b = dot(L, R.d);
    float c = dot(L, L) - S.r * S.r;

    float discriminant = b * b - a * c;

    if (discriminant > 0.0) {
        float t = (- b - sqrt(discriminant))/a;
        if (t > 0.001 && t < I.t) {

            I.p = R.o + t * R.d;
            I.n = normalize(I.p - S.c);

            I.t = t;
            I.hit = true;
            I.rec = S;

            return;
        }
        t = (- b + sqrt(discriminant))/a;
        if (t > 0.001 && t < I.t) {

            I.p = R.o + t * R.d;
            I.n = normalize(I.p - S.c);

            I.t = t;
            I.hit = true;
            I.rec = S;

            return;
        }
    }
}

struct Camera {
    vec3 pos;
    vec3 lookAt;
    vec3 vUp;
    float fov;
    float aspect;
    float aparture;
    float focus_dist;

    vec3 lower_left_corner;
    vec3 horizontal;
    vec3 vertical;
    vec3 u, v, w;
    float lens_radius;
};

void initCamera(inout Camera C){
    C.lens_radius = C.aparture * 0.5;
    float theta = C.fov * PI / 180.0;
    float half_height = tan(theta * 0.5);
    float half_width = C.aspect * half_height;

    C.w = normalize(C.pos - C.lookAt);
    C.u = normalize(cross(C.vUp, C.w));
    C.v = cross(C.w, C.u);

    C.lower_left_corner = C.pos - half_width * C.focus_dist * C.u - half_height * C.focus_dist * C.v - C.focus_dist * C.w;
    C.horizontal = 2.0 * half_width * C.focus_dist * C.u;
    C.vertical = 2.0 * half_height * C.focus_dist * C.v;

}

void createRay(inout Ray R, in Camera C, in float s, in float t) {
    vec2 rd = C.lens_radius * getRandomVector2(vTexCoord * vec2(s, t));
    vec3 offset = C.u * rd.x + C.v * rd.y;

    R.o = C.pos + offset;
    R.d = C.lower_left_corner + s * C.horizontal + t * C.vertical - R.o;

}

Sphere earth;
Sphere slist[8];

bool hitCheck(inout Ray R, inout Intersection I) {
    I.hit = false;
    I.p = vec3(0.0);
    I.n = vec3(0.0);
    I.t = 1.0e+30;

    intersectSphere(R, earth, I);

    for (int i = 0; i < 8; i++) {
        intersectSphere(R, slist[i], I);
    }

    return I.hit;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 p = fragCoord.xy / iResolution.xy;
	vTexCoord = fragCoord.xy;

    initSphere(earth, 100.0, vec3(0.0, - 102.0, 0.0));
    setMaterial(earth, 1, vec3(0.6, 0.6, 0.6), 0.1, 0.0);

    float st = 2.0 * PI / 8.0;
    float t = iTime;

    for (int i = 0; i < 8; i++) {


        if (mod(float(i), 4.0) == 1.0) {

            initSphere(slist[i],
                       0.4,
                       vec3(cos(t) * sin(3.0 * t + st * float(i)),
                            sin(t) * cos(4.0 * t + st * float(i)),
                            cos(t + st *float(i))) * 2.0
                       );

            setMaterial(slist[i], 0, vec3(0.2, 0.9, 1.0), 0.0, 0.0);

        } else if (mod(float(i), 4.0) == 3.0) {

            initSphere(slist[i],
                       0.4,
                       vec3(cos(t) * sin(3.0 * t + st * float(i)),
                            sin(t) * cos(4.0 * t + st * float(i)),
                            cos(t + st * float(i))) * 2.0
                       );

            setMaterial(slist[i], 0, vec3(1.0, 0.3, 0.3), 0.0, 0.0);

        } else {

            initSphere(slist[i],
                       0.2 * (sin(t) + 1.0) + 0.3,
                       vec3(cos(t) * sin(4.0 * t + st * float(i)),
                            sin(t) * cos(4.0 * t + st *float(i)),
                            cos(t + st * float(i))) * 2.0
                       );

            setMaterial(slist[i], 2, vec3(1.0), 0.0, 1.5);


        }


    }

    float size = 2.0 / min(iResolution.x, iResolution.y);

    float r = 4.0 + 3.5 * sin(t * 0.8);

    Camera cam;
    cam.pos = vec3(r * sin(t), 0.5 * cos(t * 0.5) + 0.5, r * cos(t));
    cam.lookAt = vec3(0.0, 0.0, 0.0);
    cam.vUp = vec3(0.0, 1.0, 0.0);
    cam.fov = 60.0;
    cam.aspect = 4.0/3.0;
    cam.focus_dist = 4.0;
    cam.aparture = 0.05;

    initCamera(cam);

    Ray ray;
    Intersection inter;

    vec3 destColor = vec3(0.0);

    float rs = 1.1;

    for (int i = 0; i < 5; i++) {
        inter.color = vec3(1.0);

        createRay(ray, cam, p.x + size * srand(rs*t + 0.5), p.y + size * srand(rs*t));

        rs *= 1.2;

        if (hitCheck(ray, inter)) {

            int counter = 0;

            for (int i = 0; i < 10; i++) {
                if (hitCheck(ray, inter)) {
                    calcColor(ray, inter);
                } else {
                	break;
                }
            }

        }

        destColor += inter.color;

    }

    destColor /= 5.0;

    fragColor = vec4(sqrt(destColor.r), sqrt(destColor.g), sqrt(destColor.b), 1.0);
}

