}

float rayMarch(vec3 origin, vec3 ray) {
    float t = 0.0;
    for (int i = 0; i < 64; i++) {
    	vec3 p = origin + ray + t;
        float d = dist(p);
        t += d * 0.5;
    }
	return t;
}
