float sphereRadius = 0.25;
const int iterations = 32;


/*Takes a position in 3D space and tels you the distance to the nearest surface of the object
that the function defines

If this float returns:
1 - outside the object
0 - on the surface of the object
-1 - inside the object */

float map(vec3 p)
{

    //transform the space into a repeating coordinate system
    vec3 q = fract(p) * 2.0 - 1.0;


	return length(q) - sphereRadius;
}

//The marching algorithm 
//needs an origin and a ray to march along
 float rayMarch(vec3 o, vec3 r)
{
	//the intersection, this is what's determined when marching
    float t = 0.0;

    for(int i = 0; i < iterations; ++i)
    {
        vec3 p = o + r * t; //where we are along the ray
        float d = map(p);
        t += d * 0.5; //accumulate t until we find the intersection
    }
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    //Transform texcoords so they go from -1 - 1
    uv = uv * 2.0 - 1.0;

    //Fix aspect ratio or image will blur
    uv.x *= iResolution.x / iResolution.y;

    //Create the ray we will march on
    //must be normalized or the ray will poke through geometry when close to camera
    vec3 r = normalize(vec3(uv, 1.0)); //add z value (FOV) to project uv into 3D space  

    vec3 o = vec3(0.0, 0.0, iTime);//set origin back from sphere

    float t = rayMarch(o, r); //find the intersection


    //the further away an object is, the darker it gets (VERY HACKEY CODE)                   
    float fog = 1.0 / (1.0 + t * t * 0.1);

    vec3 fc = vec3(fog);

	fragColor = vec4(fc,1.0);
}

float triangle(float x) {
	x -= 1.0;
    x /= 4.0;
    x -= floor(x);
    x *= 4.0;
    x -= 2.0;
	x = abs(x);
    x -= 1.0;
    return x;
}

float square(float x) {
    x /= 4.0;
    x -= floor(x);
	return 1.0 - 2.0 * step(0.5, x);
}

vec3 palette(float i) {
	float x = 0.5 + 0.5*triangle(i / 30.0);
    return vec3(2.0 * x, 2.0 * x - 0.5, 2.0 * x - 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pos = (2.0 * fragCoord.xy - iResolution.xy) / max(iResolution.x, iResolution.y);
    vec2 z0 = vec2(-0.905, 0.25);
    //vec2 z0 = vec2(-1.748, 0.001);
    float zoom = 1.0 * exp(7.0 * smoothstep(0.0, 1.0, 0.5 + 0.5 * triangle(iTime / 20.0 + 1.0)));
	vec2 z = 2.0 / zoom * pos + z0;

    float t = iTime / 2.0;
    float r = length(pos);
    float blend = clamp(2.0 * triangle(t) - square(t + 1.0) * r * 0.5, 0.0, 1.0);

    vec2 c = mix(z,z0,smoothstep(0.0, 1.0, blend));

    for(int i = 0; i < 128; i++) {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y);
        z += c;
        if(dot(z,z) > 4.0) {    
		    fragColor = vec4(palette(float(i)),1.0);
            return;
        }
    }

    fragColor = vec4(0.0);
}

