    float hres = iResolution.y/2.0;
    vec3 surf = vec3((fragCoord-hres)/iResolution.y,  -1000.0);

	float fEye ;   
    float fEye2 ; 
    float U ;
    vec3 Pi ;
    float rDist ;
    float ooDp ;

    // calulate the magnitude of the pixel - far plane vector
    float mag =dot (surf,surf);//dot prod of v with itself is length squared...

    for (int i = 0 ; i < 3; i++)
    {
        U =dot( LightPosition[i], surf)/mag;      // Signed

        //Gives us the point along the eye/frag line that is closest to light soure.
        // in a roundabout way we normalise the vector and then multiply it back up to
        // get the point along it closet to the light
        Pi =  surf * U;

        //The distance between pI and light
        vec3 t = Pi - LightPosition[i];

        rDist = length(t);

        ooDp = 1.0/rDist;

        float len = length(Pi);        


        // inscatter from Pi to infinity
        fEye = ooDp * 1.570796; // Arctan tends to pi.2 at infinity

        //Approximate Estimate of Accumulated in-scatter betweeb eye and Pi
        fEye2 = ooDp * atan(ooDp * len);            

        fEye += fEye2 ; 

        //itotal = itotal + (brightness*fEye)/-LightPosition[i].z;
        itotal = itotal + (brightness*fEye)/200.0;      
    }

   fragColor =vec4 (itotal);    
   // fragColor =vec4(fEye/200.0,fEye2/200.0, rDist*100.0, 1.0);    
   // fragColor =vec4(0.0, 0.0, rDist*20.0, 1.0);    

}

vec3 vp = vec3(3.);
