float PrSphDf (vec3 p, float s);
float PrCylDf (vec3 p, float r, float h);
float PrCylAnDf (vec3 p, float r, float w, float h);
float PrRCylDf (vec3 p, float r, float rt, float h);
float PrRoundBoxDf (vec3 p, vec3 b, float r);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
vec3 VaryNf (vec3 p, vec3 n, float f);

const float pi = 3.14159;

vec3 ltPos[2], dronePos;
vec2 aTilt;
float dstFar, tCur, htWat;
int idObj;
bool uWat;
const int idDrBod = 1, idDrLamp = 2, idDrCam = 3;

float ObjDf (vec3 p)
{
  vec3 q, qq;
  float dMin, d;
  const float dSzFac = 6.;
  dMin = dstFar;
  dMin *= dSzFac;
  qq = dSzFac * (p - dronePos);
  qq.yz = Rot2D (qq.yz, - aTilt.y);
  qq.yx = Rot2D (qq.yx, - aTilt.x);
  q = qq;
  q.y -= 0.05;
  d = PrRCylDf (q.xzy, 0.2, 0.03, 0.07);
  if (d < dMin) { dMin = d;  idObj = idDrBod; }
  q.y -= 0.07;
  d = PrRoundBoxDf (q, vec3 (0.06, 0.02, 0.12), 0.04);
  if (d < dMin) { dMin = d;  idObj = idDrLamp; }
  q = qq;
  q.y -= -0.05;
  d = PrSphDf (q, 0.17);
  if (d < dMin) { dMin = d;  idObj = idDrCam; }
  q = qq;
  q.xz = abs (q.xz) - 0.7;
  d = min (PrCylAnDf (q.xzy, 0.5, 0.05, 0.05), PrCylDf (q.xzy, 0.1, 0.03));
  q -= vec3 (-0.4, -0.1, -0.4);
  d = min (d, PrRCylDf (q.xzy, 0.05, 0.03, 0.15));
  q -= vec3 (-0.3, 0.2, -0.3);
  q.xz = Rot2D (q.xz, 0.25 * pi);
  d = min (d, min (PrRCylDf (q, 0.05, 0.02, 1.), PrRCylDf (q.zyx, 0.05, 0.02, 1.)));
  if (d < dMin) { dMin = d;  idObj = idDrBod; }
  return dMin / dSzFac;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 100; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy),
     ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec3 TrackPath (float t)
{
  return vec3 (4.7 * sin (t * 0.15) + 2.7 * cos (t * 0.19), 0., t);
}

float CaveDf (vec3 p)
{
  float s, d;
  s = p.y - htWat;
  p.xy -= TrackPath (p.z).xy;
  p += 0.1 * (1. - cos (2. * pi * (p + 0.2 * (1. - cos (2. * pi * p.zxy)))));
  d = 0.7 * (length (cos (0.6 * p - 0.5 * sin (1.4 * p.zxy +
     0.4 * cos (2.7 * p.yzx)))) - 1.1);
  if (! uWat) d = min (d, s);
  return d;
}

float CaveRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 200; j ++) {
    d = CaveDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

float CaveAO (vec3 ro, vec3 rd)
{
  float ao, d;
  ao = 0.;
  for (int j = 0; j < 8; j ++) {
    d = float (j + 1) / 8.;
    ao += max (0., d - 3. * CaveDf (ro + rd * d));
  }
  return clamp (1. - 0.2 * ao, 0., 1.);
}

vec3 CaveNf (vec3 p)
{
  vec4 v;
  const vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (CaveDf (p + e.xxx), CaveDf (p + e.xyy),
     CaveDf (p + e.yxy), CaveDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float CaveSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.1;
  for (int j = 0; j < 16; j ++) {
    h = CaveDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += max (0.2, 0.1 * d);
    if (sh < 0.05) break;
  }
  return 0.4 + 0.6 * sh;
}

vec3 ObjCol (vec3 ro, vec3 rd, vec3 vn, vec3 ltDir, float atten)
{
  vec4 col4;
  if (idObj == idDrBod) col4 = vec4 (1.2, 0., 0., 0.5);
  else if (idObj == idDrLamp) col4 = vec4 (mix (vec3 (0.1),
      vec3 (1.5), step (0., sin (10. * tCur))), -1.);
  else if (idObj == idDrCam) col4 = vec4 (0.1, 0.1, 0.1, 1.);
  if (col4.a >= 0.)
    col4.rgb = col4.rgb * (0.2 + 0.8 * CaveSShadow (ro, ltDir)) *
       (0.1 + 0.9 * atten * (max (dot (ltDir, vn), 0.) +
       col4.a * pow (max (dot (reflect (rd, vn), ltDir), 0.), 64.)));
  return col4.rgb;
}

vec3 CaveCol (vec3 ro, vec3 rd, vec3 vn, vec3 ltDir, float atten)
{
  vec3 col;
  col = vec3 (0.4, 0.4, 0.6);
  if (! uWat) col = mix (col, vec3 (0.2, 0.4, 0.2), smoothstep (0.2, 0.8, vn.y));
  col *= (0.2 + 0.8 * max (dot (vn, ltDir), 0.) +
     0.5 * pow (max (dot (reflect (ltDir, vn), rd), 0.0), 64.)) *
     (0.1 + 0.9 * atten * min (CaveSShadow (ro, ltDir), CaveAO (ro, vn)));
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, colR, bgCol, ltVec, roo, rdo, row, vnw;
  float dstCave, dstObj, atten, frFac;
  bool hitWat;
  roo = ro;
  rdo = rd;
  bgCol = (abs (rd.y) < 0.5) ? vec3 (0.05, 0.05, 0.1) : vec3 (0.01);
  uWat = false;
  hitWat = false;
  dstCave = CaveRay (ro, rd);
  dstObj = ObjRay (ro, rd);
  if (dstCave < min (dstObj, dstFar) && ro.y + rd.y * dstCave < htWat + 0.001) {
    hitWat = true;
    ro += rd * dstCave;
    row = ro;
    vnw = VaryNf (1.5 * ro, vec3 (0., 1., 0.), 0.1);
    rd = reflect (rd, vnw);
    ro += 0.01 * rd;
    dstCave = CaveRay (ro, rd);
    dstObj = ObjRay (ro, rd);
  }
  if (min (dstCave, dstObj) < dstFar) {
    ltVec = roo + 2. * ltPos[0] - ro;
    atten = 1. / (1. + 0.01 * dot (ltVec, ltVec));
    ltVec = normalize (ltVec);
    if (dstCave < dstObj) {
      ro += rd * dstCave;
      col = CaveCol (ro, rd, VaryNf (5. * ro, CaveNf (ro), 10.), ltVec, atten);
      col = mix (col, bgCol, smoothstep (0.45, 0.99, dstCave / dstFar));
    } else {
      ro += rd * dstObj;
      col = ObjCol (ro, rd, ObjNf (ro), ltVec, atten);
    }
  } else col = bgCol;
  if (hitWat) {
    frFac = rdo.y * rdo.y;
    frFac *= frFac;
    if (frFac > 0.005) {
      rd = refract (rdo, vnw, 1./1.333);
      ro = row + 0.01 * rd;
      uWat = true;
      dstCave = CaveRay (ro, rd);
      if (dstCave < dstFar) {
        ltVec = roo + 2. * ltPos[1] - ro;
        atten = 1. / (1. + 0.01 * dot (ltVec, ltVec));
        ltVec = normalize (ltVec);
        ro += rd * dstCave;
        colR = CaveCol (ro, rd, VaryNf (5. * ro, CaveNf (ro), 10.), ltVec, atten);
        colR = mix (colR, bgCol, smoothstep (0.45, 0.99, dstCave / dstFar));
      } else colR = bgCol;
      col = mix (col, colR * vec3 (0.4, 1., 0.6) * exp (0.02 * ro.y), frFac);
    }
  }
  return pow (clamp (col, 0., 1.), vec3 (0.8));
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, fpF, fpB, vd;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, t, tt, a;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  t = 2. * tCur;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az = az + 2. * pi * mPtr.x;
    el = el + 0.95 * pi * mPtr.y;
  } else {
    tt = mod (floor (0.05 * tCur), 4.);
    a = 0.45 * pi * SmoothBump (0.8, 0.95, 0.05, mod (0.05 * tCur, 1.));
    if (tt < 2.) el = (2. * tt - 1.) * a;
    else az = (2. * tt - 5.) * a;
  }
  tt = t + 1. + 7. * SmoothBump (0.2, 0.8, 0.15, mod (0.01 * t, 1.));
  dronePos = TrackPath (tt);
  aTilt = vec2 (5. * (TrackPath (tt + 0.1).x - dronePos.x), 0.);
  fpF = TrackPath (t + 0.1);
  fpB = TrackPath (t - 0.1);
  ro = 0.5 * (fpF + fpB);
  vd = fpF - fpB;
  ori = vec2 (el, az + ((length (vd.xz) > 0.) ? atan (vd.x, vd.z) : 0.5 * pi));
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  rd = vuMat * normalize (vec3 (uv, 1.5));
  ltPos[0] = vuMat * normalize (vec3 (0., 1., -1.));
  ltPos[1] = vuMat * normalize (vec3 (0., -1., -1.));
  htWat = -0.5;
  dstFar = 40.;
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrSphDf (vec3 p, float s)
{
  return length (p) - s;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float PrRCylDf (vec3 p, float r, float rt, float h)
{
  vec2 dc;
  float dxy, dz;
  dxy = length (p.xy) - r;
  dz = abs (p.z) - h;
  dc = vec2 (dxy, dz) + rt;
  return min (min (max (dc.x, dz), max (dc.y, dxy)), length (dc) - rt);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

const vec4 cHashA4 = vec4 (0., 1., 57., 58.);
const vec3 cHashA3 = vec3 (1., 57., 113.);
const float cHashM = 43758.54;

vec4 Hashv4f (float p)
{
  return fract (sin (p + cHashA4) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec4 t;
  vec2 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv4f (dot (ip, cHashA3.xy));
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 4; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n)) * (1. / 1.9375);
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  float s;
  const vec3 e = vec3 (0.1, 0., 0.);
  s = Fbmn (p, n);
  g = vec3 (Fbmn (p + e.xyy, n) - s, Fbmn (p + e.yxy, n) - s,
     Fbmn (p + e.yyx, n) - s);
  return normalize (n + f * (g - n * dot (n, g)));
}

float pi = 3.141592;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    float aspect = iResolution.x/iResolution.y;
    uv.x *= aspect;


    vec2 pos = vec2(0.0, 0.0);

    float dist = length(uv - pos);// * sin(3.141592 * iTime);

    float time = iTime;

    float rippleRadius = time;

    float diff = rippleRadius - dist;

    float func = sin(pi * diff);

    uv += uv * func * 0.1;



    //uv *= vec2(sin(iTime), cos(iTime));

    float stripes = 10.0;
    float thickness = 10.0;
    float sharpness = 2.0;
    vec2 a = sin(stripes * 0.5 * pi * uv - pi/2.0);
    vec2 b = abs(a);

    vec3 color = vec3(0.0);
    color += 1.0 * exp(-thickness * b.x * (0.8 + 0.5 * sin(pi * iTime)));
    color += 1.0 * exp(-thickness * b.y);
    color += 0.5 * exp(-(thickness/4.0) * sin(b.x));
    color += 0.5 * exp(-(thickness/3.0) * b.y);

    float ddd = exp(-5.5 * clamp(pow(dist, 3.0), 0.0, 1.0));

    vec3 t = vec3(uv.x * 0.5+0.5*sin(iTime), uv.y * 0.5+0.5*cos(iTime), pow(cos(iTime), 4.0)) + 0.5;

    fragColor = vec4(color * t * ddd, 0.0);
    //fragColor = vec4(ddd);
}

#define TWO_PI  6.28318530718

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2	uv          = 2.0 * vec2(fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
   	int     num        	= 10;
    float   h_smooth    = 0.0025;
    float   dist        = length(uv);
    float  	di          = floor(dist * float(num));
    float   df          = di / float(num);
    float   seed        = floor(iMouse.x) + 12.0;
    float   gen         = fract(sin(df)*seed);
    float   sd          = abs(sin(di));
    float   sp          = sin(di);
    float   ro          = gen * TWO_PI;
    float   th          = ro + ((sp) * iTime); 
    vec2  	q           = vec2(cos(th)*uv.x + sin(th)*uv.y, -sin(th)*uv.x + cos(th)*uv.y);
    float   a           = atan(q.x, q.y) * (gen * 5.0);
    float   act      	= mod(di, 2.0);
    float   fs          = 0.1;
    float   strk        = (gen * fs + 0.005);
    float   crve        = (strk * 0.25) * (1.0-act);
    float   sect        = ((4.000 * gen) * act);
    float   sw          = (0.040 * act) * gen;
    float   success     = step(gen, sd);
    float   angle       = (a < 0.0) ? (a + TWO_PI) / TWO_PI : a / TWO_PI;
    float   ring        = step(sw * (1.0+df*-1.0), fract(angle * sect));
    float   inner       = smoothstep(df-h_smooth, df+h_smooth,  dist);
    float   inSlope     = 0.25 * gen;
    float   outSope     = 0.75 * gen;
    float   oDist       = dist + smoothstep(inSlope, inSlope + 0.008, angle) * (1.0 - smoothstep(outSope, outSope+0.008, angle)) * crve;  
    float   outer       = 1.0 -  smoothstep((df + strk - 0.001)-h_smooth, (df + strk - 0.001)+h_smooth, oDist);
    float   solid       = 1.0 - (strk / fs) ;
    float   outline     = step(dist, 1.0) * 1.0-step(dist, 0.05);
    float   final       = inner * outer * ring * outline * solid * success;

    fragColor = vec4 (final*(0.392), final*(0.627+(gen*0.176)), final*(0.392+(df*0.607)), 1.0) * 1.6;
}

