    for(float i = 0.; i < 10.; i ++)
    {
        float val = plotImpulse(uv, 2.+i, sin(iTime/(10.+i))*.77, .0);
        c -= val;
    }
    fragColor = vec4(c,1.0);
}

vec3  v_cam_pos = vec3(0.0, 1.0, -6.0);
