vec3  v_cam_up = vec3(0.0, 1.0, 0.0);
vec3  v_sundir = normalize(vec3(0.0, 1.0, 1.10));
float f_near = 2.3;

#define SPHERE 100
#define FLOOR 999
#define WALL 1000
#define CEIL 1001
#define INFINITY 99999.0

#define EPSILON 0.00001
#define MAX_STEPS 400

#define CEIL_HEIGHT 12.0
#define BOX_RADIUS 20.0

#define PI 3.1415926535

vec3 ToRay(in vec2 fragCoord, in mat3 m)
{
    vec2 aspect = vec2(iResolution.x / iResolution.y, 1.0);
	vec2 ndc = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0; 
	vec3 ray = vec3(aspect * ndc, f_near);
	return m * normalize(ray);
}

void Box(vec3 point, out int matid, inout float distance)
{
    float boxdist = INFINITY;

    #define TEST(dist, mid) if (dist<distance) { distance = dist; matid=mid; }

    float zrad = sin(point.x)*0.2 + BOX_RADIUS;
    float xrad = sin(point.z)*0.2 + BOX_RADIUS;

    float z = zrad + point.z;
    float x = xrad + point.x;

    if (point.z > 0.0)
    	z = zrad - point.z;

    if (point.x > 0.0)
    	x = xrad - point.x;

    TEST(z, WALL);
    TEST(x, WALL);
    TEST(point.y - 0.1*sin(length(point.xz)+iTime), FLOOR);
    TEST(CEIL_HEIGHT - point.y, CEIL);
}

vec3 spheres_pos[7];

void Sphere(vec3 point, out int matid, inout float distance, float x, float y, int id)
{   
    float dist = (1.0 + max(0.0, sin(iTime * 4.0 + (float(id)*PI * 0.25)))) * 3.0;

    vec3 position = vec3(x * dist, 2.0, y * dist);
    float radius = 1.0;

    spheres_pos[id] = position + vec3(0.0, radius * 1.1, 0.0);

    float l2c = length(point - position) - radius;

    if (l2c < distance)
    {
        distance = l2c;
        matid = int((x * 17.0) + (y * 13.0) + 23.0) ^ 0x55555555;
    }
}

float SceneDistance(vec3 point, out int matid)
{
    vec3 offset = vec3(0.0, 3.4, 0.0);
    float distance = INFINITY;

    Sphere(point, matid, distance, -1.0,  0.0, 0);
    Sphere(point, matid, distance,  0.0,  0.0, 1);
    Sphere(point, matid, distance,  1.0,  0.0, 2);
    Sphere(point, matid, distance,  0.0,  1.0, 3);
    Sphere(point, matid, distance,  0.0, -1.0, 4);
	Sphere(point, matid, distance, -2.0, -2.0, 5);
    Sphere(point, matid, distance,  2.0,  2.0, 6);

    Box(point, matid, distance);


	return distance;
}

vec3 March(vec3 start, vec3 ray, out int matid, out int samples, out float prec)
{   
    matid = -1;
    samples = -1;
    float step = 0.0;

	for (int i = 0; i < MAX_STEPS; ++i)
    {
        step = SceneDistance( start, matid );

        if (step < EPSILON)
        {
            prec = step;
            samples = i;
            return start;
        }  

        start += step * ray;
    }

    return vec3(0,0,0);
}

/////////////////////////////////////////////////////////////////////////////
// calculate normal 
// taken from https://www.shadertoy.com/view/4lGSWt
float df(vec3 p)
{
    int matid = 0;
	return SceneDistance(p, matid);
}
vec3 calcNormal(vec3 p) {
	float eps = 0.001;
	return normalize(vec3(
		df(p + vec3(eps, 0, 0)) - df(p + vec3(-eps, 0, 0)),
		df(p + vec3(0, eps, 0)) - df(p + vec3(0, -eps, 0)),
		df(p + vec3(0, 0, eps)) - df(p + vec3(0, 0, -eps))
		));
}
/////////////////////////////////////////////////////////////////////////////

void SampleEnv(in vec3 ori, in vec3 dir, out vec3 pos, out vec3 nrm, out int matid, out int samples)
{
    float prec;

    pos = March(ori, dir, matid, samples, prec);   
    nrm = calcNormal(pos);
}

vec4 IdToColor(int id)
{
    vec4 outc = vec4(0.0);

    outc.x = float((id)%11) * 0.1;
    outc.y = float((id+3)%11) * 0.1;
    outc.z = float((id+6)%11) * 0.1;
    outc.w = 0.7;

    if (id == FLOOR)
    {
    	outc = vec4(1.3, 1.0, 0.3, 0.1);
    }
    else if (id == CEIL)
    {
        outc = vec4(1.0, 0.5, 1.0, 1.0);
    }
    else if (id == WALL)
    {
    	outc = vec4(3.0, 1.3, 1.0, 0.1);
    }

    return outc;
}

float AmbientOcclusion(float samples)
{
	return (1.0-clamp(pow(samples / 400.0, 0.2), 0.0, 1.0));
}

float ShadowSample(vec3 pos, vec3 sunpos)
{
    sunpos *= 0.9;
    int id;
    int smp;
    float prec;

    vec3 sundir = normalize(pos-sunpos);

    vec3 shadow_pos = March(sunpos, sundir, id, smp, prec);

    if (smp < 0)
        return 1.0;

    float dist2pos = length(sunpos - pos);
    float dist2shd = length(sunpos - shadow_pos);

    float dist = abs(dist2pos - dist2shd);

    if (dist2shd + 0.01 < dist2pos)
        return 0.5;
	else 
        return 1.0;
}

float Shadow(vec3 pos)
{
    float shadow = 0.0;

    for (int i = 0; i < 7; ++i)
    {
        shadow += ShadowSample(pos, spheres_pos[i] );
    }


    return shadow * 0.25;
}

vec3 AmbientLight()
{
	return vec3(1.0, 1.0, 1.0) * 0.2;
}

vec3 SunLight(vec3 nrm)
{
	return dot(nrm, v_sundir) * vec3(1.0, 1.0, 1.0); // * vec3(0.9, 0.2, 0.2) * 6.0;
}

vec3 Diffuse(vec3 pos, int id)
{
    vec3 color = IdToColor(id).xyz;

	return color;
}

vec3 Specular(vec3 pos, vec3 nrm, int id)
{
    vec3 VertexToEye = normalize(v_cam_pos - pos);
    float str = 0.0;

    for (int i = 0; i < 7; ++i)
    {
        vec3 sundir = normalize(pos - (spheres_pos[i]));

        vec3 LightReflect  = normalize(reflect(sundir, nrm));

        str = max(str, dot(VertexToEye, LightReflect));

    }

    float spec = IdToColor(id).w;

	vec3 spc = pow(str, 30.0 * spec) * vec3(1.0, 1.0, 1.0);

    return spc;
}

vec3 Reflection(vec3 ray, vec3 pos, vec3 nrm, int surface_id)
{  
    vec3 reflected = reflect(ray, nrm);

    int matid;
    int samples;
    float prec;
    vec3 hit = March(pos.xyz + nrm*0.01, reflected, matid, samples, prec);

    if (samples < 0)
		return vec3(0, 0, 0);


    return Diffuse(hit.xyz, matid);
}

vec3 Fog(vec3 pos)
{
    float distance = length(pos.xyz - v_cam_pos);

	return max(0.0, exp(-distance * 0.01 )) * vec3(1.0, 1.0, 1.0);
}

mat3 CameraMatrix()
{
    float radius = 15.0;

    float param = iTime * 0.3;
    float height = 4.0;

    if (iMouse.z > 0.0)
    {
    	param = iMouse.x * 0.1;//obalTime * 0.3;
    	height = 1.0 + iMouse.y * 0.01;

    }

    v_cam_pos.x = sin(param) * radius;
    v_cam_pos.y = height;
    v_cam_pos.z = cos(param) * radius;

    v_cam_forward = -normalize(v_cam_pos);
    v_cam_forward.y = 0.0;
    v_cam_forward = normalize(v_cam_forward);

    v_cam_forward = -normalize(v_cam_pos);

    v_cam_right   = cross(v_cam_forward, v_cam_up);

    mat3 m;
    m[0] = v_cam_right;
    m[1] = v_cam_up;
    m[2] = v_cam_forward;

    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	mat3 cam = CameraMatrix();
    vec3 ray = ToRay(fragCoord, cam);

    vec3 pos;
    vec3 nrm;
    int matid;
    int samples;

    SampleEnv(v_cam_pos, ray, pos, nrm, matid, samples);

    vec4  mat = IdToColor(matid);

    vec3  dif = Diffuse(pos, matid);
    vec3  spc = Specular(pos, nrm, matid);
    vec3  rfl = Reflection(ray, pos, nrm, matid);
    vec3  amb = AmbientLight();
    vec3  sun = SunLight(nrm);
    vec3  fog = Fog(pos);
    float shd = AmbientOcclusion(float(samples)) * Shadow(pos);

    vec3 color = vec3(0.0);

    color = (amb + dif + spc + rfl*mat.w) * shd * fog;

    fragColor.xyz = color;

    //fragColor.xyz = mat.xyz;
   // fragColor.xyz = nrm * 0.5 + 0.5;
    fragColor.w = 1.0;  

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 0.01;
	vec2 uv = zoom * (fragCoord.xy - iResolution.xy / 2.0);

    float t = iTime;

    float x = uv.x;
    float y = uv.y;

    float m = 5.;
    float n = 5. + 4. * sin(t/10.5); // m * (1. + t*t*t / 2.);

    float a = 1.;
    float b = 1. + sin(t/1.) * 0.90;

    float value = (
       a * sin(m*x) * sin(n*y) + b * sin(n*x) * sin(m*y)
    );


    float color = (value) * 6.0;

    if(color > 0.0) {
      fragColor = 1. - (color * vec4(0.2,1.5,2.,0)); 
    } else {
      //fragColor = vec4(med, med, low,1.0);
      fragColor = 1. - (-color * vec4(1,1.5,0.2,0));
    }

}

