vec3 getSurfacePosition(vec2 coord) {

    float mov_radius;
    vec2  screen_center;
    vec3  Sphere_center;

    float index;
    vec2  tmpv2;
    float acc;

    index = 1.0;

    mov_radius = min(iResolution.x, iResolution.y)*0.25;
    screen_center = iResolution.xy * 0.5;
    acc = 0.0;

    for (int k=0; k<5; k++) {
        index += 1.0;
        Sphere_center = vec3(screen_center.x + mov_radius * -cos(iTime*index),
           screen_center.y + mov_radius * sin(iTime*(5.0-index)*1.013),
           0.0);

        tmpv2 = Sphere_center.xy - coord.xy;
        acc += Sphere_radius/length(tmpv2);
    }
    if (acc > 1.00) {
        acc = 1.0 - 1.0/acc;
        acc *= Sphere_radius;

        acc = (Sphere_radius * acc)/(Sphere_radius*0.30 + acc);
        acc = (Sphere_radius * acc)/(Sphere_radius*0.30 + acc);
        acc = (Sphere_radius * acc)/(Sphere_radius*0.20 + acc);

        } else {
          acc = -200.0;
      }

      return vec3(coord, acc);
  }

