const float pi = 3.141592654;
const vec4 white = vec4(1.0);
const vec4 black = vec4(0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.yy;
	float r = length(uv);
    float theta = atan(uv.y, uv.x);   
    fragColor = fract(2.5 * theta / pi + 7.0 * pow(r, 0.4) - 2.5 * iTime) < 0.5 ? white : black;
}

// Created by Stephane Cuillerdier - Aiekick/2017 (twitter:@aiekick)
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// Tuned via XShade (http://www.funparadigm.com/xshade/)

vec2 df(vec3 p)
{
    p.y -= sin(p.z*0.05)*4.;
    float a = p.z * 0.01 * atan(p.x,p.y)/3.14159;
    p.xy *= mat2(cos(a),-sin(a),sin(a),cos(a));
	p = mod(p,6.)-3.;
    p += sin(p*3.)*1.2;
    float cyl = min(min(length(p.xy),length(p.xz)),length(p.yz));
	return vec2(cyl-0.4, 0);
}

vec3 nor( vec3 p, float prec )
{
    vec2 e = vec2( prec, 0. );
    vec3 n = vec3(
		df(p+e.xyy).x - df(p-e.xyy).x,
		df(p+e.yxy).x - df(p-e.yxy).x,
		df(p+e.yyx).x - df(p-e.yyx).x );
    return normalize(n);
}

// from Dave Hoskins // https://www.shadertoy.com/view/Xsf3zX
vec3 GetSky(in vec3 rd, in vec3 sunDir, in vec3 sunCol)
{
	float sunAmount = max( dot( rd, sunDir), 0.0 );
	float v = pow(1.0-max(rd.y,0.0),6.);
	vec3  sky = mix(vec3(.1, .2, .3), vec3(.32, .32, .32), v);
	sky = sky + sunCol * sunAmount * sunAmount * .25;
	sky = sky + sunCol * min(pow(sunAmount, 800.0)*1.5, .3);
	return clamp(sky, 0.0, 1.0);
}

float SubDensity(vec3 p, float ms) 
{
	return df(p - nor(p,0.0001) * ms).x/ms;
}

vec2 shade(vec3 ro, vec3 rd, float d, vec3 lp, vec3 ldo, float li)
{
	vec3 p = ro + rd * d;
	vec3 n = nor(p, 0.1);
	vec3 ldp = normalize(lp-n*1.5-p);
	vec3 refl = reflect(rd,n);
	float amb = 0.6;
	float diff = clamp( dot( n, ldp ), 0.0, 1.0);
	float fre = pow( clamp( 1. + dot(n,rd),0.0,1.0), 4.);
	float spe = pow(clamp( dot( refl, ldo ), 0.0, 1.0 ), 16.);
	float sss = 1. - SubDensity(p, 0.1);
	return vec2(
        (diff + fre + spe) * amb * li,
        (diff + fre + sss) * amb * li + spe
    );
}

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;
	float t = iTime * 15.;

    vec3 ld = vec3(0.,1., .5);

	vec3 ro = vec3(0,sin(t*0.05)*4.,t);
	vec3 cu = vec3(0,1,0);
	vec3 tg = ro + vec3(0,0,.1);

	float fov = .5;
	vec3 z = normalize(tg - ro);
	vec3 x = normalize(cross(cu, z));
	vec3 y = normalize(cross(z, x));
	vec3 rd = normalize(z + fov * (uv.x * x + uv.y * y));

	float s = 1., d = 1.;
	float dm = 50.;

	for (float i=0.; i<250.; i++)
	{
		if (log(d*d/s/1e5)>0. || d>dm) break;
		d += (s = df(ro + rd * d).x) * .3;
	}

    fragColor.rgb = GetSky(rd, ld, vec3(1.5));

	if (d<dm)
	{
		vec2 sh = shade(ro, rd, d, ro, ld, 1.);
		fragColor.rgb = mix( 
            vec3(.49,1,.32) * sh.y * .6 + vec3(.45,0,.72) * sh.x * 1.2, 
            fragColor.rgb, 
            1.0 - exp( -0.001*d*d ) ); 
	}
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec2 centre = iResolution.xy / 2.0;

    vec4 col = vec4(0.0,0.0,0.0,1.0);

    float steps = 16.0;

    for(float x = -(steps/2.0); x < (steps/2.0); x++){
    	for(float y = -(steps/2.0); y < (steps/2.0); y++){
    		float dist = sqrt(sqrt((distance(fragCoord + vec2(x, y) / steps, centre))) * 100.0);
    		col += floor(mod(dist - iTime * 3.0, 2.0));
    	}
    }

    col /= steps * steps;

	fragColor = col * (1.0 - (distance(fragCoord, centre) / iResolution.x));
}

float dist (vec3 d) {
    vec3 e = fract(d) * 2.0 - 1.0;
	return length(e) - 0.4;
}

float rayMarch(vec3 origin, vec3 ray) {
    float t = 0.0;
    for (int i = 0; i < 64; i++) {
    	vec3 p = origin + ray * 2.0 * t;
        float d = dist(p);
        t += d * 0.5;
    }
	return t;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
	uv.x *= iResolution.x/iResolution.y;
    uv.xy *= 6.0 * sin(iTime);
    vec3 ray = normalize(vec3(uv,1.0));
    vec3 origin = vec3(0.0, 0.0, iTime);
    float march = rayMarch(origin, ray);
    float fog = 1.0/(1.0 + march*march*0.1);
    vec3 finalColor = vec3(fog/0.66, fog/0.33, fog/0.22);
    fragColor = vec4(finalColor,1.0);
}

// Goal: Clone the visual effect of RED FANGs music video for No Air, found here: https://youtu.be/attb8PujeFc?t=568

// Constants
const float kPI = 3.14159265359;
const vec4 kBackgroundColor = vec4( 0.58, 0.47, 0.27, 1.0 );
const float kTotalTime = 12.0;

// Equilateral triangle 
// All triangles have these vertex positions relative to a center (0,0)
// Note the dy: this shift up the triangle to the centroid, so rotations look much better
const float dy = 86.0 / 3.0;
const vec2 kVertA = vec2( -50.0, 0.0 - dy );
const vec2 kVertB = vec2( 50.0, 0.0 - dy );
const vec2 kVertC = vec2( 0.0, 100.0 * 0.5 * sqrt(3.0) - dy );

// Rotate a given point
// Build a 2x2 rotation matrix that you can apply to a vec2 for rotating a point about the origin
mat2 GetRotationMatrix2D( float theta )
{
	// Simple affine transform matrix
    return mat2( cos( theta ),-sin( theta ),
                 sin( theta ), cos( theta ) );
}

// Geometry functions based on distance-functions: 
// http://iquilezles.org/www/articles/distfunctions/distfunctions.htm

float sdCapsule( vec2 p, vec2 a, vec2 b, float r )
{
    vec2 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return 1.0 - clamp( length( pa - ba*h ) - r, 0.0, 1.0 );
}

float sdCapsuleAnimated( vec2 p, vec2 a, vec2 b, float r, float t )
{
    // Special rule: if t is zero, do nothing
    if( t <= 0.0 )
        return 0.0;

    // Compute moving dest, which is assumed 2nd arg
    b = a + (b - a) * t;

    vec2 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return 1.0 - clamp( length( pa - ba*h ) - r, 0.0, 1.0 );
}

float sdDashedCapsule( vec2 p, vec2 a, vec2 b, float r, float dashT )
{
    // Dash pattern
    float dist = length( a - ((b - a) * dashT + p) );
    if( mod( dist, 8.0 ) < 4.0 )
        return 0.0;

    vec2 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return 1.0 - clamp( length( pa - ba*h ) - r, 0.0, 1.0 );
}

float sdDashedCapsuleAnimated( vec2 p, vec2 a, vec2 b, float r, float t, float dashT )
{
    // Special rule: if t is zero, do nothing
    if( t <= 0.0 )
        return 0.0;

    // Dash pattern
    float dist = length( a - ((b - a) * dashT + p) );
    if( mod( dist + dashT, 8.0 ) < 4.0 )
        return 0.0;

    // Compute moving dest, which is assumed 2nd arg
    b = a + (b - a) * t;

    vec2 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return 1.0 - clamp( length( pa - ba*h ) - r, 0.0, 1.0 );
}

float sdStrokeTriangle( vec2 p, vec2 a, vec2 b, vec2 c, float r )
{
    float total = 0.0;

    total += sdCapsule( p, a, b, r );
    total += sdCapsule( p, b, c, r );
    total += sdCapsule( p, c, a, r );

    return total;
}

float sdStrokeHexagon1( vec2 fragCoord, vec2 centerPoint )
{
    float total = 0.0;

    vec2 a = centerPoint + kVertA * 1.0;
    vec2 b = centerPoint + kVertB * 1.0;
    vec2 c = centerPoint + kVertC * 1.0;

    vec2 d = centerPoint + kVertA * vec2(1, -1);
    vec2 e = centerPoint + kVertB * vec2(1, -1);
    vec2 f = centerPoint + kVertC * vec2(1, -1);

    total += sdCapsule( fragCoord, b, f, 1.0 );
    total += sdCapsule( fragCoord, f, a, 1.0 );
    total += sdCapsule( fragCoord, a, d, 1.0 );
    total += sdCapsule( fragCoord, d, c, 1.0 );
    total += sdCapsule( fragCoord, c, e, 1.0 );
    total += sdCapsule( fragCoord, e, b, 1.0 );

    return total;
}

float sdStrokeAnimatedTriangle( vec2 p, float thickness, vec2 trans, float scale, float theta, float t )
{
    float total = 0.0;
    const float kFloatThirdTime = ( 1.0 / 3.0 );

    // Scale, rotate, and translate triangle
    vec2 a = kVertA * scale * GetRotationMatrix2D( theta ) + trans;
    vec2 b = kVertB * scale * GetRotationMatrix2D( theta ) + trans;
    vec2 c = kVertC * scale * GetRotationMatrix2D( theta ) + trans;

    if( t < kFloatThirdTime  )
    {
        total += sdCapsuleAnimated( p, a, b, thickness, t / kFloatThirdTime );
    }
    else if( t < (2.0 * kFloatThirdTime) )
    {
        total += sdCapsule( p, a, b, thickness );
        total += sdCapsuleAnimated( p, b, c, thickness, ( t - kFloatThirdTime ) / kFloatThirdTime );
    }
    else
    {
        total += sdCapsule( p, a, b, thickness );
        total += sdCapsule( p, b, c, thickness );
        total += sdCapsuleAnimated( p, c, a, thickness, ( t - 2.0 * kFloatThirdTime ) / kFloatThirdTime );
    }

    return total;
}

float sdStrokeAnimatedDashedTriangle( vec2 p, float thickness, vec2 trans, float scale, float theta, float t )
{
    float total = 0.0;
    const float kFloatThirdTime = ( 1.0 / 3.0 );

    float dashT = -iTime / 5.0;

    // Scale, rotate, and translate triangle
    vec2 a = kVertA * scale * GetRotationMatrix2D( theta ) + trans;
    vec2 b = kVertB * scale * GetRotationMatrix2D( theta ) + trans;
    vec2 c = kVertC * scale * GetRotationMatrix2D( theta ) + trans;

    if( t < kFloatThirdTime  )
    {
        total += sdDashedCapsuleAnimated( p, a, b, thickness, t / kFloatThirdTime, dashT );
    }
    else if( t < (2.0 * kFloatThirdTime) )
    {
        total += sdDashedCapsule( p, a, b, thickness, dashT );
        total += sdDashedCapsuleAnimated( p, b, c, thickness, ( t - kFloatThirdTime ) / kFloatThirdTime, dashT );
    }
    else
    {
        total += sdDashedCapsule( p, a, b, thickness, dashT );
        total += sdDashedCapsule( p, b, c, thickness, dashT );
        total += sdDashedCapsuleAnimated( p, c, a, thickness, ( t - 2.0 * kFloatThirdTime ) / kFloatThirdTime, dashT );
    }

    return total;
}


float sdStrokeAnimatedHexagonl( vec2 p, float thickness, vec2 trans, float scale, float theta, float t )
{
    float total = 0.0;

    vec2 a = kVertA * scale * GetRotationMatrix2D( theta ) + trans;
    vec2 b = kVertB * scale * GetRotationMatrix2D( theta ) + trans;
    vec2 c = kVertC * scale * GetRotationMatrix2D( theta ) + trans;

    vec2 d = kVertA * vec2(1.0, -1.0) * scale * GetRotationMatrix2D( theta ) + trans;
    vec2 e = kVertB * vec2(1.0, -1.0) * scale * GetRotationMatrix2D( theta ) + trans;
    vec2 f = kVertC * vec2(1.0, -1.0) * scale * GetRotationMatrix2D( theta ) + trans;

    // Animation trick: take the normalized time, grow it by 6, then offset it
    // based on which segment we're working on, then re-normalize

    total += sdCapsuleAnimated( p, b, f, 1.0, clamp( (t * 6.0 - 0.0), 0.0, 1.0 ) );
    total += sdCapsuleAnimated( p, f, a, 1.0, clamp( (t * 6.0 - 1.0), 0.0, 1.0 ) );
    total += sdCapsuleAnimated( p, a, d, 1.0, clamp( (t * 6.0 - 2.0), 0.0, 1.0 ) );
    total += sdCapsuleAnimated( p, d, c, 1.0, clamp( (t * 6.0 - 3.0), 0.0, 1.0 ) );
    total += sdCapsuleAnimated( p, c, e, 1.0, clamp( (t * 6.0 - 4.0), 0.0, 1.0 ) );
    total += sdCapsuleAnimated( p, e, b, 1.0, clamp( (t * 6.0 - 5.0), 0.0, 1.0 ) );

    return total;
}


// Normalized time generation method: returns 0.0 until iTime hits the desired time interval, which then normalizes it to [0.0, 1.0]
// Animation loops after the given max time
float AnimationTime( float startTime, float endTime, float initialDelay, float repeatTime )
{
    // Mod based on desired repeat time
    initialDelay = 0.0;
    float t = mod( max( 0.0, iTime - initialDelay ), repeatTime );
    return clamp( ( t - startTime ) / ( endTime - startTime ), 0.0, 1.0 );
}

float Triangle1( vec2 fragCoord, vec2 centerPoint )
{
    const float delay = 1.0;

    // 1 second delay, starts at about full-screen, zooms in until we start rotating..
    float thicknessAnimation = 1.0 + 5.0 * ( 1.0 - AnimationTime( 0.0, 3.5, delay, kTotalTime ) );
    float theta = -( 2.0 / 3.0 ) * kPI * sin( 0.5 * kPI * AnimationTime( 4.0, 4.8, delay, kTotalTime ) )
            	+ -( 2.0 / 3.0 ) * kPI * sin( 0.5 * kPI * AnimationTime( 7.0, 7.8, delay, kTotalTime ) );
    float strokeAnimation = AnimationTime( 0.0, 3.5, delay, kTotalTime );
    float scaleAnimation = 3.5 - sin( 0.5 * kPI * AnimationTime( 0.0, 3.5, delay, kTotalTime ) * 1.0 )
            			 + 20.0 * sin( 0.5 * kPI * AnimationTime( 11.0, 14.0, 1.0, kTotalTime ) );

    return sdStrokeAnimatedTriangle( fragCoord, thicknessAnimation, centerPoint, scaleAnimation, theta, strokeAnimation );
}

float Triangle2( vec2 fragCoord, vec2 centerPoint )
{
    const float delay = 1.0;

    float thicknessAnimation = 0.5;
    float theta = kPI + -( 2.0 / 3.0 ) * kPI * sin( 0.5 * kPI * AnimationTime( 4.0, 4.8, delay, kTotalTime ) )
        		+ -( 2.0 / 3.0 ) * kPI * sin( 0.5 * kPI * AnimationTime( 7.0, 7.8, delay, kTotalTime ) );
    float strokeAnimation = AnimationTime( 3.5, 4.5, delay, kTotalTime );
    float scaleAnimation = 0.5 * ( 3.5 - sin( 0.5 * kPI * AnimationTime( 0.0, 3.5, delay, kTotalTime ) * 1.0 ) )
        				 + 20.0 * sin( 0.5 * kPI * AnimationTime( 11.0, 14.0, 1.0, kTotalTime ) );

    return sdStrokeAnimatedTriangle( fragCoord, thicknessAnimation, centerPoint, scaleAnimation, theta, strokeAnimation );
}

float Hexagonl1( vec2 fragCoord, vec2 centerPoint )
{
    const float delay = 1.0;

    float strokeAnimation = sin( 0.5 * kPI * AnimationTime( 5.0, 7.0, 1.0, kTotalTime ) );
    float theta = -( 2.0 / 3.0 ) * kPI * sin( 0.5 * kPI * AnimationTime( 7.0, 7.8, delay, kTotalTime ) );
    float scaleAnimation = 2.5
        				 + 20.0 * sin( 0.5 * kPI * AnimationTime( 11.0, 14.0, 1.0, kTotalTime ) );

    return sdStrokeAnimatedHexagonl( fragCoord, 1.0, centerPoint, scaleAnimation, theta, strokeAnimation );
}

float Triangle3( vec2 fragCoord, vec2 centerPoint )
{
    const float delay = 1.0;

    float thicknessAnimation = 0.5
        					 + 2.0 * ( 0.5 + 0.5 * sin( iTime * 8.0 ) ) * sin( 0.5 * kPI * AnimationTime( 7.5, 10.0, 1.0, kTotalTime ) );
    float theta = -( 2.0 / 3.0 ) * kPI * sin( 0.5 * kPI * AnimationTime( 7.0, 7.8, delay, kTotalTime ) );
    float strokeAnimation = AnimationTime( 5.5, 7.0, delay, kTotalTime );
    float scaleAnimation = 1.25
        				 + 20.0 * sin( 0.5 * kPI * AnimationTime( 11.0, 14.0, 1.0, kTotalTime ) );

    return sdStrokeAnimatedDashedTriangle( fragCoord, thicknessAnimation, centerPoint, scaleAnimation, theta, strokeAnimation );
}

float Triangle4( vec2 fragCoord, vec2 centerPoint )
{
    const float delay = 1.0;

    float thicknessAnimation = 0.5
        					 + 2.0 * ( 0.5 + 0.5 * sin( iTime * 8.0 + 4.0 ) ) * sin( 0.5 * kPI * AnimationTime( 7.5, 10.0, 1.0, kTotalTime ) );
    float theta = kPI -( 2.0 / 3.0 ) * kPI * sin( 0.5 * kPI * AnimationTime( 7.0, 7.8, delay, kTotalTime ) );
    float strokeAnimation = AnimationTime( 5.5, 7.0, delay, kTotalTime );
    float scaleAnimation = 2.5
        				 + 20.0 * sin( 0.5 * kPI * AnimationTime( 11.0, 14.0, 1.0, kTotalTime ) );

    return sdStrokeAnimatedDashedTriangle( fragCoord, thicknessAnimation, centerPoint, scaleAnimation, theta, strokeAnimation );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Compute visual center
    vec2 centerPoint = iResolution.xy / 2.0;

    // Sum all the animated geometries
    float color = 0.0;
    color += Triangle1( fragCoord, centerPoint );
    color += Triangle2( fragCoord, centerPoint );
    color += Hexagonl1( fragCoord, centerPoint );
    color += Triangle3( fragCoord, centerPoint );
    color += Triangle4( fragCoord, centerPoint );

    // Apply final coloring rule: 0.0 means black, 1.0 means background color
    fragColor = ( 1.0 - clamp( color, 0.0, 1.0 ) ) * kBackgroundColor;
}

float metallic = 1.0;
float roughness = 0.1;

const float PI = 3.14159265359;

float degToRad(float d) 
{
    return d * PI / 180.0;
}

mat4 makeYRotation(in float angleInRadians) 
{
  float c = cos(degToRad(angleInRadians));
  float s = sin(degToRad(angleInRadians));
  mat4 r;
  r[0].xyzw = vec4(c, 0, -s, 0);
  r[1].xyzw = vec4(0, 1, 0, 0);
  r[2].xyzw = vec4(s, 0, c, 0);
  r[3].xyzw = vec4(0, 0, 0, 1);

  return r;
}

vec3 opTx( vec3 p, mat4 m )
{
    vec3 q = mat3(inverse(m))*p;
    return q;
}

float sdSphere( vec3 p, float s)
{
	return length(p + vec3(0.0, 0.5, 0.0))-s;
}

float udRoundBox( vec3 p, vec3 b, float r )
{
  return length(max(abs(p + vec3(0.0, 0.5, 0.0))-b,0.0))-r;
}

float smin( float a, float b )
{
	float k = 0.1;
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float map( in vec3 p)
{
	float res = 0.0;
	float resSph = sdSphere(p, 3.0);

	vec3 q = opTx(p, makeYRotation(5.0 * iTime));
	float resCube = udRoundBox(q, vec3(2.0), 0.3);

	res = smin(resCube, resSph);

    vec2 M = ((iMouse.xy / iResolution.xy)*2.0-1.0)*6.0;
    vec3 lp = vec3(M.x, M.y, 0.) - vec3(0., 0., -1.5);


	float sph = sdSphere(p-lp, 1.0);

	return min(res, sph);
}

vec3 calcNormal( in vec3 pos )
{
    vec3 eps = vec3(0.002,0.0,0.0);

	return normalize( vec3(
           map(pos+eps.xyy) - map(pos-eps.xyy),
           map(pos+eps.yxy) - map(pos-eps.yxy),
           map(pos+eps.yyx) - map(pos-eps.yyx) ) );
}

float intersect( in vec3 ro, in vec3 rd )
{
    const float maxd = 10.0;
	float h          = 1.0;
    float t          = 0.0;
    for( int i=0; i<50; i++ )
    {
        if( h<0.001 || t>maxd ) break;
	    h = map( ro+rd*t);
        t += h;
    }

    if( t>maxd ) t=-1.0;

    return t;
}

float orenNayarDiffuse(
  vec3 ld,
  vec3 vd,
  vec3 sn,
  float r,
  float a) {

  float LdotV = dot(ld, vd);
  float NdotL = dot(ld, sn);
  float NdotV = dot(sn, vd);

  float s = LdotV - NdotL * NdotV;
  float t = mix(1., max(NdotL, NdotV), step(.0, s));

  float sigma2 = r * r;
  float A = 1. - .5 * (sigma2/((sigma2 + .33) + .000001));
  float B = .45 * sigma2 / (sigma2 + .09) + .00001;

  float ga = dot(vd-sn*NdotV,sn-sn*NdotL);

  return max(0., NdotL) * (A + B * max(0., ga) * sqrt((1.0-NdotV*NdotV)*(1.0-NdotL*NdotL)) / max(NdotL, NdotV));
}

float DistributionGGX(vec3 N, vec3 H, float roughness)
{
    float a      = roughness*roughness;
    float a2     = a*a;
    float NdotH  = max(dot(N, H), 0.0);
    float NdotH2 = NdotH*NdotH;

    float nom   = a2;
    float denom = (NdotH2 * (a2 - 1.0) + 1.0);
    denom = PI * denom * denom;

    return nom / denom;
}

float GeometrySchlickGGX(float NdotV, float roughness)
{
    float r = (roughness + 1.0);
    float k = (r*r) / 8.0;

    float nom   = NdotV;
    float denom = NdotV * (1.0 - k) + k;

    return nom / denom;
}
float GeometrySmith(vec3 N, vec3 V, vec3 L, float roughness)
{
    float NdotV = max(dot(N, V), 0.0);
    float NdotL = max(dot(N, L), 0.0);
    float ggx2  = GeometrySchlickGGX(NdotV, roughness);
    float ggx1  = GeometrySchlickGGX(NdotL, roughness);

    return ggx1 * ggx2;
}
vec3 F;
vec3 kS;
vec3 kD;

vec3 fresnelSchlickRoughness(float cosTheta, vec3 F0, float roughness)
{
    return F0 + (max(vec3(1.0 - roughness), F0) - F0) * pow(1.0 - cosTheta, 5.0);
}  

vec3 lighting(in vec3 n, in vec3 lp, in vec3 p)
{


    vec3 ld = normalize(lp - p);
    vec3 vd = normalize(vec3(0., 0., 5.) - p);
	float diff = orenNayarDiffuse(ld, vd, n, roughness, 1.0);


	vec3 F0 = vec3(0.04);
    F0      = mix(F0, vec3(1.0), metallic);

    F = fresnelSchlickRoughness(max(dot(n, vd), 0.0), F0, roughness);
    kS = F;
    kD = vec3(1.0) - kS;
    kD *= 1.0 - metallic;

	vec3 H = normalize(vd + ld);
        float distance = length(lp - p);
        float attenuation = 1.0/pow(distance, 2.0);
        vec3 radiance     = vec3(1.0) * attenuation;        

        /* Cook-Torrance BRDF*/
        float NDF = DistributionGGX(n, H, clamp(roughness, 0.03, 1.0));        
        float G   = GeometrySmith(n, vd, ld, roughness);      

        vec3 nominator    = NDF * G * F;
        float denominator = (4.0 * max(dot(vd, n), 0.0) * max(dot(ld, n), 0.0)) + 0.001; 
        vec3 brdf = nominator / denominator;

        vec3 Lo = (kD * vec3(1.0) / PI + brdf) * radiance * diff; 


	return vec3(diff) + Lo;
}

float saturate(in float n) {
	return clamp(n, 0., 1.);
}

float sss(in vec3 L, in vec3 N, in vec3 V, in vec3 P){

	int iLPower = 2;
    float fLTScale = 5.;
    float fLTThickness = 0.5;
    float fLTAmbient = 0.0;
    float fLightAttenuation = 2.0 * 1.0/pow(length(P - L), 2.0);

    vec3 vLTLight = normalize(L + N * 0.3);
    float fLTDot = pow(saturate(dot(V, -vLTLight)), float(iLPower)) * fLTScale; 
    float fLT = fLightAttenuation * (fLTDot + fLTAmbient) * fLTThickness;

    return fLT;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 q = uv * 2.0 - 1.0;
	q.x *= 16.0/9.0;

	vec3 ro = vec3(0.0, 0.0, 5.0 );
    vec3 rd = normalize( vec3(q,-1.0) );
	vec3 col = vec3(0.0);
    float t = intersect(ro,rd);
    if( t>0.0 )
    {
    	vec3 pos = ro+rd*t;
        float d =  max(0.0, 5.0 - t)/2.0;
        vec3 normal = calcNormal(pos);
        vec2 M = ((iMouse.xy / iResolution.xy)*2.0-1.0)*6.0;
        vec3 lp = vec3(M.x, M.y, 0.) - vec3(0., 0., -1);
        vec3 L = pos - lp;

        vec3 light = vec3(lighting(normal, lp, pos));

        light += sss(L, normal, rd, pos) * vec3(0.7, 0.2, 0.1);

        col = light * vec3(0.2, 0.4, 0.9) + vec3(0.04);
    }

    fragColor = vec4(col, 1.0 );
}

vec3  v_cam_pos = vec3(0.0, 1.0, -6.0);
vec3  v_cam_forward = vec3(0.0, 0.0, 1.0);
vec3  v_cam_right = vec3(1.0, 0.0, 0.0);
vec3  v_cam_up = vec3(0.0, 1.0, 0.0);
vec3  v_sundir = normalize(vec3(0.0, 0.3, 1.0));
float f_near = 2.3;

#define FLOOR 999
#define INFINITY 99999.0
#define NUM_SRC 5

vec3 ToRay(in vec2 fragCoord, in mat3 m)
{
    float aspect = iResolution.x / iResolution.y;
    vec2 invResolution = 1.0 / iResolution.xy;
	vec2 ndc = (fragCoord.xy * invResolution) * 2.0 - 1.0; 
	vec3 ray = vec3(aspect * ndc.x, ndc.y, f_near);

	return m * normalize(ray);
}

float HeightAt(vec2 pos, int i)
{
    vec4 src[NUM_SRC];
    src[0] = vec4(-20.0, 0.0, 10.0, 10.0);
    src[1] = vec4(30.0, 0.0, 10.0, 10.0);
    src[2] = vec4(0.0, -20.0, 10.0, 10.0);
    src[3] = vec4(0.0, 40.0, 10.0, 10.0);
    src[4] = vec4(10.0, 10.0, 15.0, 10.0);

    vec2 center = src[i].xy;

    float dist2center = 10.0 + distance(pos, center);
    float attenuation = exp(-dist2center * 0.031);

    float strength = src[i].z * attenuation;
    float freq = src[i].w;

    float H =  sin( dist2center - (iTime * freq) ) * strength / dist2center;

    return H;
}

float HeightAt(vec2 pos)
{
    float sum = 0.0;

    for (int i = 0; i < NUM_SRC; ++i)
    {
        sum += HeightAt(pos, i);
    }

    return sum;
}

float Scene(vec3 point, out int id)
{
    id = 0;
    float distance = INFINITY;

    float dist = point.y - HeightAt(point.xz);

	return dist;
}

vec4 March(vec3 start, vec3 ray, out int id)
{   
    float step = 0.0;

	for (int i = 0; i < 100; ++i)
    {
        start = start + (step * ray);
        step = Scene( start, id );

        if (step < 0.0005)
        {
            return vec4(start, float(i));
        }  

        if (step > 30.0)
        {
        	break;
        }

    }

    return vec4(0,0,0,-1);
}


float ShadowSample(vec3 pos)
{
    int id = -1;
    float shadow = 1.0;
    vec4 shadow_pos = March(pos + v_sundir * 80.0, -v_sundir, id);

    if (shadow_pos.w < 0.0)
        return 1.0;

    float distToShadowCaster = length(shadow_pos.xyz - pos.xyz);

    shadow = min(1.0, max(0.0, 1.0 - distToShadowCaster));


    return shadow;
}

float Shadow(vec3 pos)
{
    return ShadowSample(pos);
}

vec3 Ambient()
{
	return vec3(1.0, 1.0, 1.0) * 0.3;
}

vec3 Diffuse(vec3 nrm)
{
	return dot(nrm, v_sundir) * vec3(1.0, 1.0, 1.0);
}

vec3 Opq(vec3 pos, int id)
{
	return vec3(1.0, 0.8, 0.3) * 2.0;
}

vec3 Reflection(vec3 ray, vec4 pos, vec3 nrm, int surface_id)
{  
    vec3 reflected = reflect(ray, nrm);

    int id = -1;
    vec4 hit = March(pos.xyz + nrm*0.01, reflected, id);

    vec3 value = Opq(hit.xyz, id);

    if (hit.w < 0.0)
    {
        float sun = dot(reflected, v_sundir);

        vec3 a = vec3(0.0, 0.3, 1.0) * 2.0;
        vec3 b = vec3(0.0, 0.3, 1.0) * 5.0;

		value *= mix(a, b, 1.0 - sun);
    }

    return value;
}

vec3 Fog(vec4 pos)
{
    if (pos.w < 0.0)
        return vec3(0.0, 0.0, 0.0);

    float distance = length(pos.xyz - v_cam_pos);

	return max(0.0, exp(-distance * 0.0014 )) * vec3(1.0, 1.0, 1.0);
}

vec3 Specular(vec3 pos, vec3 nrm)
{
    vec3 VertexToEye = normalize(v_cam_pos - pos);
    vec3 LightReflect  = normalize(reflect(-v_sundir, nrm));

    float str = max(0.0, dot(VertexToEye, LightReflect));

	vec3 spc = pow(str, 100.0) * vec3(1.0, 1.0, 1.0);

    return spc;
}

float AmbientOcclusion(vec4 pos)
{
	return (1.0-clamp(pow(pos.w / 100.0, 0.2), 0.0, 1.0));
}

mat3 CameraMatrix()
{
	float radius = 30.0;
    float param = iMouse.x * 0.6;//obalTime * 0.3;

    if (iMouse.z > 0.0)
    {
        v_cam_pos.x = 0.5 * iResolution.x - iMouse.x; //sin(param) * radius;
    	v_cam_pos.y = 20.0;//1.0 + iMouse.y * 0.01;
    	v_cam_pos.z = iMouse.y - 0.5 * iResolution.y;//cos(param) * radius;
    }
    else
    {
    	v_cam_pos = vec3(0.0, 20.0, -50.0);
    }


    v_cam_forward = vec3(0.0, -0.5, 1.0);//= -normalize(v_cam_pos);
    v_cam_forward = normalize(v_cam_forward);

    v_cam_right   = cross(v_cam_forward, v_cam_up);

    mat3 m;
    m[0] = v_cam_right;
    m[1] = v_cam_up;
    m[2] = v_cam_forward;

    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	mat3 cam = CameraMatrix();

    int id = -1;

    vec3 ray   = ToRay(fragCoord, cam);
    vec4 pos   = March(v_cam_pos, ray, id);



    int id_alt = -1;
    vec3 ray_A = ToRay(fragCoord + vec2(0.2, 0.0), cam);
    vec3 ray_B = ToRay(fragCoord + vec2(0.0, 0.2), cam);
    vec3 pos_A = March(v_cam_pos, ray_A, id_alt).xyz;
    vec3 pos_B = March(v_cam_pos, ray_B, id_alt).xyz;
    vec3 diff1 = normalize(pos.xyz - pos_A);
    vec3 diff2 = normalize(pos.xyz - pos_B);
    vec3 nrm = normalize(cross(diff1, diff2));

    vec3  opq = Opq(pos.xyz, id);
    vec3  rfl = Reflection(ray, pos, nrm, id);
    vec3  spc = Specular(pos.xyz, nrm);
    vec3  amb = Ambient();
    vec3  dif = Diffuse(nrm);
    vec3  fog = Fog(pos);
    float shd = Shadow(pos.xyz);

    vec3 color;

    color = (amb + dif * shd + (spc + rfl)) * opq;

    color *= AmbientOcclusion(pos);
    fragColor.xyz = color * fog;

    //fragColor.xyz = opq.xyz;
    fragColor.w = 1.0;  

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float skip_ahead_seconds = 140.;

	vec2 uv = ((skip_ahead_seconds + iTime / 200.0) / 1.0) * ((fragCoord.xy - (iResolution.xy / 2.0)) / iResolution.y);

    //uv = floor(uv * 2.);
    float value = (uv.x * sign(uv.y) + uv.y * sign(uv.x)) * sin(uv.x) * sin(uv.y);
    float color = sin(value) * 3.0;
    color = color * sign(uv.x) * sign(uv.y); // revision 2

    float low = abs(color);
    float med = abs(color) - 1.0;
    float high = abs(color) - 2.0;
    if(color > 0.0) {
	  fragColor = vec4(med*2., high, high,1.0);
    } else {
      fragColor = vec4(high, high, med*2.,1.0);
    }
}

#define saturate(x) clamp(x, 0.0, 1.0)
#define SUN_DIR normalize(vec3(0.75, 2.25, 0.75))
#define GAMMA 2.2
#define PI 3.14159265359

// -----------------
// domain distortion
// -----------------
void pR(inout vec2 p, float a) {
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

float vmax(vec2 v) {
	return max(v.x, v.y);
}

float vmax(vec3 v) {
	return max(max(v.x, v.y), v.z);
}

// -----------------
// obj combination
// -----------------
float fOpUnionRound(float a, float b, float r) {
	vec2 u = max(vec2(r - a,r - b), vec2(0));
	return max(r, min (a, b)) - length(u);
}

float fOpIntersectionRound(float a, float b, float r) {
	vec2 u = max(vec2(r + a,r + b), vec2(0));
	return min(-r, max (a, b)) + length(u);
}

// -----------------
// sdf
// -----------------
float sdCylinder(vec3 p, float r, float height) {
	float d = length(p.xz) - r;
	d = max(d, abs(p.y) - height);
	return d;
}

float sdBoxCheap(vec3 p, vec3 b) {
	return vmax(abs(p) - b);
}

float sdBox2Cheap(vec2 p, vec2 b) {
	return vmax(abs(p)-b);
}

// -----------------
// terrain
// -----------------
float f(vec2 p)
{
    return sin(p.x) * sin(p.y);
}

vec3 getTerrainNormal(vec3 p)
{
    vec3 n;
    float eps = 0.02;
    n.x = f(vec2(p.x - eps, p.z)) - f(vec2(p.x + eps, p.z));
    n.y = 2.0 * eps;
    n.z = f(vec2(p.x, p.z - eps)) - f(vec2(p.x, p.z + eps));

    return normalize(n);
}

float traceTerrain(vec3 ro, vec3 rd, out float resT)
{
    const float minT = 5.0;
    const float maxT = 10.0;
    float lh = 0.0;
    float ly = 0.0;
    float delta = 0.01;
    for (float t = minT; t < maxT;) {
        vec3 p = ro + rd * t;
        float h = f(p.xz);
        if (p.y < h) {
         	resT = t - delta * delta + (lh - ly) / (p.y - ly - h + lh);
            return 1.0;
        }

        delta = 0.01 * t;
        t += delta;
        lh = h;
        ly = p.y;
    }

    return 0.0;
}

// -----------------
// scene
// -----------------
const float SPHERE = 1.0;
const float GROUND = 2.0;
const float FRAME = 3.0;
const float WALL = 4.0;

vec2 map(vec3 p)
{
    float d1 = p.y + 1.0;

    vec3 wP = p;
    wP.x -= 1.6;

    // wall
    vec3 wallP = wP;
    wallP.y -= 0.5;
    wallP.x += 2.0;
    float wall = sdBox2Cheap(wallP.xy, vec2(0.1, 2.5));

    vec3 windowP = wP;
    windowP.x += 2.3;
    windowP.y -= 0.75;
    windowP.z -= 0.1;
    float window = sdBoxCheap(windowP, vec3(0.5, 0.8, 0.6));

    wall = max(-window, wall);


    // position the frame
    vec3 fP = p;
    fP.y += 0.35;
    pR(fP.xy, radians(13.0));

    // downtube
    vec3 dtP = fP;
    dtP.z += 0.1;
    pR(dtP.yz, radians(-50.0));
    float downtube = sdCylinder(dtP, 0.05, 0.9);

    // headtube
    vec3 htP = fP;
    htP.y -= 0.65;
    htP.z += 0.825;
    pR(htP.yz, radians(20.0));
    float headtube = sdCylinder(htP, 0.065, 0.2);

    //toptube
    vec3 ttP = fP;
    pR(ttP.yz, radians(95.0));
    ttP.z += 0.65;
    ttP.y -= 0.0;
    float toptube = sdCylinder(ttP, 0.05, 0.80);

    // seattube
    vec3 stP = fP;
    pR(stP.yz, radians(15.0));
    stP.z -= 0.7;
    stP.y -= 0.45;
    float seattube = sdCylinder(stP, 0.05, 0.75);

    // seatstays
    vec3 ssP = fP;
    ssP.x = abs(ssP.x);
    ssP.z -= 1.22;
    ssP.x -= 0.1;
    ssP.y -= 0.1;
    pR(ssP.zy, radians(35.0));
    pR(ssP.xy, radians(5.0));
    float seatstays = sdCylinder(ssP, 0.015, 0.55);

    // chainstays
    vec3 csP = fP;
    csP.x = abs(csP.x);
    csP.x -= 0.08;
    csP.z -= 1.1;
    csP.y += 0.445;
    pR(csP.zy, radians(100.0));
    pR(csP.xy, radians(8.0));
    float chainstays = sdCylinder(csP, csP.y * 0.03 + 0.03, 0.45);

    float f1 = fOpUnionRound(downtube, seattube, 0.15);
    float f2 = fOpUnionRound(f1, chainstays, 0.1);
    float f3 = fOpUnionRound(f2, headtube, 0.05);
    float f4 = fOpUnionRound(f3, seatstays, 0.05);
    float frame = fOpUnionRound(f4, toptube, 0.05);

    vec2 result;

    result.x = min(frame, min(wall, d1));
    result.y = FRAME;
    if (result.x == d1) result.y = GROUND;
    else if (result.x == wall) result.y = WALL;

    return result;
}

vec2 trace(vec3 ro, vec3 rd, out float resT, out vec3 pos)
{
    const float EPSILON = 0.002;
    const float MAX_DIST = 100.0;
    const int STEPS = 92;
    float t = 0.0;

    for (int i = 0; i < STEPS; i++) {
        pos = ro + rd * t;
        vec2 d = map(pos);

        if (d.x < EPSILON) {
            resT = d.x;

            return d;
        }

        if (d.x > MAX_DIST) {
            return vec2(-1.0);
        }

        t += d.x * 0.7;
    }

    return vec2(-1.0);
}

float shadow(vec3 ro, vec3 rd)
{
    float res = 1.0;
    const float k = 18.0;
 	float t;
    const float mint = 0.01;
    const float maxt = 30.0;

    for (t = mint; t < maxt;)
    {
     	float h = map(ro + rd * t).x;
        if (h < 0.0001) {
         	return 0.0;   
        }

        res = min(res, k * h / t);

        t += h;
    }

    return res;
}

vec3 getNormal(vec3 p)
{
    vec3 n;
    const vec3 eps = vec3(0.02, 0.0, 0.0);
    n.x = map(p + eps.xyy).x - map(p - eps.xyy).x;
    n.y = map(p + eps.yxy).x - map(p - eps.yxy).x;
    n.z = map(p + eps.yyx).x - map(p - eps.yyx).x;

    return normalize(n);
}

vec3 renderTerrain(vec3 ro, vec3 rd)
{
    vec3 sky = vec3(0.1, 0.2, 0.3) + (ro.y * ro.y) * 0.2;
 	float resT;
    float t = traceTerrain(ro, rd, resT);

    if (t == 0.0) {
        return sky;
    }

    vec3 n = getTerrainNormal(ro + rd * resT);

    float incidence = saturate(dot(n, SUN_DIR));
    vec3 Fd = vec3(0.1, 0.2, 0.3) * incidence;

    Fd = mix(Fd, vec3(sky), 1.0 - exp(-length(ro + rd * resT) * 0.5) * 3.0);

    return Fd;
}           

vec3 render(vec3 ro, vec3 rd, out vec3 pos)
{
    vec3 colour = vec3(0.7, 0.7, 0.9);
    float resT;
    vec2 t = trace(ro, rd, resT, pos);
    vec3 volumetric = vec3(0.0);

    if (pos.x < -0.5) {
        return renderTerrain(ro, rd);
    }

    if (t.x <= 0.0) {
    	return colour;
    }

    vec3 diffuseColour;
    vec3 n;
    float shininess;

    if (t.y == GROUND) {
        diffuseColour = vec3(0.3, 0.7, 0.2);
        n = vec3(0.0, 1.0, 0.0) + texture(iChannel1, pos.xz * 0.3).xyz;
    }
    else if (t.y == FRAME) {
     	diffuseColour = vec3(1.0);
        n = getNormal(pos);;
    }
    else if (t.y == WALL) {
        diffuseColour = vec3(0.05, 0.2, 0.1);
        n = getNormal(pos);
    }

    float LoV = saturate(dot(SUN_DIR, normalize(-pos)));

    // diffuse
    float incidence = saturate(dot(SUN_DIR, n));
    vec3 Fd = diffuseColour * incidence;

    if (incidence > 0.0) {
     	Fd *= shadow(pos, SUN_DIR);   
    }

    return Fd;
}

mat3 setCamera(vec3 ro, vec3 ta, float cr)
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 r = iResolution.xy;
    vec2 m = vec2(0.0);
    vec2 q = fragCoord.xy / r.xy;
    vec2 uv = q * 2.0 - 1.0;
    uv.x *= r.x / r.y;
    if (iMouse.z > 0.0) {
   	    m += (2.0 * iMouse.xy - r.xy) / r.yy;
    }

    float dist = 2.5;
    vec3 ro = vec3(dist * clamp(cos(iTime * 0.1 + 6.0 * m.x), 0.5, 1.0),
                   clamp(1.0 + m.y * 4.0, 0.5, 2.0),
                   dist * clamp(sin(iTime * 0.1 + 6.0 * m.x), 0.2, 0.25));

    vec3 target = vec3(0.0);
    mat3 ca = setCamera(ro, target, 0.0);
    vec3 rd = ca * normalize(vec3(uv.xy, 1.5));

    vec3 pos;
    vec3 colour = render(ro, rd, pos);

    // indoor "fog"
    if (pos.x > -0.5 || pos.y > 10.0 && pos.x > 1.0) {
    	colour = mix(colour, vec3(0.0), 1.0 - exp(-length(pos)) * 0.5);
    }

    // vignette
    colour *= 0.5 + 0.8 * pow(8.0 * q.x * q.y * (1.0 - q.x) * (1.0 - q.y), 0.25);

    // gamma correct
    colour = pow(colour, vec3(1.0 / GAMMA));

	fragColor = vec4(colour, 1.0);
}

