   Conchoid facts:
  ----------------
	- (a) These curves are also called the conchoids of Nicomedes, after the Greek scholar Nicomedes.
		  Called conchoids because the shape of their outer branches resembles a conch shell.
    - (b) All of these curves (except a = 0) have two branches. Both branches approach the vertical
		  asymptote x = a as x  a from the left or right.
    - (c) If a < 1, both branches are smooth. When a reaches 1, the right branch acquires a
		  sharp point, called a cusp.
    - (d) For 1 < a < 0, the cusp turns into a loop, which becomes large as a  0
    - (e) If a = 0, both branches come together and form a circle.
    - (f) For 0 < a < 1, the left branch has a loop, which shrinks to a cusp when a = 1. For a > 1,
          the branches become smooth again.
*/

#define res_            iResolution
#define time_           iTime
#define trace_len_      64
#define eps_            1.

vec2  domain(vec2 uv, float s);
vec2  parametric(float t);
float trace(vec2 p, float t);

void mainImage(out vec4 o, vec2 f) {
	vec2 p = domain(f, 8.);

    // t -> [0., 5.]
    float t = time_*2.;

    // Conchoid traces
    float dtrace = smoothstep(0.08, 0.001, trace(p, t));
    float dtrace_s0 = smoothstep(0.16, 0.001, trace(p, t));

    // Axis
    float ax = min(
        smoothstep(0., 0.2, abs(p.x)),
        smoothstep(0., 0.2, abs(p.y))
    );
    // Origin
    float org = smoothstep(0.09, 0., length(p)-.01);

    // Palette 
    vec3 gray   = vec3(.2);
    vec3 white  = vec3(5.);

    // Shading
    vec3 c  = vec3(0.);
    c = mix(c, gray, 1.-ax);
    c = mix(c, white, org);

    c = mix(c, vec3(1., 11.0, 0.), dtrace_s0);
    c = mix(c, white, dtrace);
    //c.b += dtrace_s0/p.x;
    c = mix(c, vec3(.1, .02, .1), p.x-dtrace);

    o = vec4(sqrt(c), 1.);
}

vec2 parametric(float t) {
	float a = sin(time_*.2)*4.;
    return vec2(a + cos(t), a*tan(t) + sin(t));
}

float trace(vec2 p, float t) {
    float d = 1e10;
    float e = 0.;
    for(int i = 0; i < trace_len_; i++) {
    	d  = min(d, distance(p, parametric(t-e)));
        e += eps_; 
    }
    return d;
}

vec2 domain(vec2 uv, float s) {
    return (2. * uv.xy - res_.xy) / res_.y * s;
}

float norsin ( float inA ){
	return 0.5 + 0.5 * sin ( inA );
}
float GRAD ( float inUv, float repeat, float align ){
	return norsin( ( inUv + align ) * pow( repeat, 2.0 ) );
}
float movingPoints ( float uvx, float uvy, float velX, float velY, float repeat ){
	return GRAD( uvx, repeat, iTime * velX ) *  GRAD( uvy, repeat, iTime * velY );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float a = 0.25;
    float b = 0.75;
    float c = 1.5;
    float d = 8.0;
    float uvx = uv.x;
    float uvy = uv.y;
    float uvxc = uvx * ( iResolution.x / iResolution.y );

    float grad1 = GRAD ( -uvy + iTime * 1.0, 1.5, -0.5 ) + 0.3;
    uvxc = uvx * ( iResolution.x/ iResolution.y );
    float uv1 = GRAD (uvxc + GRAD ( uvy / 4.0 + iTime / 4.0, 1.0, 0.0) * 5.0 , 2.5, 0.0);
    float r = movingPoints( uvxc-uv1/18.0, uvy+uv1/18.0, 0.0, -0.1, 6.0 );
    r = min ( 0.3, r );
    r *= 3.0;
	r = min ( 0.9, r );
    uv1 *= 1.5;
    uv1 = pow ( uv1, 0.8 );
    grad1 *= uv1;
    r *= grad1*0.6;
	fragColor = vec4(pow(r,2.5),pow(r,1.7),pow(r,0.9), 1.0 );
}

