float pi = 3.1416;

void mainImage( out vec4 col, in vec2 coo )
{
	vec2 uv = (coo.xy - iResolution.xy * 0.5) / iResolution.y;
    vec2 p = vec2(sin(iTime / 3.1), cos(iTime / 3.1));
    float v = (
        sin(length(uv - p) * 20.0) + 
        sin(iTime + 10.0 * (uv.x + uv.y)) + 
        sin(iTime / 4.0 + uv.x*6.0*pi) + 
        sin(iTime / 2.8 + uv.y*4.0*pi) + 
