void moveCenters()
{
    for(int i=0; i<NUM; i++)
    {
     float fi = 2.0*3.14*(float(i)+0.02*iTime)/float(NUM);
     c[i]= vec2(0.5*sin(fi), 0.5*cos(fi));
    }
}

float avgDistance(vec2 uv)
{
 	float d = 0.0;
    float k = 100.0+10.0*sin(iTime/5.0);
    for(int i=0; i<NUM; i++)    
     	d+= sin(k*distance(uv,c[i])); 
    return d/float(NUM);  
}

vec4 distToColor(float d)
{
    return vec4(0.65,1.0-sin(d*12.0),1.0-cos(d*13.0),1.0);
}

